import React, { useEffect, useState } from 'react'
import DataAPI from '../../../helpers/DataAPI.js'
import {DateFilter, getDateLabel, formatDate, formatDate2, round} from '../../../helpers/utils.js'

import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol
} from '@coreui/react'

import {
  CChartLine,
  CChartPolarArea
} from '@coreui/react-chartjs'

import { DocsLink } from 'src/reusable'

const ClimateSummary = () => {

  const [dateRange, setDateRange] = useState('y');
  const [groupBy, setGroupBy] = useState('day');
  const [generators, setGenerators] = useState([]);
  const [generator, setGenerator] = useState(null);

  const [loading, setLoading] = useState(false);

  const [lineChartOneData, setLineChartOneData] = useState({
    labels: [],
    datasets: []
  });
  const [lineChartTwoData, setLineChartTwoData] = useState({
    labels: [],
    datasets: []
  });
  const [polarChartData, setPolarChartData] = useState({
    labels: [],
    datasets: []
  });


useEffect(() => {
  getChartData(dateRange);
}, []);
  
  
  var colorCounter=0;

  const colors = [
            'blue','lightblue','turquoise', 'yellow', 'orange', 'red', 
          ];

  const windRoseLabels = [
            '0-3','3-6','6-9', '9-12', '12-15', '15*', 
          ];

  const windRoseColor = (value) => {



    let n=0;

    if (value < 3) {
      n=0;
    } else if (value < 6){
      n=1;
    } else if (value < 9){
      n=2;
    } else if (value < 12){
      n=3;
    } else if (value < 15){
      n=4;
    } else {
      n=5;

    }

    console.log('windRoseColor', value, n)

    const x = colors;

    return x[n];
  }


  const windRoseRange = (value) => {

    let n=0;

    if (value < 3) {
      n=0;
    } else if (value < 6){
      n=1;
    } else if (value < 9){
      n=2;
    } else if (value < 12){
      n=3;
    } else if (value < 15){
      n=4;
    } else {
      n=5;

    }

    return n;
  }


  const getChartData = (period) => {

    setLoading(true);

    const body = {}

    if (period && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }
    
    DataAPI(
      {
      'endpoint': 'chart/climate',
      'method': 'POST',
      'body': body
    })
      .then(function (responseData) {

        if (responseData.error && responseData.error.message) alert(responseData.error.message)

        setLoading(false);

        var windSpeedData = {
          labels: [],
          datasets: []
        }
        var temperatureData = {
          labels: [],
          datasets: []
        }
        var windRoseData = {
          labels: [],
          datasets: []
        }


        var windSpeedDataSets = {
          label: 'WindSpeed',
          borderColor: '#f87979',
          pointBackgroundColor: 'transparent',
          pointBorderColor: 'transparent',
          backgroundColor: 'transparent',
          data: []
        };
        var temperatureDataSets = {
          label: 'Temperature',
          borderColor: '#00D8FF',
          pointBackgroundColor: 'transparent',
          pointBorderColor: 'transparent',
          backgroundColor: 'transparent',
          data: []
        };




        if (responseData && responseData.graphs){



        responseData.graphs.find((item) => {
          switch (item.type) {
            case 'WindSpeed':
              item.data.forEach((rDs) => {
                const windSpeed = (Math.round(rDs.windSpeed * 100) / 100).toFixed(2);
                windSpeedDataSets.data.push(windSpeed);

                let date = rDs.datetimeFrom.split(' ')[0];
                let time = rDs.datetimeFrom.split(' ')[1];
                let timeArr = time.split(':')

                

                const rDfrom = `${date} - ${timeArr[0]}:${timeArr[1]}`;
                const rDto = rDs.datetimeTo;
                windSpeedData.labels.push(rDfrom);
              });

              console.log('responseData.graphs', responseData.graphs);
              
              windSpeedData.datasets.push(windSpeedDataSets);
              console.log('windSpeedData', windSpeedData);
              setLineChartOneData(windSpeedData)
              break;
            case 'Temperature':
              item.data.forEach((rDs) => {
                temperatureDataSets.data.push(round(rDs.temperature))
                const rDfrom = rDs.datetimeFrom;
                const rDto = rDs.datetimeTo;
                temperatureData.labels.push(formatDate2(rDfrom, 'time'));
              });
              temperatureData.datasets.push(temperatureDataSets);
              setLineChartTwoData(temperatureData)
              break;
            case 'WindRose':
              item.data.forEach((rDs) => {
                let windRoseDataSets = {}
                const diff = parseInt(item.data.length) - parseInt(rDs.radiuses.length);
                
                const color = windRoseColor(rDs);

                windRoseDataSets.data = rDs.radiuses;
                windRoseDataSets.backgroundColor=[];

                rDs.radiuses.forEach(radius => windRoseDataSets.backgroundColor.push(windRoseColor(radius)));

                if (diff > 0) {
                  for (let i = 0; i <= diff; i++) {
                    windRoseDataSets.data.push(0);
                  }
                }
                
                windRoseDataSets.pointBackgroundColor = color;
                // windRoseDataSets.pointBorderColor = "white",
                windRoseDataSets.pointHoverBackgroundColor = color;
                windRoseDataSets.pointHoverBorderColor = color;
                
                windRoseData.datasets.push(windRoseDataSets);

                // console.log('windRoseRange',rDfrom,rDs.angle.from)
                

                
              });

              windRoseData.datasets.backgroundColor = ['red','blue'];

              setPolarChartData(windRoseData)
              break;
          }
        });

      }
      });

  }


  const windRoseLabelsMap = windRoseLabels.map(
    (v,m) => {
      const color = colors[m];
      return <span style={{marginRight:'20px'}}>{v}<span style={{backgroundColor:color, width: '30px', height: '10px', display: 'inline-block', 'marginLeft':'10px' }}></span></span>;
    }
  )


  return (

      <CCard>

      <CCardHeader>
          <CRow>
            <CCol sm="7">
              <h3 id="traffic" className="card-title mb-0">
                Climate
              </h3>
              <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div>
              </CCol>

               <CCol sm="5" class="text-right">

            <DateFilter options={'y,cm,cy,12w,12m,x,xx'.split(',')} disabled={loading} onChange={(value) => { setDateRange(value); getChartData(value);}} />

                            </CCol>

            
                            <CCol sm="3">
              <h3 id="traffic" className="card-title mb-0">
                Alarms
              </h3>
              <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div>
            </CCol>
            
            <CCol sm="9" className="text-right d-flex flex-center">
                <h6 className="mr-2 ml-4 mb-0" style={{lineHeight:2.4,minWidth:'64px'}}>Generator</h6>
                <CSelect disabled={loading} onChange={(ev) => {setGenerator(ev.target.value); }} custom name="generator" id="generator">
                  <option value="0">All generators</option>
                {generators.map((oGenerator) => (
                                    <option value={oGenerator.id}>
                                      {oGenerator.code}
                                    </option>
                                  ))}</CSelect>
                <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4,minWidth:'60px'}}>Group by</h6>
                <CSelect value={groupBy} disabled={loading} onChange={(ev) => { setGroupBy(ev.target.value); }} custom name="period" id="period">
                    <option value="day" selected>Day</option>
                    <option value="week">Week</option>
                    <option value="month">Month</option>
                </CSelect>
                <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4}}>Period</h6>
              <DateFilter value={dateRange} options={['y','cm','cy','12w','12m','x','xx']} disabled={loading} onChange={(value) => { setDateRange(value);}} />
              <CButton color="primary" disabled={loading} className="mr-2 ml-3" onClick={() => { fetchData({period: dateRange, groupBy: groupBy, generator: generator});}} >Submit</CButton>
            </CCol>








          </CRow>


        </CCardHeader>

        <CCardBody>


        <CRow>
          <CCol>
          <h5>Wind Speed</h5>

          <div>m/s</div>
          <CChartLine
            datasets={lineChartOneData.datasets}
            labels={lineChartOneData.labels}
            options={{
              tooltips: {
                enabled: true
              }
            }}
          />


          <br />
          <br />
          
          </CCol>
          </CRow>
          <CRow>
            <CCol>
          
          {false && <><h5>Wind Rose</h5>

          <div style={{maxWidth:'600px', margin:'auto'}}>{windRoseLabelsMap}</div>

          <CChartPolarArea
            datasets={polarChartData.datasets}
            labels={polarChartData.labels}
            options={{
              tooltips: {
                enabled: true
              }
            }}
          />


          <br />
          <br /></>}

          </CCol>
          </CRow>
          <CRow>


            <CCol>

          <h5>Temperature</h5>

          <div>Â°C</div>
          <CChartLine
            datasets={lineChartTwoData.datasets}
            labels={lineChartTwoData.labels}
            options={{
              tooltips: {
                enabled: true
              }
            }}
          />
          </CCol>
          </CRow>
        </CCardBody>
      </CCard>

  )
}

export default ClimateSummary
