import React, { useState, useEffect } from 'react'

import { useSelector, useDispatch } from 'react-redux'
import {setCookie, getCookie} from '../helpers/sessionCookie.js'
import {
  CHeader,
  CToggler,
  CHeaderBrand,
  CHeaderNav,
  CHeaderNavItem,
  CHeaderNavLink,
  CSubheader,
  CBreadcrumbRouter,
  CLink,
  CSelect,
  CRow,
  CCol
} from '@coreui/react'
import CIcon from '@coreui/icons-react'

import DataAPI from '../helpers/DataAPI.js'

// routes config
import routes from '../routes'

import {
  TheHeaderDropdown,
  TheHeaderDropdownMssg,
  TheHeaderDropdownNotif,
  TheHeaderDropdownTasks
}  from './index'

const TheHeader = () => {
  const dispatch = useDispatch()
  const sidebarShow = useSelector(state => state.sidebarShow)

  const [parks, setParks] = useState([]);
  const [parksLoaded, setParksLoaded] = useState(false);


   useEffect(() => {

      fetchParks();

  }, []);




  const updateLocation = (locationId) => {

    console.log(`Updating location to: security/authenticate/location/${locationId}`)

    DataAPI({
      'endpoint': `security/authenticate/location/${locationId}`,
      'method': 'GET'
    }).then(
      response => {

        console.log(response)
        
       setCookie('location', locationId);
       window.location.reload();

      }
    ).catch(
      response => {
        console.error(response)
      }
    );

  }


  const fetchParks = () => {

    DataAPI({
      'endpoint': 'security/authenticate/location',
      'method': 'GET'
    }).then(
      response => {


        
        if (!parksLoaded) {
          setParks(response);
          setParksLoaded(true);
        }

      }
    );

  }

  const toggleSidebar = () => {
    const val = [true, 'responsive'].includes(sidebarShow) ? false : 'responsive'
    dispatch({type: 'set', sidebarShow: val})
  }

  const toggleSidebarMobile = () => {
    const val = [false, 'responsive'].includes(sidebarShow) ? true : 'responsive'
    dispatch({type: 'set', sidebarShow: val})
  }

  return (
    <CHeader withSubheader>
      <CToggler
        inHeader
        className="ml-md-3 d-lg-none"
        onClick={toggleSidebarMobile}
      />
      <CToggler
        inHeader
        className="ml-3 d-md-down-none"
        onClick={toggleSidebar}
      />
      <CHeaderBrand className="mx-auto d-lg-none" to="/"></CHeaderBrand>



      {true && <CHeaderNav className="d-md-down-none mr-auto">
        <CHeaderNavItem className="px-3" >
          {false && <CHeaderNavLink to="/dashboard">Dashboard</CHeaderNavLink>}
        </CHeaderNavItem>
        <CHeaderNavItem className="px-3">
        </CHeaderNavItem>
      </CHeaderNav>}

      <CHeaderNav className="px-3">

      { getCookie('name') }&nbsp;<a onClick={(ev) => { setCookie('name', ''); ev.preventDefault(); ev.stopPropagation(); window.location.reload(); return false;   }} href="#">Logout</a>

        {false && <><TheHeaderDropdownNotif/>
        <TheHeaderDropdownTasks/>
        <TheHeaderDropdownMssg/></>}
        <TheHeaderDropdown/>
      </CHeaderNav>

      <CSubheader className="px-3 justify-content-between">
        <CBreadcrumbRouter
          className="border-0 c-subheader-nav m-0 px-0 px-md-3"
          routes={routes}
        />
          {false && <div className="d-md-down-none mfe-2 c-subheader-nav">
                    <CLink className="c-subheader-nav-link"href="#">
                      <CIcon name="cil-speech" alt="Settings" />
                    </CLink>
                    <CLink
                      className="c-subheader-nav-link"
                      aria-current="page"
                      to="/dashboard"
                    >
                      <CIcon name="cil-graph" alt="Dashboard" />&nbsp;Dashboard
                    </CLink>
                    <CLink className="c-subheader-nav-link" href="#">
                      <CIcon name="cil-settings" alt="Settings" />&nbsp;Settings
                    </CLink>
                    </div>
                  }



        <CRow className="flex-center">
          <CCol style={{whiteSpace: 'nowrap'}}>Parque&nbsp;
          <CSelect onChange={(ev) => { updateLocation(ev.target.value); }} custom name="ccmonth" id="ccmonth">

        {parks && parks.map((oGenerator) => (
                                <option selected={oGenerator.id==getCookie('location')?'selected':''} value={oGenerator.id}>
                                  {oGenerator.name}
                                </option>
                              ))}</CSelect>
                              </CCol>
        </CRow>

      </CSubheader>
    </CHeader>
  )
}

export default TheHeader
