import React, { lazy, useEffect, useState } from 'react'

import {
  CDataTable,
  CBadge,
  CButton,
  CButtonGroup,
  CCard,
  CCardBody,
  CCardFooter,
  CCardHeader,
  CCol,
  CProgress,
  CRow,
  CCallout,
  CSpinner,
  CWidgetDropdown,
  CDropdown,
  CDropdownMenu,
  CDropdownItem,
  CDropdownToggle,
  CCardTitle,
  CCardText
} from '@coreui/react'

import CIcon from '@coreui/icons-react'

import MainChartExample from '../../../charts/MainChartExample.js'

import DataAPI from '../../../../helpers/DataAPI.js'
import {DateFilter, round, getDateLabel, getCurrentDateNDaysBefore} from '../../../../helpers/utils.js'
import {setCookie,getCookie} from '../../../../helpers/sessionCookie.js'

import { Doughnut } from 'react-chartjs-2';

const WidgetsDropdown = lazy(() => import('./Widgets.js'))
const WidgetsBrand = lazy(() => import('../../../widgets/WidgetsBrand.js'))

const Overview = () => {

  const [loading, setLoading] = useState(false);
  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoaded2, setDataLoaded2] = useState(false);
  const [date, setDate] = useState('');
  const [generators, setGenerators] = useState([]);
  
  const [period, setPeriod] = useState('y');

  const [totalACProductionMwh, setTotalACProductionMwh] = useState();
  const [irradiationKwhM2, setIrradiationKwhM2] = useState();
  const [avgAmbientTemp, setAvgAmbientTemp] = useState();

  const [timeAvailability, setTimeAvailability] = useState('');
  const [performance, setperformance] = useState('');
  const [specificYield, setspecificYield] = useState();

  const [timeAvailabilityChartData, setTimeAvailabilityChartData] = useState(
    {
      datasets: [],
      labels: []
    }
  );
  const [performanceChartData, setPerformanceChartData] = useState(
    {
      datasets: [],
      labels: []
    }
  );
  const [specificYieldChartData, setSpecificYieldChartData] = useState(
    {
      datasets: [],
      labels: []
    }
  );


  useEffect(() => {
    loadGenerators();
    refreshChart(period);
  }, []);

const loadGenerators = () => {

  DataAPI({
    'endpoint': 'admin/locations/current',
    'method': 'GET'
  }).then(
    response => {
      
      if (response && response.error) {
        setCookie('lastTimeStamp', '');
        setCookie('name', '');
        window.location.reload();
      }
      else if ((!dataLoaded || !dataLoaded2) && response && !response.error) {
        let gen = [];
        if (response.generators != null) {
          response.generators.forEach(
            generator => { 
              gen.push(generator.id)
            })
          setGenerators(gen);
        }
      }
    }
  );


}

const fetchData = (period) => {

  setLoading(true);
  
  const body = {}
  body.location = getCookie('location')
  if (period && period.split('--').length==2) {
    body.from = period.split('--')[0]
    body.to = period.split('--')[1]
  } else {
    body.period = period
  }

  DataAPI({
    'endpoint': 'solar/overview',
    'method': 'POST',
    'body': body
  }).then(
    response => {


      setLoading(false);

      if (response.error) {
          if (response.error.message) {
          return(alert(response.error.message))
          } else {
          return(alert(response.error)) 
          }
      }

      let chartData = response.data && response.data[0];

      if (chartData) {

        const timeAvailability = round(chartData.timeBasedAvailability);
        const performance = round(chartData.performance)
        const specificYield = round(chartData.specificYield);
        const totalACProductionMwh = round(chartData.totalACProductionMwh);
        const irradiationKwhM2 = round(chartData.irradiationKwhM2);
        const avgAmbientTemp = round(chartData.avgAmbientTemp);

        const specificYieldDataset = {
          clip:true,
          labels: ['Specific Yield'],
          datasets: [{
            borderWidth: 0,
            data: [specificYield,100-specificYield],
            backgroundColor: ['#ca6510', 'rgba(0,0,0,.7)']
          }
        ]}


        const timeAvailabilityDataset = {
          clip:true,
          labels: ['Time-based Availability'],
          datasets: [{
            borderWidth: 0,
            data: [timeAvailability,100-timeAvailability],
            backgroundColor: ['#0a58ca', '#052c65']
          }
        ]}

        const performanceDataset = {
          clip:true,
          labels: ['Performance'],
          datasets: [{
            borderWidth: 0,
            data: [performance,100-performance],
            backgroundColor: ['#722595', '#380a4e']
          }
        ]}
      
        setAvgAmbientTemp(`${timeAvailability}%`)
        setTimeAvailability(`${timeAvailability}%`)
        setperformance(`${performance}%`)
        setspecificYield(`${specificYield}%`)
        
        setTimeAvailabilityChartData(timeAvailabilityDataset)
        setPerformanceChartData(performanceDataset)
        setSpecificYieldChartData(specificYieldDataset)
        setTotalACProductionMwh(totalACProductionMwh)
        setIrradiationKwhM2(irradiationKwhM2)

      }
    }
  );
}

  const refreshChart = (period) => {
    fetchData(period);
  }

  const filterData = (date) => {
    setPeriod(date);
    fetchData(date);
  }

  const options = {
    animation: { duration: loading ? 0 : 1000 },
    plugins: {
      legend: {
        labels: {
          color: '#ffffff'
        }
      },
      tooltip: {
        callbacks: {
          label: function(tooltipItem) {
            return round(tooltipItem.raw,1) + ' %'
          }
        }
      },
    },
  };
  const bodyOpacity = loading?.7:1

  return (
    <>
      
      <CCard>
        <CCardHeader>
          <CRow>
            <CCol sm="9">
              <h3 id="traffic" className="card-title mb-0">
                Overview
              </h3>
              <div className="small text-medium-emphasis">{getDateLabel(period)}</div>
            </CCol>
            <CCol sm="3" className="text-right">
              <DateFilter warning={"Seleccionar un rango máximo de 31 días"} options={['y','cm','cy','x','xx']} disabled={loading} onChange={(value) => { filterData(value); }} />
            </CCol>
          </CRow>
        </CCardHeader>
        
        <CCardBody>
          <div style={{opacity:{bodyOpacity}}} >

            {false && <div style={{zIndex: 999, position: 'absolute', width: '100%', height: '100%', backgroundColor:'rgba(255,255,255,.7)'}}><CSpinner style={{position: 'absolute'}} /></div>}
          
            <CRow>
              <CCol sm="6" lg="3">
                <CCard
                  color={"success"}
                  textColor={"white"}
                  style={{opacity: loading ? 0.7 : 1}}
                >
                  <CCardBody>
                    <CCardTitle>PRODUCTION AND CLIMATE</CCardTitle>
                    <CCardText>
                      <p class="h6">Production: {totalACProductionMwh!=undefined?round(totalACProductionMwh )+' MWh':''}</p>
                      <p class="h6">Irradiation: {irradiationKwhM2!=undefined?round(irradiationKwhM2)+' Kwh/m2':''}</p>
                      <p class="h6">Ambient Temperature: {avgAmbientTemp!=undefined?round(avgAmbientTemp)+' °C':''}</p>
                    </CCardText>
                  </CCardBody>
                </CCard>
              </CCol>

              <CCol sm="6" lg="3">
                <CWidgetDropdown
                  color="gradient-warning"
                  header="SPECIFIC YIELD (MWh/MW)"
                  text={specificYield}
                  style={{opacity: loading ? 0.7 : 1}}
                  footerSlot={
                    <div className="px-lg-3 pb-lg-3 text-center">
                      <div className="d-inline-block w-100" style={{maxWidth:'300px'}}>
                        <Doughnut
                          data={specificYieldChartData}
                          options={options}
                        />
                      </div>
                    </div>
                  }
                >
                  
                </CWidgetDropdown>
              </CCol>

              <CCol sm="6" lg="3">
                <CWidgetDropdown
                  color="gradient-info"
                  header="TIME-BASED AVAILABILITY (%)"
                  text={timeAvailability}
                  style={{opacity: loading ? 0.7 : 1}}
                  footerSlot={
                    <div className="px-lg-3 pb-lg-4 text-center">
                      <div className="d-inline-block w-100" style={{maxWidth:'300px'}}>
                        <Doughnut
                          data={timeAvailabilityChartData}
                          options={options}
                        />
                      </div>
                    </div>
                  }
                >
                </CWidgetDropdown>
              </CCol>

              <CCol sm="6" lg="3">
                <CWidgetDropdown
                  color="gradient-purple"
                  header="PERFORMANCE"
                  text={performance}
                  style={{opacity: loading ? 0.7 : 1}}
                  footerSlot={
                    <div className="px-lg-3 pb-lg-4 text-center">
                      <div className="d-inline-block w-100" style={{maxWidth:'300px'}}>
                        <Doughnut
                          data={performanceChartData}
                          options={options}
                        />
                      </div>
                    </div>
                  }
                >
                </CWidgetDropdown>
              </CCol>

            </CRow>
          </div>
          
        </CCardBody>

      </CCard>

    </>
  )
}

export default Overview
