import { Redirect } from "react-router-dom";


import DataAPI from '../../../helpers/DataAPI.js'
import {setCookie} from '../../../helpers/sessionCookie.js'

import React, {useState} from 'react'
import { Link } from 'react-router-dom'
import {
  CButton,
  CCard,
  CCardBody,
  CCardGroup,
  CCol,
  CContainer,
  CForm,
  CInput,
  CInputGroup,
  CInputGroupPrepend,
  CInputGroupText,
  CLink,
  CRow
} from '@coreui/react'
import CIcon from '@coreui/icons-react'
import logo from '../../../assets/renovus-logo.png'

const Login = () => {

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [authenticated, setAuthenticated] = useState(false);


  const authenticateUser = () => {

    DataAPI({
      'endpoint': 'security/authenticate',
      'method' : 'POST',
      'body': {
          "email": username,
          "password": password
      },
    }).then(
      response => {


        if (response.authenticated) {
          console.log('response~~~~~~',response)
          
          setCookie('name', response.name);
          setCookie('user', response.id && response.id);
          setCookie('client', response.client.id && response.client.id);
          setCookie('location', response.location.id);
          setCookie('parkType', response.client.type);
          
          setCookie('functionalities', response.functionalities);
          var arrFunctionalities = response.functionalities.map((f));
          var json_str = JSON.stringify(arr);
createCookie('mycookie', json_str);
          // setCookie('parkName', response.client.name);
          
          setAuthenticated(true);
          window.location.reload();
        } else {
          alert('wrong user!');
        }

      }
    ).catch(
      e=> { alert('error!') }
    )

  }


  return (


    authenticated ?

      <Redirect to={'/'} />:

  
    
    <div className="bg-gradient-custom c-app c-default-layout flex-row align-items-center">
      <CContainer>
        <CRow className="justify-content-center">
          <CCol md="8">
            <CCardGroup className="shadow">
              <CCard className="p-4 border-light">
                <CCardBody>
                  <CForm>
                    <h1 className="text-dark-blue">Login</h1>
                    <p className="text-muted">Sign In to your account</p>
                    <CInputGroup className="mb-3">
                      <CInputGroupPrepend>
                        <CInputGroupText>
                          <CIcon name="cil-user" />
                        </CInputGroupText>
                      </CInputGroupPrepend>
                      <CInput onChange={(ev) => {setUsername(ev.target.value)}} value={username} type="text" placeholder="E-mail" autoComplete="username" />
                    </CInputGroup>
                    <CInputGroup className="mb-4">
                      <CInputGroupPrepend>
                        <CInputGroupText>
                          <CIcon name="cil-lock-locked" />
                        </CInputGroupText>
                      </CInputGroupPrepend>
                      <CInput onChange={(ev) => {setPassword(ev.target.value)}} type="password" placeholder="Password" autoComplete="current-password" />
                    </CInputGroup>
                    <CRow>
                      <CCol xs="6">
                        <CButton onClick={authenticateUser} color="primary" className="px-4 mr-3">Login</CButton>
                        
                      </CCol>
                      <CCol xs="6" className="text-right d-flex justify-content-end align-items-center">
                        <CLink to="/requestPasswordReset" className="text-dark-blue">Reset password?</CLink>
                      </CCol>
                    </CRow>
                  </CForm>
                </CCardBody>
              </CCard>
              <CCard className="text-dark bg-light border-light py-5 d-md-down-none" style={{ width: '44%' }}>
                <CCardBody className="text-center">
                  <div>
                    <img src={logo} width="250" class="mb-3"/>
                    <p>Please login with your e-mail and password.</p>
                  </div>
                </CCardBody>
              </CCard>
            </CCardGroup>
          </CCol>
        </CRow>
      </CContainer>
    </div>
    
  )


}

export default Login
