import React, {
    useState,
    useEffect,
    useCallback,
    useRef,
    Fragment,
  } from 'react';
import moment from 'moment';
import {setCookie,getCookie} from './sessionCookie.js'
  
const SessionTimeout =()=> {

    const [events, setEvents] = useState(['click', 'load', 'scroll']);
    const [second, setSecond] = useState(0);
    // const {
    //     authState: { authenticated },
    //     logout,
    //   } = useAuth();

    let timeStamp;
    let warningInactiveInterval = useRef();
    let startTimerInterval = useRef();
    let authenticated = getCookie('name')!==false && getCookie('name')!=='';

    // start inactive check
    let timeChecker = () => {
        startTimerInterval.current = setTimeout(() => {
            let storedTimeStamp = getCookie('name'); //sessionStorage.getItem('lastTimeStamp');
            warningInactive(storedTimeStamp);
        }, 600);
    };

    // warning timer
    let warningInactive = (timeString) => {
        clearTimeout(startTimerInterval.current);
    
        warningInactiveInterval.current = setInterval(() => {
        const maxTime = 2; // Maximum ideal time given before logout 
        // const popTime = 1; // remaining time (notification) left to logout.
    
        const diff = moment.duration(moment().diff(moment(timeString)));
        const minPast = diff.minutes();
        // const leftSecond = 60 - diff.seconds();
    
        // if (minPast === popTime) {
        //     setSecond(leftSecond);
        // }
    
        if (minPast === maxTime) {
            clearInterval(warningInactiveInterval.current);
            // sessionStorage.removeItem('lastTimeStamp');
            setCookie('lastTimeStamp', '');
            // your logout function here
        }
        }, 1000);
    };

    // reset interval timer
    let resetTimer = useCallback(() => {
        clearTimeout(startTimerInterval.current);
        clearInterval(warningInactiveInterval.current);
    
        if (authenticated) {
            timeStamp = moment();
            setCookie('lastTimeStamp', timeStamp);
        } else {
            clearInterval(warningInactiveInterval.current);
            setCookie('lastTimeStamp', '');
        }
        timeChecker();
    }, [authenticated]);
    
    
    useEffect(() => {
        events.forEach((event) => {
        window.addEventListener(event, resetTimer);
        });
    
        timeChecker();
    
        return () => {
        clearTimeout(startTimerInterval.current);
        };
    }, [resetTimer, events, timeChecker]);


    return <Fragment />;
};

export default SessionTimeout;
