import React, { lazy, useEffect, useState } from 'react'

import {
  CDataTable,
  CBadge,
  CButton,
  CButtonGroup,
  CCard,
  CCardBody,
  CCardFooter,
  CCardHeader,
  CCol,
  CProgress,
  CRow,
  CCallout,
  CSpinner,
  CWidgetDropdown,
  CDropdown,
  CDropdownMenu,
  CDropdownItem,
  CDropdownToggle,
  CCardTitle,
  CCardText
} from '@coreui/react'

import CIcon from '@coreui/icons-react'

import MainChartExample from '../charts/MainChartExample.js'

import DataAPI from '../../helpers/DataAPI.js'
// import {DateFilter, round, getDateLabel} from '../../helpers/utils.js'

import { Doughnut } from 'react-chartjs-2';

const WidgetsDropdown = lazy(() => import('./Widgets.js'))
const WidgetsBrand = lazy(() => import('../widgets/WidgetsBrand.js'))

const Dashboard = () => {

  const [data, setData] = useState('');
  const [loading, setLoading] = useState(false);
  const [fetchingData, setFetchingData] = useState(false);
  const [date, setDate] = useState('');
  
  const [dateRange, setDateRange] = useState('y');
  const [dateRangeName, setDateRangeName] = useState('Ayer');


  const [averageWindSpeed, setAverageWindSpeed] = useState();
  const [metMastWindSpeed, setMetMastWindSpeed] = useState();

  const [availability, setAvailability] = useState('');
  const [loadFactor, setLoadFactor] = useState();
  const [mWh, setMWh] = useState([]);

  const [availabilityChartData, setAvailabilityChartData] = useState([]);
  const [loadFactorChartData, setLoadFactorChartData] = useState([]);


  useEffect(() => {
      
      refreshChart(dateRange);

  }, []);


  const fields = [
  'id',
  'name',
  'MWh generated for period',
  'Capacity factor',
  'Time based availability',
  'Met mast wind speed',
  'MWh from wind turbines'
];

const getBadge = status => {
  switch (status) {
    case 'Active': return 'success'
    case 'Inactive': return 'secondary'
    case 'Pending': return 'warning'
    case 'Banned': return 'danger'
    default: return 'primary'
  }
}


const fetchData = (dateRange) => {

  setLoading(true);
  
  const body = {}

  if (dateRange && dateRange.split('--').length==2) {
      body.from = dateRange.split('--')[0]
      body.to = dateRange.split('--')[1]
    } else {
      body.period = dateRange
    }

    DataAPI({
      'endpoint': 'chart/overview',
      'method': 'POST',
      'body': body
    }).then(
      response => {


        setLoading(false);



        if (response.error) {
           if (response.error.message) {
            return(alert(response.error.message))
           } else {
            return(alert(response.error)) 
           }
        }


          let chartData = response.data && response.data[0];

          if (chartData){

        const availability = Math.round(chartData.timeBasedAvailability*10)/10;
        const loadFactor = Math.round(chartData.capacityFactor*10)/10;
        const mWh = Math.round(chartData.mWhFromWindTurbines*10)/10;

        const loadFactorDataset = {
          clip:true,
          labels: ['Load Factor'],
          datasets: [{
            borderWidth: 0,
            data: [loadFactor,100-loadFactor],
            backgroundColor: ['#ca6510', 'rgba(0,0,0,.7)']
          }
        ]}


        const availabilityDataset = {
          clip:true,
          labels: ['Availability'],
          datasets: [{
            borderWidth: 0,
            data: [availability,100-availability],
            backgroundColor: ['#0a58ca', '#052c65']
          }
        ]}

        setAverageWindSpeed(chartData.averageWindSpeed);
        setMetMastWindSpeed(chartData.metMastWindSpeed);

        setAvailability(`${availability}%`)
        setLoadFactor(`${loadFactor}%`)
        
        setAvailabilityChartData(availabilityDataset)
        setLoadFactorChartData(loadFactorDataset)
        setMWh(mWh)

        
        
      }


          // setDateRange(`${response.chart.from.replace(" 00:00:00", "")} - ${response.chart.to.replace(" 00:00:00", "")}`);

      }
    );


}


  const refreshChart = (dateRange) => {
    fetchData(dateRange);
  }

  // const filterData = (date) => {
  //   setDateRangeName(getDateLabel(date));
  //   fetchData(date);
  //   // setDateRangeName(value); setDateRange(value); refreshChart();
  // }

  const options = {animation: {duration: loading ? 0 : 1000}};
  const bodyOpacity = 1;//loading?.5:1

  return (
    <>
      
      <CCard>
        <CCardHeader>
          <CRow>
            <CCol sm="7">
              <h4 id="traffic" className="card-title mb-0">
                Overview
              </h4>
              <div className="small text-medium-emphasis">{dateRangeName}</div>
              </CCol>
            <CCol sm="5" class="text-right">

            <DateFilter warning={"Seleccionar un rango máximo de 31 días"} options={['y','cm','x']} disabled={loading} onChange={(value) => { filterData(value); }} />

                            </CCol>
                            </CRow>


        </CCardHeader>
        <CCardBody>

        <div style={{opacity:bodyOpacity}}>

        {false && <div style={{zIndex: 999, position: 'absolute', width: '100%', height: '100%', backgroundColor:'rgba(255,255,255,.7)'}}><CSpinner style={{position: 'absolute'}} /></div>}
        


        <CRow>
      

      {/* <CCol sm="6" lg="4">
        <CWidgetDropdown
          color="gradient-warning"
          header="LOAD FACTOR (%)"
          text={loadFactor}
          footerSlot={
            <div class="px-lg-5 pb-lg-5">
            <Doughnut
               data={loadFactorChartData}
               options={options}
            />
            </div>
          }
        >
          
        </CWidgetDropdown>
      </CCol>

      <CCol sm="6" lg="4">
        <CWidgetDropdown
          color="gradient-info"
          header="AVAILABILITY (%)"
          text={availability}
          footerSlot={
            <div class="px-lg-5 pb-lg-5">
            <Doughnut
               data={availabilityChartData}
               options={options}
            />
            </div>
          }
        >
          
        </CWidgetDropdown>
      </CCol> */}




      // <CCol sm="6" lg="4">
          

          <CCard
      color={"success"}
      textColor={"white"}

    >

      <CCardBody>
        <CCardTitle>PRODUCTION AND WIND</CCardTitle>
        <CCardText>
          {/* <p class="h6">Production: {round(mWh)} MWh</p>
          <p class="h6">Turbine average wind speed: {round(averageWindSpeed)} m/s</p>
          <p class="h6">Met mast average wind speed: {round(metMastWindSpeed)} m/s</p> */}
        </CCardText>
      </CCardBody>
    </CCard>

      </CCol>
    </CRow></div>
        
        </CCardBody>

        </CCard>

    </>
  )
}

export default Dashboard
