import React, { useState, useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CButtonGroup,
  CProgress,
  CCardFooter,
  CFormSelect,
  CDataTable,
  CBadge,
  CSelect,
  CModal,
  CModalHeader,
  CModalFooter,
  CModalBody,
  CModalTitle,
  CSpinner
} from '@coreui/react'
import {
  CChartBar,
  CChartLine,
  CChartDoughnut,
  CChartRadar,
  CChartPie,
  CChartPolarArea
} from '@coreui/react-chartjs'
import { DocsLink } from 'src/reusable'

import DataAPI from '../../../helpers/DataAPI.js'
import {DateFilter, formatDate, formatDate2, round, getDateLabel, formatNumber} from '../../../helpers/utils.js'
import {getCookie} from '../../../helpers/sessionCookie.js'

const Budget = () => {

  const currentYear = new Date().getFullYear()

  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState();
  const [curves, setCurves] = useState([]);
  const [filtersSelected, setFiltersSelected] = useState(false);
  const [fiscalYear, setFiscalYear] = useState(currentYear);
  const [addEstimatedCurtailments, setAddEstimatedCurtailments] = useState(true);
  const [parks, setParks] = useState([]);
  const [parksLoaded, setParksLoaded] = useState(false);
  const [park, setPark] = useState(null);
  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });
  const [graphData2, setGraphData2] = useState({
    labels: [],
    datasets: []
  });


  useEffect(() => {
    fetchParks();
  });


  const fetchParks = () => {

    DataAPI({
      'endpoint': 'security/authenticate/location',
      'method': 'GET'
    }).then(
      response => {
        
        if (!parksLoaded) {
          setParks(response);
          setParksLoaded(true);
        }

      }
    );

  }


  const fetchData = () => {

    setLoading(true);

    const body = {}
    body.location = getCookie('location');
    body.fiscalYear = fiscalYear
    body.addEstimatedCurtailments = addEstimatedCurtailments
    body.park = park


    DataAPI({
      'endpoint': 'chart/budget',
      'method': 'POST',
      'body': body
    }).then(
      response => {


        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

        const generatorData = response.generator && response.generator[0].data;

        setDataLoaded(true);

        if (generatorData.curves.length==0) return;

        const curves = generatorData.curves;

        const graphData = {
          labels: curves.map((x, i) => { return x.label }),
          datasets: [
            {
              label: '% of Time',
              data: curves.map((x, i) => { return x.deviation }),
              borderColor: '#7a5195',
              backgroundColor: '#7a5195',
              yAxisID: 'yDeviation',
              order: 1
            },
            {
              label: 'Average Wind Speed',
              data: curves.map((x, i) => { return x.windspeed }),
              borderColor: '#ffa600',
              backgroundColor: '#ffa600',
              type: 'line',
              yAxisID: 'yWindSpeed',
              order: 0
            },
          ],
        }

        setGraphData1(graphData);

      }
    );


  }

  

  const filterSubmit = () => {

    setDataLoaded(false)
    setFiltersSelected(true)
    // fetchData()
  
  }



  return (
    <CCard>
      <CCardHeader>
        <CRow>
          <CCol sm="4">
            <h3 id="traffic" className="card-title mb-0">
              Budget
            </h3>
            {/* <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div> */}
          </CCol>

          <CCol sm="8" className="text-right d-flex px-2 flex-center">
            <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4,minWidth:'64px'}}>Fiscal Year</h6>
            <CSelect disabled={loading} custom name="fiscalYear" id="fiscalYear" className="" onChange={(ev) => {setFiscalYear(ev.target.value);}}>
              {[0,1,2,3,4].map((x) => (
                                <option value={currentYear-x}>
                                  {currentYear-x}
                                </option>
                              ))}
            </CSelect>

            <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4,minWidth:'170px'}}>Add Estimated Curtailments</h6>
            <CSelect disabled={loading} custom name="addEstimatedCurtailments" id="addEstimatedCurtailments" className="" onChange={(ev) => {setAddEstimatedCurtailments(ev.target.value);}}>
              <option value="1">Yes</option>
              <option value="0">No</option>
            </CSelect>

            <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4}}>Park</h6>
            <CSelect disabled={loading} custom name="park" id="park" className="" onChange={(ev) => {setPark(ev.target.value);}}>
                {parks && parks.map((park) => (
                    <option key={park.id} value={park.id}>
                      {park.name}
                    </option>
                      ))}
            </CSelect>

            <CButton color="primary" disabled={loading || !parksLoaded} className="mr-2 ml-3" onClick={() => { fetchData();}} >Submit</CButton>
          </CCol>

          </CRow>
      </CCardHeader>
      <CCardBody>

        {!loading || dataLoadError ? 

          <div>

            <CRow className={'mb-5'}>
              <CCol>
                <h4 className="pb-2 mb-4 border-bottom" >Monthly Production</h4>
                <div>MWh</div>
                <Line
                  data={lineChartOneData}
                  options={options1}
                />
              </CCol>
            </CRow>

            <CRow className={'mb-5'}>
              <CCol>
                <h4 className="pb-2 mb-4 border-bottom">Temperature and Air Density</h4>
                <div className='d-flex'>
                  <div className="text-left" style={{width: '50%'}}>Â°C</div>
                  <div className="text-right" style={{width: '50%'}}>kg/m3</div>
                </div>
                <Line 
                  data={lineChartTwoData}
                  options={options2} />
              </CCol>
            </CRow>

            <CRow className={'mb-3'}>
              <CCol xs="12">
                <h4 className="pb-2 mb-4 border-bottom" >Wind Direction</h4>
                <div>Degrees</div>
                <Line
                  data={lineChartThreeData}
                  options={options3}
                />
              </CCol>
              <CCol xs="12" className={'mt-4'}>
                <CCard className={'pl-3 pr-2'} style={{maxWidth: '480px', marginLeft: 'auto', marginRight: 'auto'}}>
                  <CCardBody>
                    <CRow>
                      <CCol sm="6" xs="12">
                        <CLabel className={'w-100 mb-0'}>0-45 North to Northeast</CLabel>
                        <CLabel className={'w-100 mb-0'}>45-90 NorthEast to East</CLabel>
                        <CLabel className={'w-100 mb-0'}> 90-135 East to SouthEast</CLabel>
                        <CLabel className={'w-100 mb-0'}>135-180 SouthEast to South</CLabel>
                      </CCol>
                      <CCol sm="6" xs="12">
                        <CLabel className={'w-100 mb-0'}> 180-225 South to SouthWest</CLabel>
                        <CLabel className={'w-100 mb-0'}>225-270 SouthWest to West</CLabel>
                        <CLabel className={'w-100 mb-0'}> 270-315 West to NorthWest</CLabel>
                        <CLabel className={'w-100 mb-0'}> 315-360 NorthWest to North</CLabel>
                      </CCol>
                    </CRow>
                  </CCardBody>
                </CCard>
                
              </CCol>
            </CRow>

            <CRow className={'mb-5'}>
              <CCol>
                <h4 className="pb-2 mb-4 border-bottom" >Turbulence Intensity</h4>
                <div>%</div>
                <Line
                  data={lineChartFourData}
                  options={options4}
                />
              </CCol>
            </CRow>

          </div>
        : 
          <div className='text-center'>
            <CSpinner 
              className="loading-spinner"
              color='#321fdb'
            />
          </div>
        }


      </CCardBody>
    </CCard>
  )
}

export default Budget
