import React, { useState, useEffect } from 'react'


import {
  CCard,
  CCardBody,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CSelect,
  CDataTable,
  CBadge,
  CSpinner,
  CImg,
} from '@coreui/react'

import DataAPI from '../../../../helpers/DataAPI.js'
import {formatNumber, formatDate2, round, getDateLabel, DateFilter} from '../../../../helpers/utils.js'
import {setCookie} from '../../../../helpers/sessionCookie.js'

import { Line } from 'react-chartjs-2';

import ChartLoading from '../../../others/ChartLoading.js'

import img from '../../../../assets/img-energy-generation.png'

import res from './res_short.json';


const EnergyGeneration = () => {

  const [generator, setGenerator] = useState('');
  const [generators, setGenerators] = useState([]);
  const [generatorsLoaded, setGeneratorsLoaded] = useState(false);
  const [dateRange, setDateRange] = useState('cm');
  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });

  useEffect(() => {
    loadGenerators();
    loadStaticData();
  }, []);


  const loadStaticData = () => {

    const data = res.data;
    const graphData = {
      labels: data.evolution.map((point, i) => { return point.x }),
      datasets: [
        {
          label: 'Data',
          data: data.evolution.map((point, i) => { return point.y * 100 }),
          borderColor: '#bc5090',
          backgroundColor: 'transparent',
          borderWidth: 1,
          pointRadius: 0
        },
        {
          label: 'Outlier Limit Top',
          data: data.evolution.map((point, i) => { return 80 }),
          borderColor: 'red',
          backgroundColor: 'transparent',
          borderWidth: 1.2,
          pointRadius: 0
        },
        {
          label: 'Outlier Limit Bottom',
          data: data.evolution.map((point, i) => { return 20 }),
          borderColor: 'red',
          backgroundColor: 'transparent',
          borderWidth: 1.2,
          pointRadius: 0
        },
        {
          label: 'Median Performance',
          data: data.evolution.map((point, i) => { return 50 }),
          borderColor: '#42e428',
          backgroundColor: 'transparent',
          borderWidth: 1.2,
          pointRadius: 0
        }
      ],
    }
    setGraphData(graphData);
  }


  const fetchData = () => {

    setLoading(true);

    const body = {}
    body.location = getCookie('location')
    if (generator) body.generators = [generator];
    if (period && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }

    DataAPI({
      'endpoint': 'wind/performanceIndex',
      'method': 'POST',
      'body': body
    }).then(
      response => {

        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

        setDataLoaded(true);
        setData(response);
        

        const data = {
          labels: [],
          datasets: []
        }
        const data2 = {
          labels: [],
          datasets: []
        }
        const data3 = {
          labels: [],
          datasets: []
        }

        setPerformanceData(responseData.data);
        responseData.data.forEach((rDs) => {

          const dataset = {
            labels: [],
            label: rDs.code,
            backgroundColor: generatorColors[rDs.code],
            data: [],
          };
          const dataset2y = {
            labels: [],
            label: "Production: "+rDs.code,
            backgroundColor: generatorColors[rDs.code],
            borderColor: generatorColors[rDs.code],
            borderWidth: 1,
            pointBorderColor: generatorColors[rDs.code],
            pointBorderWidth: 2,
            data: [],
            yAxisID: 'y',
            pointRadius: 4,
            pointHoverRadius: 5,
          };
          const dataset2y1 = {
            labels: [],
            label: "Wind speed: "+rDs.code,
            borderColor: generatorColors[rDs.code],
            borderWidth: 1,
            pointBorderColor: generatorColors[rDs.code],
            pointBorderWidth: 2,
            pointBackgroundColor: '#d8dbe0',
            data: [],
            yAxisID: 'y1',
            pointRadius: 4,
            pointHoverRadius: 5,
          };

          const dataset3 = {
            labels: [],
            label: rDs.code,
            backgroundColor: generatorColors[rDs.code],
            data: [],
          };

          rDs.data.graph.forEach((rD) => {
            const label = (groupBy == "day") ? `${formatDate(rD.from)}` : `${formatDate(rD.from)} - ${formatDate(rD.to)}`
            dataset.labels.push(label)
            dataset2y.labels.push(label)
            dataset2y1.labels.push(label)
            dataset3.labels.push(label)
            dataset.data.push(rD.performance)
            dataset2y.data.push(rD.power/1000)
            dataset2y1.data.push(rD.windspeed)
            dataset3.data.push(rD.availability)

            if (rDs.name == "Total" && rDs.id.length > 1) {
              dataset.label = "Total"
              dataset2y.label = "Production: Total"
              dataset2y1.label = "Wind Speed: Total"
              dataset3.label = "Total"
              dataset.backgroundColor = generatorColors[rDs.name]
              dataset2y.backgroundColor = generatorColors[rDs.name]
              dataset2y.borderColor = generatorColors[rDs.name]
              dataset2y1.borderColor = generatorColors[rDs.name]
              dataset3.backgroundColor = generatorColors[rDs.name]
            }
          });

          dataset.labels.reverse();
          dataset.data.reverse();
          dataset2y.labels.reverse();
          dataset2y.data.reverse();
          dataset2y1.labels.reverse();
          dataset2y1.data.reverse();
          dataset3.labels.reverse();
          dataset3.data.reverse();

          if (rDs.name != "Total" || rDs.id.length > 1) {
            data.datasets.push(dataset);
            data2.datasets.push(dataset2y);
            data2.datasets.push(dataset2y1);
            data3.datasets.push(dataset3);
          }

        });

        responseData.data[0].data.graph.forEach((rD) => {
          const rDfrom = rD.from;
          const rDto = rD.to;
          const label = (groupBy == "day") ? `${formatDate(rD.from, 'noyear')}` : `${formatDate(rD.from, 'noyear')} - ${formatDate(rD.to, 'noyear')}`
          data.labels.push(label);
          data2.labels.push(label);
          data3.labels.push(label);

        });

        data.labels.reverse();
        data2.labels.reverse();
        data3.labels.reverse();

        setGraphData(data);
        setGraphData2(data2);
        setGraphData3(data3);
        setDataLoaded(true);

      }
    );


  }


  const loadGenerators = () => {

    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        
        if (response && response.error) {
          setCookie('lastTimeStamp', '');
          setCookie('name', '');
          window.location.reload();
        }
        else if (!dataLoaded && response  && !response.error) {

          if (response.generators != null) {
            setGenerators(response.generators);
            setGenerator(response.generators[0].id);
          }
          setGeneratorsLoaded(true)

        }
      }
    );


  }



  const options = {
    responsive: true,
    options: {
      animation: false,
      spanGaps: true
    },
    scales: {
      y:
        {
          suggestedMin: 10,
          suggestedMax: 90,
        }
    },
    tooltips: {
      enabled: false
    },
    elements: {
      line: {
          tension: .4
      }
    },
    plugins: {
      legend: {
        display: false
      },
      tooltip: {
        callbacks: {
          label: function(tooltipItem, data) {
              return 'Percentile of performance: ' + round(tooltipItem.raw,1) + " %";
          }
        }
      }
    }
  };


  return (
      <CCard className="mb-4">

      <CCardHeader>
          <CRow>
            <CCol sm="5">
              <h3 id="traffic" className="card-title mb-0">
              AI Analytics - Energy Generation
              </h3>
              <div className="small text-medium-emphasis">{getDateLabel(period)}</div>
            </CCol>
            <CCol sm="7" className="text-right d-flex flex-center flex-justify-end">
              <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4,minWidth:'64px'}}>Turbine</h6>
              <CSelect className={'input-md'} disabled={loading} onChange={(ev) => {setGenerator(ev.target.value); }} custom name="generator" id="generator">
              {generators.map((oGenerator) => (
                                  <option value={oGenerator.id}>
                                    {oGenerator.code}
                                  </option>
                                ))}</CSelect>
              <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4}}>Period</h6>
              <DateFilter className={'input-md'} value={period} options={['y','cm','cy','x','xx']} disabled={loading} onChange={(value) => { setPeriod(value); }} />
              <CButton color="primary" disabled={loading || !generatorsLoaded} className="mr-2 ml-3" onClick={() => { fetchData();}} >Submit</CButton>
            </CCol>
           </CRow>

        </CCardHeader>

        <CCardBody>

              
        <CRow className="mb-4">
          <CCol>
            <h4 className="pb-2 mb-4 text-center" >Wind Turbines for investigation/resolution: 3</h4>
            <div>Percentile of performance</div>
            <Line
              data={graphData}
