import React, { useState, useEffect } from 'react'


import {
  CCard,
  CCardBody,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CSelect,
  CDataTable,
  CBadge,
  CSpinner,
  CImg,
} from '@coreui/react'

import DataAPI from '../../../helpers/DataAPI.js'
import {formatNumber, formatDate2, round, getDateLabel, DateFilter} from '../../../helpers/utils.js'
import {setCookie} from '../../../helpers/sessionCookie.js'

import { Line } from 'react-chartjs-2';

import ChartLoading from '../../others/ChartLoading.js'

import img from '../../../assets/img-energy-generation.png'


const EnergyGeneration = () => {

  const [generator, setGenerator] = useState('');
  const [generators, setGenerators] = useState([]);
  const [generatorsLoaded, setGeneratorsLoaded] = useState(false);
  const [dateRange, setDateRange] = useState('cm');
  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });

  useEffect(() => {
    loadStaticData();
  }, []);


  const loadStaticData = () => {

    fetch('./res.json'
    ,{
      headers : { 
        'Content-Type': 'application/json',
        'Accept': 'application/json'
       }
    }
    )
    .then(function(response){
      const data = response.json().data;
      const graphData = {
        labels: data.evolution.map((point, i) => { return point.x }),
        datasets: [
          {
            label: '',
            data: data.evolution.map((point, i) => { return point.y }),
            backgroundColor: '#bc50907a',
          }
        ],
      }
      setGraphData(graphData);
    })   

  }



  const options = {
    responsive: true,
    animation: {duration: loading ? 0 : 1000},
    tooltips: {
      enabled: true
    },
    elements: {
      line: {
          tension: .4
      }
    },
    plugins: {
      tooltip: {
        mode: 'index',
        callbacks: {
          label: function(tooltipItem, data) {
              return tooltipItem.dataset.label + ': ' + round(tooltipItem.raw,1) + " m/s";
          }
        }
      }
    }
  };


  return (
      <CCard className="mb-4">

      <CCardHeader>
          <CRow>
            <CCol sm="6">
              <h3 id="diagnostics" className="card-title mb-0">
                AI Analytics - Energy Generation
              </h3>
              {/* <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div> */}
            </CCol>            
          </CRow>

        </CCardHeader>

        <CCardBody>

              
        <CRow className="mb-4">
          <CCol>
            <h4 className="pb-2 mb-4 border-bottom" >Wind Turbines for investigation/resolution: 3</h4>
            <div>Percentile of performance</div>
            <Line
              data={graphData}
              options={options}
            />



            {/* <CImg
              width={'100%'}
              src={img}
              alt=""
              className={'mt-3'}
            /> */}

          </CCol>
        </CRow>

          
        </CCardBody>

      </CCard>



  )
}

export default EnergyGeneration
