import React, { useEffect, useState } from 'react'
import DataAPI from '../../../helpers/DataAPI.js'
import {DateFilter, getDateLabel, formatDate, formatDate2, round} from '../../../helpers/utils.js'
import {setCookie,getCookie} from '../../../helpers/sessionCookie.js'
import moment from 'moment-timezone';

import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CSpinner,
  CLabel,
} from '@coreui/react'

import {
  Chart,
  Plugin
} from 'chart.js';

import { CChartLine } from '@coreui/react-chartjs'

import { Line } from 'react-chartjs-2'

// import { OPEN_WEATHER_API, STORMGLASS_API_KEY, STORMGLASS_API } from '../../../constants'
import arrow from '../../../assets/arrow.png'


const ClimateForecast = () => {

  const [dataLoaded, setDataLoaded] = useState(false)
  const [dataLoadError, setDataLoadError] = useState(false)
  const [loading, setLoading] = useState(true)
  const [arrowImage, setArrowImage] = useState(null)
  const [datetimes, setDatetimes] = useState([])

  const [lineChartOneData, setLineChartOneData] = useState({
    labels: [],
    datasets: []
  })

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {

    setLoading(true);

    var arrowImage = new Image(22,22);
    arrowImage.src = arrow
    arrowImage.onload = function() {
      setArrowImage(this);
    }

    // const APIResponse = await fetch(OPEN_WEATHER_API);
    // const responseData = await APIResponse.json();

    // if (responseData.error && responseData.error.message) {
    //   setDataLoadError(true)
    //   alert(responseData.error.message)
    // } 

    // const graphData = {
    //   labels: [],
    //   datasets: []
    // }

    // let firstDate = 0
    // let lastDate = 0

    // if (responseData) {

    //   graphData.labels = responseData.list.map( (x,i) => {
    //     return x.wind.deg;
    //   })

    //   const dataset = {
    //     label:  'Height 10 meters (Open Weather)',
    //     borderColor: '#003f5c',
    //     pointBackgroundColor: '#003f5c',
    //     pointBorderColor: 'transparent',
    //     backgroundColor: 'transparent',
    //     data: responseData.list.map( (x,i) => {
    //       return x.wind.speed;
    //     })
    //   }
    //   graphData.datasets.push(dataset)

    //   const datetimes = []
    //   firstDate = responseData.list[0].dt
    //   lastDate = responseData.list[responseData.list.length-1].dt
    //   responseData.list.forEach((value,index) => {
    //     datetimes.push(value.dt);
    //   })
    //   setDatetimes(datetimes)

    // }


    DataAPI({
      'endpoint': '/weather',
      'method': 'GET'
    }).then(
      response => {
        setLoading(false);
        if (response.error) {
            if (response.error.message) {
            return(alert(response.error.message))
            } else {
            return(alert(response.error)) 
            }
        }

        const responseData = response.data && response.data[0];

        const graphData = {
          labels: [],
          datasets: []
        }


      })


    // let start = moment()
    // let new_hour = Math.trunc(start.hour()/6)*6
    // start.set('hour',new_hour)
    // const APIResponse = await fetch(STORMGLASS_API.replace('@START@',start.unix()).replace('@END@',""), {
    //   headers: {
    //     'Authorization': STORMGLASS_API_KEY
    //   }
    // });
    // const responseData = await APIResponse.json();

    // if (responseData.errors && responseData.errors.key) {
    //   setDataLoadError(true)
    //   alert(responseData.errors.key)
    // } 

    const graphData = {
      labels: [],
      datasets: []
    }

    if (responseData && !responseData.errors) {

      graphData.labels = responseData.hours.filter( (x,i) => (i%6==0) ).map( (x,i) => {
        return x.windDirection100m.sg;
      })

      const dataset = {
        label:  'At 100 meters height',
        borderColor: '#ff764a',
        pointBackgroundColor: '#ef5675',
        pointBorderColor: 'transparent',
        backgroundColor: 'transparent',
        data: responseData.hours.filter( (x,i) => (i%6==0) ).map( (x,i) => {
          return x.windSpeed100m.sg;
        })
      }
      graphData.datasets.push(dataset)

      const datetimes = []
      responseData.hours.forEach((value,i) => {
        if (i%6==0)
          datetimes.push(value.time);
      })
      setDatetimes(datetimes)

    }

    setLineChartOneData(graphData)

    setLoading(false)

    


  }

  const plugins = [{
    afterDraw: (chart) => {
      let ctx = chart.ctx;
      var xAxis = chart.scales['x'];
      var yAxis = chart.scales['y'];
      xAxis.ticks.forEach((value, index) => {
        
        if (index%2==0) {
          var x = xAxis.getPixelForTick(index);

          // var deg = value.label;
          // var newImageCanvas = document.createElement('canvas');
          // newImageCanvas.width  = 22;
          // newImageCanvas.height = 22;
          // var newCtx = newImageCanvas.getContext('2d') ;
          // newCtx.save();
          // newCtx.translate(newImageCanvas.width/2,newImageCanvas.height/2);
          // newCtx.rotate((deg+90)*0.01745);
          // newCtx.translate(-newImageCanvas.width * 0.5, -newImageCanvas.height * 0.5);  
          // newCtx.drawImage(arrowImage, 0,0);
          // newCtx.restore();
          // ctx.drawImage(newImageCanvas, x - 10, yAxis.bottom + 34, arrowImage.width, arrowImage.height)
        
          const datetime = moment(datetimes[index])
          const month = datetime.month()+1 > 9 ? datetime.month()+1 : '0'+(datetime.month()+1)
          const day = datetime.date() > 9 ? datetime.date() : '0'+datetime.date()
          const hour = datetime.hour() > 9 ? datetime.hour() : '0'+(datetime.hour())
          const minute = datetime.minutes() > 9 ? datetime.minutes() : '0'+datetime.minutes()
          const date = day+"-"+month
          const time = hour+":"+minute
          ctx.font = "11px Arial";
          ctx.fillText(date, x - 12, yAxis.bottom + 16);
          ctx.fillText(time, x - 12, yAxis.bottom + 28);
          
        }
      });
      ctx.font = "12px Arial";
      // ctx.fillText("Direction", 0, yAxis.bottom + 49);
    }
  }];

  const options =  {
    type: 'line',
    responsive: true,
    animation: {duration: loading ? 0 : 1000},
    tooltips: {
      enabled: true
    },
    legend: {
      display: false
    },
    elements: {
      line: {
          tension: .4
      }
    },
    layout: {
        padding: {
            bottom: 80,
            left: 20,
            right: 20
        }
    },
    scales: {
      x: {
        ticks: {
            display: false
        }
      },
      y: {
        min: 0,
        beginAtZero: true
      }
    },
    plugins: {
      tooltip: {
        callbacks: {
          title: function(tooltipItem){
            return '';
          },
          label: function(tooltipItem) {
            const datetime = moment(datetimes[tooltipItem.dataIndex])
            const month = datetime.month()+1 > 9 ? datetime.month()+1 : '0'+(datetime.month()+1)
            const day = datetime.date() > 9 ? datetime.date() : '0'+datetime.date()
            const hour = datetime.hour() > 9 ? datetime.hour() : '0'+(datetime.hour())
            const minute = datetime.minutes() > 9 ? datetime.minutes() : '0'+datetime.minutes()
            const date = day+"-"+month
            const time = hour+":"+minute
            return ["Date: "+date+" "+time,"Wind Speed: "+round(tooltipItem.raw,1) + ' m/s']
          }
        }
      },
      // legend: {
      //   display: false
      // },
    }
  };


  return (

      <CCard>

      <CCardHeader>
          <CRow>
            <CCol sm="4">
              <h3 id="traffic" className="card-title mb-0">
                Climate - Forecast
              </h3>
              {/* <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div> */}
            </CCol>

            <CCol sm="8" className="text-right d-flex flex-center flex-justify-end">
              {/* <h6 className="mr-2 ml-4 m-0" style={{lineHeight:1.2}}>Period</h6>
              <DateFilter options={['y','cm','cy','x','xx']} disabled={loading} onChange={(value) => { setDateRange(value); }} /> */}
            </CCol>
          </CRow>

        </CCardHeader>

        <CCardBody>

            {!loading || dataLoadError ? 

              <div>

                <CRow className={'mb-5'}>
                  <CCol>
                    <h4 className="pb-2 mb-4 border-bottom" >Wind Speed</h4>
                    <div>m/s</div>
                    <Line
                      data={lineChartOneData}
                      options={options}
                      plugins={plugins}
                    />
                  </CCol>
                </CRow>


              </div>
            : 
              <div className='text-center'>
                <CSpinner 
                  className="loading-spinner"
                  color='#321fdb'
                />
              </div>
            }

        </CCardBody>
      </CCard>

  )
}

export default ClimateForecast
