import React, { lazy, useEffect, useState } from 'react'

import {
  CDataTable,
  CBadge,
  CButton,
  CButtonGroup,
  CCard,
  CCardBody,
  CCardFooter,
  CCardHeader,
  CCol,
  CProgress,
  CRow,
  CCallout,
  CSpinner,
  CWidgetDropdown,
  CDropdown,
  CDropdownMenu,
  CDropdownItem,
  CDropdownToggle,
  CCardTitle,
  CCardText
} from '@coreui/react'

import CIcon from '@coreui/icons-react'

import MainChartExample from '../../charts/MainChartExample.js'

import DataAPI from '../../../helpers/DataAPI.js'
import {DateFilter, round, getDateLabel, getCurrentDateNDaysBefore} from '../../../helpers/utils.js'
import {setCookie,getCookie} from '../../../helpers/sessionCookie.js'

import { Doughnut } from 'react-chartjs-2';

const WidgetsDropdown = lazy(() => import('./Widgets.js'))
const WidgetsBrand = lazy(() => import('../../widgets/WidgetsBrand.js'))

const Overview = () => {

  const [data, setData] = useState('');
  const [loading, setLoading] = useState(false);
  const [dataLoaded, setDataLoaded] = useState(false);
  const [fetchingData, setFetchingData] = useState(false);
  const [date, setDate] = useState('');
  const [generators, setGenerators] = useState([]);
  
  const [dateRange, setDateRange] = useState('y');
  // const [dateRangeName, setDateRangeName] = useState('Ayer');

  const [averageWindSpeed, setAverageWindSpeed] = useState();
  const [metMastWindSpeed, setMetMastWindSpeed] = useState();
  const [mWh, setMWh] = useState([]);
  const [estimatedCurtailment, setEstimatedCurtailment] = useState([]);

  const [timeAvailability, setAvailability] = useState('');
  const [loadFactor, setLoadFactor] = useState();

  // const [alert1, setAlert1] = useState([]);
  // const [alert2, setAlert2] = useState([]);
  // const [alert3, setAlert3] = useState([]);

  const [timeAvailabilityChartData, setTimeAvailabilityChartData] = useState(
    {
      datasets: [],
      labels: []
    }
  );
  const [productionAvailabilityChartData, setProductionAvailabilityChartData] = useState(
    {
      datasets: [],
      labels: []
    }
  );
  const [loadFactorChartData, setLoadFactorChartData] = useState(
    {
      datasets: [],
      labels: []
    }
  );


  useEffect(() => {
    loadGenerators();
    refreshChart(dateRange);
  }, []);


  const fields = [
  'id',
  'name',
  'MWh generated for period',
  'Capacity factor',
  'Time based availability',
  'Met mast wind speed',
  'MWh from wind turbines'
];

const getBadge = status => {
  switch (status) {
    case 'Active': return 'success'
    case 'Inactive': return 'secondary'
    case 'Pending': return 'warning'
    case 'Banned': return 'danger'
    default: return 'primary'
  }
}

const loadGenerators = () => {

  DataAPI({
    'endpoint': 'admin/locations/current',
    'method': 'GET'
  }).then(
    response => {
      
      if (response && response.error) {
        setCookie('lastTimeStamp', '');
        setCookie('name', '');
        window.location.reload();
      }
      else if (!dataLoaded && response  && !response.error) {
        let gen = [];
        if (response.generators != null) {
          response.generators.forEach(
            generator => { 
              gen.push(generator.id)
            })
          setGenerators(gen);
        }
        // fetchAlerts();
      }
    }
  );


}

const fetchData = (dateRange) => {

  setLoading(true);
  
  const body = {}
  body.location = getCookie('location')
  if (dateRange && dateRange.split('--').length==2) {
      body.from = dateRange.split('--')[0]
      body.to = dateRange.split('--')[1]
    } else {
      body.period = dateRange
    }

    DataAPI({
      'endpoint': 'chart/overview',
      'method': 'POST',
      'body': body
    }).then(
      response => {


        setLoading(false);



        if (response.error) {
           if (response.error.message) {
            return(alert(response.error.message))
           } else {
            return(alert(response.error)) 
           }
        }


        let chartData = response.data && response.data[0];

        if (chartData) {

          const timeAvailability = Math.round(chartData.timeBasedAvailability*10)/10;
          const productionAvailability = Math.round(chartData.productionBasedAvailability*10)/10;
          const loadFactor = Math.round(chartData.capacityFactor*10)/10;
          const mWh = Math.round(chartData.mWhFromWindTurbines*10)/10;
          const estimatedCurtailment = Math.round(chartData.mWhEstimatedCurtailed*10)/10;

          const loadFactorDataset = {
            clip:true,
            labels: ['Load Factor'],
            datasets: [{
              borderWidth: 0,
              data: [loadFactor,100-loadFactor],
              backgroundColor: ['#ca6510', 'rgba(0,0,0,.7)']
            }
          ]}


          const timeAvailabilityDataset = {
            clip:true,
            labels: ['Time-based Availability'],
            datasets: [{
              borderWidth: 0,
              data: [timeAvailability,100-timeAvailability],
              backgroundColor: ['#0a58ca', '#052c65']
            }
          ]}

          const productionAvailabilityDataset = {
            clip:true,
            labels: ['Production-based Availability'],
            datasets: [{
              borderWidth: 0,
              data: [productionAvailability,100-productionAvailability],
              backgroundColor: ['#0a58ca', '#052c65']
            }
          ]}

          setAverageWindSpeed(chartData.averageWindSpeed);
          setMetMastWindSpeed(chartData.metMastWindSpeed);

          setTimeAvailability(`${timeAvailability}%`)
          setProductionAvailability(`${productionAvailability}%`)
          setLoadFactor(`${loadFactor}%`)
          
          setTimeAvailabilityChartData(timeAvailabilityDataset)
          setProductionAvailabilityChartData(productionAvailabilityDataset)
          setLoadFactorChartData(loadFactorDataset)
          setMWh(mWh)
          setEstimatedCurtailment(estimatedCurtailment)

          
          
        }


          // setDateRange(`${response.chart.from.replace(" 00:00:00", "")} - ${response.chart.to.replace(" 00:00:00", "")}`);

      }
    );


}


