import React, { lazy, useEffect, useState } from 'react'

import {
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CRow,
  CSpinner,
  CWidgetDropdown,
  CCardTitle,
  CCardText
} from '@coreui/react'

import CIcon from '@coreui/icons-react'

import DataAPI from '../../../../helpers/DataAPI.js'
import {DateFilter, round, getDateLabel, getCurrentDateNDaysBefore} from '../../../../helpers/utils.js'
import {setCookie,getCookie} from '../../../../helpers/sessionCookie.js'

const Overview = () => {

  const [loading, setLoading] = useState(false);
  const [dataLoaded, setDataLoaded] = useState(false);
  const [generators, setGenerators] = useState([]);

  const [alert1, setAlert1] = useState([]);
  const [alert2, setAlert2] = useState([]);
  const [alert3, setAlert3] = useState([]);


  useEffect(() => {
    loadGenerators();
  }, []);


  const loadGenerators = () => {

    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        
        if (response && response.error) {
          setCookie('lastTimeStamp', '');
          setCookie('name', '');
          window.location.reload();
        }
        else if (!dataLoaded && response  && !response.error) {
          let gen = [];
          if (response.generators != null) {
            response.generators.forEach(
              generator => { 
                gen.push(generator.id)
              })
            setGenerators(gen);
          }
          fetchAlerts();
        }
      }
    );
  }


  const fetchAlerts = () => {
  
    const body = {}
    body.location = getCookie('location')
    body.groupBy ="day"
    body.period = "cm"
    DataAPI({
      'endpoint': 'chart/performanceIndex',
      'method': 'POST',
      'body': body
    }).then(
      response => {

        if (response.error) {
           if (response.error.message) {
            return(alert(response.error.message))
           } else {
            return(alert(response.error)) 
           }
        }

        let generatorsAlert1 = []
        response.data.forEach((rDs) => {
          if (rDs.name!='Total') {
            if (rDs.data.performance.performanceIndex < 90) {
              generatorsAlert1.push(rDs.code)
            }
          }
        });
        setAlert1(generatorsAlert1)
      }
    );

    const body2 = {}
    body2.location = getCookie('location')
    body2.groupBy ="day"
    body2.from = getCurrentDateNDaysBefore("-",2)
    body2.to = getCurrentDateNDaysBefore("-",1)
    DataAPI({
      'endpoint': 'chart/performanceIndex',
      'method': 'POST',
      'body': body2
    }).then(
      response => {

        if (response.error) {
           if (response.error.message) {
            return(alert(response.error.message))
           } else {
            return(alert(response.error)) 
           }
        }

        let generatorsAlert2 = []
        response.data.forEach((rDs) => {
          if (rDs.name!='Total') {
            const pIYesterday = rDs.data.graph[0].value
            const pIBeforeYesterday = rDs.data.graph[1].value
            if (pIBeforeYesterday - pIYesterday > 6) {
              generatorsAlert2.push(rDs.code)
            }
          }
        });
        setAlert2(generatorsAlert2)
      }
    );

    const body3 = {}
    body3.location = getCookie('location')
    body3.generators = generators
    body3.period = "y"
    DataAPI({
      'endpoint': 'chart/stopped',
      'method': 'POST',
      'body': body3
    }).then(
      response => {

        if (response.error) {
           if (response.error.message) {
            return(alert(response.error.message))
           } else {
            return(alert(response.error)) 
           }
        }
        let generatorsAlert3 = []
        response.data.forEach((rDs) => {
          if (rDs.missingHours.split(":")[0] >= 10) {
            generatorsAlert3.push(rDs.gen_code)
          }
        });
        setAlert3(generatorsAlert3)   
        
      }
    );

    const body4 = {}
    body3.location = getCookie('location')
    body3.generators = generators
    body3.period = "y"
    DataAPI({
      'endpoint': 'chart/stopped',
      'method': 'POST',
      'body': body3
    }).then(
      response => {

        if (response.error) {
           if (response.error.message) {
            return(alert(response.error.message))
           } else {
            return(alert(response.error)) 
           }
        }
        let generatorsAlert3 = []
        response.data.forEach((rDs) => {
          if (rDs.missingHours.split(":")[0] >= 10) {
            generatorsAlert3.push(rDs.gen_code)
          }
        });
        setAlert3(generatorsAlert3)   
        
      }
    );
}

  const options = {animation: {duration: loading ? 0 : 1000}};
  const bodyOpacity = 1;//loading?.5:1

  return (
    <>
      
      <CCard>
        <CCardHeader>
          <CRow>
            <CCol sm="9">
              <h3 id="traffic" className="card-title mb-0">
                Alerts
              </h3>
              {/* <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div> */}
            </CCol>
          </CRow>


        </CCardHeader>
        <CCardBody>

        <div style={{opacity:bodyOpacity}}>

          {false && <div style={{zIndex: 999, position: 'absolute', width: '100%', height: '100%', backgroundColor:'rgba(255,255,255,.7)'}}><CSpinner style={{position: 'absolute'}} /></div>}
          
          <CRow>
            <CCol sm="12" lg="6">
              <CCard
                color={"danger"}
                textColor={"white"}
              >

                <CCardBody>
                  <CCardTitle>ALERTS</CCardTitle>
                  <CCardText>
                    <p class="">Wind turbines with performance &lt; 90% (current month): 
                      <h3>{alert1.length > 0 ? alert1.join(' - ') : " - "}</h3>
                    </p>
                    <p class="">Wind turbines with negative change exceeding -6% in performance (yesterday): 
                      <h3>{alert2.length > 0 ? alert2.join(' - ') : " - "}</h3>
                    </p>
                    <p class="">Wind turbines with long stops (yesterday): 
                      <h3>{alert3.length > 0 ? alert3.join(' - ') : " - "}</h3>
                    </p>
                    <p class="">&lt; 90% data available (yesterday): 
                      <h3>{alert4.length > 0 ? alert4.join(' - ') : " - "}</h3>
                    </p>
                  </CCardText>
                </CCardBody>
              </CCard>


            </CCol>
          </CRow>

        </div>
        
        </CCardBody>

        </CCard>

    </>
  )
}

export default Overview
