import React, { useState, useEffect } from 'react'

import {
  CCard,
  CCardBody,
  CCardHeader,
  CRow,
  CCol,
  CSpinner,
  CButton,
} from '@coreui/react'

import DataAPI from '../../../helpers/DataAPI.js'
import { formatNumber, round, DateFilter } from '../../../helpers/utils.js'
import { getCookie } from 'src/helpers/sessionCookie.js'

import { Bar } from 'react-chartjs-2';


const Sales = () => {

  const [period, setPeriod] = useState('cy');
  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });

  

  useEffect(() => {
    fetchData();
  }, []);


  const fetchData = () => {

    setLoading(true)
    setDataLoaded(false)

    const body = {}
    body.location = getCookie('location');
    body.period = period
    
    DataAPI({
      'endpoint': 'chart/revenue/sales',
      'method': 'POST',
      'body': body
    }).then(
      response => {


        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

        setDataLoaded(true);
        if (response.months.length==0) return;
        const months = response.months;

        const graphData = {
          labels: months.map((x, i) => { return x.label }),
          datasets: [
            {
              label: 'Income',
              data: months.map((x, i) => { return x.dRecIncome }),
              borderColor: '#7a5195',
              backgroundColor: '#7a5195',
              type: 'line',
              yAxisID: 'yIncome',
              order: 0
            },
            {
              label: 'D-RECs sold',
              data: months.map((x, i) => { return x.dRecSold }),
              borderColor: '#bc5090',
              backgroundColor: '#bc5090',
              yAxisID: 'yProduction',
              order: 1
            },
          ],
        }

        setGraphData(graphData);

      }
    );

  }


  const options = {
    responsive: true,
    animation: {duration: loading ? 0 : 1000},
    tooltips: {
      enabled: true
    }
  };


  const optionsGraph =  {
                  ...options,
                  scales: {
                    yIncome: {
                      type: 'linear',
                      display: true,
                      position: 'right',
                      beginAtZero: false,
                    },
                    yProduction: {
                      type: 'linear',
                      display: true,
                      position: 'left',
                      grid: {
                        drawOnChartArea: false, // only want the grid lines for one axis to show up
                      },
                    },
                    
                  },
                  plugins: {
                    tooltip: {
                      mode: 'index',
                      callbacks: {
                        label: function(tooltipItem, data) {
                          if (tooltipItem.dataset.label == 'Income')
                            return tooltipItem.dataset.label + ': ' + formatNumber(round(tooltipItem.raw,1)) + ' USD';
                          else
                            return tooltipItem.dataset.label + ': ' + round(tooltipItem.raw,2) + ' MWh';
                        }
                      }
                    }
                  }
                };


  return (
      <CCard className="mb-4">

      <CCardHeader>
          <CRow>
            <CCol sm="6">
              <h3 id="sales" className="card-title mb-0">
                Sales
              </h3>
            </CCol>

            <CCol sm="6" className="text-right d-flex flex-center flex-justify-end">
              <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4}}>Period</h6>
              <DateFilter value={period} options={['cy','cy-1','cy-2','cy-3']} disabled={loading} onChange={(value) => { setPeriod(value);}} />
              <CButton color="primary" disabled={loading} className="mr-2 ml-3" onClick={() => { fetchData();}} >Submit</CButton>
            </CCol>
            
          </CRow>

        </CCardHeader>

        <CCardBody>

         { (loading || dataLoaded) &&
          <div>

            <CRow>
              <CCol>

                {!loading || dataLoadError ?
                  <div style={{marginBottom:'50px'}}>
                    <div className='d-flex'>
                      <div className="text-left" style={{width: '50%'}}>MWh</div>
                      <div className="text-right" style={{width: '50%'}}>USD</div>
                    </div>
                      <Bar 
                            data={graphData}
                            options={optionsGraph} 
                          />
                    <div className="text-center" style={{width: '100%'}}>Months</div>
                  </div>
                : 
                  <div className='text-center'>
                    <CSpinner 
                      className="loading-spinner"
                      color='#321fdb'
                    />
                  </div>
                }
                

              </CCol>
            </CRow>
          </div>
         }
          
        </CCardBody>

      </CCard>



  )
}

export default Sales
