import React, { useState, useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CButtonGroup,
  CProgress,
  CCardFooter,
  CFormSelect,
  CDataTable,
  CBadge,
  CSelect,
  CModal,
  CModalHeader,
  CModalFooter,
  CModalBody,
  CModalTitle,
  CSpinner
} from '@coreui/react'
import {
  CChartBar,
  CChartLine,
  CChartDoughnut,
  CChartRadar,
  CChartPie,
  CChartPolarArea
} from '@coreui/react-chartjs'
import { DocsLink } from 'src/reusable'

import DataAPI from '../../../helpers/DataAPI.js'
import {DateFilter, formatDate, formatDate2, round, getDateLabel, formatNumber} from '../../../helpers/utils.js'

const Budget = () => {

  const currentYear = new Date().getFullYear()

  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState();
  const [curves, setCurves] = useState([]);
  const [windSectors, setWindSectors] = useState('');
  const [fiscalYear, setFiscalYear] = useState(currentYear);
  const [addEstimatedCurtailments, setAddEstimatedCurtailments] = useState(true);
  const [parks, setParks] = useState([]);
  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });
  const [graphData2, setGraphData2] = useState({
    labels: [],
    datasets: []
  });


  



  return (
    <CCard>
      <CCardHeader>
        Budget
      </CCardHeader>
      <CCardBody>

        <CRow className={"py-3 mb-4 mx-0 bg-light"} style={{borderRadius:"3px"}}>
          <CCol xl="10" md="10" className="">

            <CRow className={"w-100"}>
              <CCol xl="3" md="6" className="d-flex flex-center px-md-1 py-2 pl-4 pr-0">
                  <h6 className="mr-2 ml-md-4 m-0 col-md-5 text-right col-4 px-0 px-sm-1" style={{minWidth:'64px'}}>Fiscal Year</h6>
                  <CSelect disabled={loading} custom name="fiscalYear" id="fiscalYear" className="col-md-7" onChange={(ev) => {setFiscalYear(ev.target.value);}}>
                      {[0,1,2,3,4].map((x) => (
                                        <option value={currentYear-x}>
                                          {currentYear-x}
                                        </option>
                                      ))}</CSelect>
              </CCol>

              

              <CCol xl="3" md="6" className="d-flex flex-center px-md-1 py-2 pl-4 pr-0">
                    <h6 className="mr-2 ml-md-4 m-0 col-md-5 text-right col-4 px-0 px-sm-1">Add Estimated Curtailments</h6>
                    <CSelect disabled={loading} custom name="fiscalYear" id="fiscalYear" className="col-md-7" onChange={(ev) => {setAddEstimatedCurtailments(ev.target.value);}}>
                      <option value={currentYear-x}>
                                          {currentYear-x}
                                        </option>
                                      ))}</CSelect>
              </CCol>

              <CCol xl="6" md="9" className="d-flex flex-center px-md-1 py-2 pl-4 pr-0">
                <h6 className="mr-2 ml-md-4 m-0 text-right col-4 col-md-3 px-0 px-sm-1">Wind sector</h6>
                  <div class="multiselect-container">
                    <Multiselect 
                      disabled={loading}
                      options={angles.map((angle) => {
                          return {id:angle,name:angle}
                        })}
                      onSelect={(selectedList) => {setWindSectors(selectedList.map((angle) => { 
                          const angleSplit = angle.id.split("-"); 
                          return {from:angleSplit[0], to:angleSplit[1]} 
                        } 
                      ))}}
                      onRemove={(selectedList) => {setWindSectors(selectedList.map((angle) =>{ 
                          const angleSplit = angle.id.split("-"); 
                          return {from:angleSplit[0], to:angleSplit[1]} 
                        } 
                      ))}}
                      displayValue="name"
                      />
                  </div>
              </CCol>
            </CRow>
          </CCol>

          <CCol xl="2" md="2" className="text-right d-flex flex-center flex-justify-end px-4 py-2">
            <CButton color="primary" className="mx-2" onClick={() => filterSubmit()} >Submit</CButton>
          </CCol>
        </CRow>











      </CCardBody>
    </CCard>
  )
}

export default Budget
