import React, { useEffect, useState } from 'react'
import DataAPI from '../../../helpers/DataAPI.js'
import {DateFilter, getDateLabel, formatDate, formatDate2, round} from '../../../helpers/utils.js'

import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol
} from '@coreui/react'

import {
  CChartLine,
  CChartPolarArea
} from '@coreui/react-chartjs'

import { DocsLink } from 'src/reusable'

const ClimateSummary = () => {

  const [dateRange, setDateRange] = useState('y');
  const [groupBy, setGroupBy] = useState('day');
  const [generators, setGenerators] = useState([]);
  const [generator, setGenerator] = useState(null);

  const [loading, setLoading] = useState(false);

  const [lineChartOneData, setLineChartOneData] = useState({
    labels: [],
    datasets: []
  });
  const [lineChartTwoData, setLineChartTwoData] = useState({
    labels: [],
    datasets: []
  });
  const [polarChartData, setPolarChartData] = useState({
    labels: [],
    datasets: []
  });


useEffect(() => {
  getChartData(dateRange);
}, []);
  
  
  var colorCounter=0;

  const colors = [
            'blue','lightblue','turquoise', 'yellow', 'orange', 'red', 
          ];

  const windRoseLabels = [
            '0-3','3-6','6-9', '9-12', '12-15', '15*', 
          ];

  const windRoseColor = (value) => {



    let n=0;

    if (value < 3) {
      n=0;
    } else if (value < 6){
      n=1;
    } else if (value < 9){
      n=2;
    } else if (value < 12){
      n=3;
    } else if (value < 15){
      n=4;
    } else {
      n=5;

    }

    console.log('windRoseColor', value, n)

    const x = colors;

    return x[n];
  }


  const windRoseRange = (value) => {

    let n=0;

    if (value < 3) {
      n=0;
    } else if (value < 6){
      n=1;
    } else if (value < 9){
      n=2;
    } else if (value < 12){
      n=3;
    } else if (value < 15){
      n=4;
    } else {
      n=5;

    }

    return n;
  }


  const getChartData = (period) => {

    setLoading(true);

    const body = {}

    if (period && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }
    body.groupBy = groupBy;
    
    DataAPI(
      {
      'endpoint': 'chart/climate',
      'method': 'POST',
      'body': body
    })
      .then(function (responseData) {

        if (responseData.error && responseData.error.message) alert(responseData.error.message)

        setLoading(false);



        const labels = Utils.months({count: 7});
        const data = {
          labels: labels,
          datasets: [
            {
              label: 'Met Mast Average',
              data: Utils.numbers(NUMBER_CFG),
              borderColor: Utils.CHART_COLORS.red,
              backgroundColor: Utils.transparentize(Utils.CHART_COLORS.red, 0.5),
            },
            {
              label: 'Turbine Average',
              data: Utils.numbers(NUMBER_CFG),
              borderColor: Utils.CHART_COLORS.blue,
              backgroundColor: Utils.transparentize(Utils.CHART_COLORS.blue, 0.5),
            }
          ]
        };

        if (responseData && responseData.graphs){



        responseData.graphs.find((item) => {
          switch (item.type) {
            case 'WindSpeed':
              item.data.forEach((rDs) => {
