import React, { useEffect, useState, useRef } from 'react'
import DataAPI from '../../../../helpers/DataAPI.js'
import {round} from '../../../../helpers/utils.js'
import {setCookie,getCookie} from '../../../../helpers/sessionCookie.js'
import moment from 'moment-timezone';

import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CSpinner,
  CLabel,
} from '@coreui/react'

import {
  Chart,
  Plugin
} from 'chart.js';

import { CChartLine } from '@coreui/react-chartjs'

import { Line } from 'react-chartjs-2'

import arrow from '../../../../assets/arrow.png'


const SolarClimateForecast = () => {

  const [dataLoaded, setDataLoaded] = useState(false)
  const [dataLoadError, setDataLoadError] = useState(false)
  const [loading, setLoading] = useState(true)
  const [arrowImage, setArrowImage] = useState(null)
  const [datetimes, _setDatetimes] = useState([])
  const datetimesRef = useRef(datetimes);
  const setDatetimes = datetimes => {
    datetimesRef.current = datetimes;
    _setDatetimes(datetimes);
  };

  const [lineChartOneData, setLineChartOneData] = useState({
    labels: [],
    datasets: []
  })

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {

    setLoading(true);

    var arrowImage = new Image(22,22);
    arrowImage.src = arrow
    arrowImage.onload = function() {
      setArrowImage(this);
    }

    DataAPI({
      'endpoint': 'solar/weather',
      'method': 'GET'
    }).then(
      response => {
        setLoading(false);

        if (response.error) {

          setCookie('lastTimeStamp', '');
          setCookie('name', '');
          window.location.reload();

          // if (response.error.message) {
          //   return(alert(response.error.message))
          // } else {
          //   return(alert(response.error)) 
          // }
        }

        const graphData = {
          labels: [],
          datasets: []
        }

        const responseData = response.data;

        graphData.labels = responseData.map( (x,i) => {
          return x.values.find(y => y.name == "TYPE_WEATHER_RADIATION_FLUX").value;
        })
  
        const datasetIrradiance = {
          label:  '',
          yAxisID: 'y',
          borderColor: '#ff764a',
          pointBackgroundColor: '#ef5675',
          pointBorderColor: 'transparent',
          backgroundColor: 'transparent',
          data: responseData.map( (x,i) => {
            return x.values.find(y => y.name == "TYPE_WEATHER_RADIATION_FLUX").value;
          })
        }

        const datasetTemperature = {
          label:  '',
          yAxisID: 'y1',
          borderColor: '#0400ff',
          pointBackgroundColor: '#0400ff',
          pointBorderColor: 'transparent',
          backgroundColor: 'transparent',
          data: responseData.map( (x,i) => {
            return x.values.find(y => y.name == "TYPE_WEATHER_RADIATION_FLUX").value;
          })
        }

        graphData.datasets.push(datasetIrradiance);
        graphData.datasets.push(datasetTemperature);
  
        const datetimes = []
        responseData.forEach( (x,i) => {
          datetimes.push(x.time);
        })
        setDatetimes(datetimes)
        
        setLineChartOneData(graphData)

        setLoading(false)

      })

  }

  const plugins = [{
    afterDraw: (chart) => {
      let ctx = chart.ctx;
      var xAxis = chart.scales['x'];
      var yAxis = chart.scales['y'];
      xAxis.ticks.forEach((value, index) => {
        
        // if (index%6==0) {
          var x = xAxis.getPixelForTick(index);
        
          const datetime = datetimesRef.current[index].split(' ')
          let date = datetime[0].split('-')
          date = date[2] + "-" + date[1]
          let time = datetime[1].split(':')
          if (time[0] == '00' || time[0] == '06' || time[0] == '12' || time[0] == '18' ) {
            time = time[0] + ":" + time[1]
            // const month = datetime.month()+1 > 9 ? datetime.month()+1 : '0'+(datetime.month()+1)
            // const day = datetime.date() > 9 ? datetime.date() : '0'+datetime.date()
            // const hour = datetime.hour() > 9 ? datetime.hour() : '0'+(datetime.hour())
            // const minute = datetime.minutes() > 9 ? datetime.minutes() : '0'+datetime.minutes()
            // const date = day+"-"+month
            // const time = hour+":"+minute
            
            ctx.font = "9px Arial";
            ctx.fillText(date, x - 12, yAxis.bottom + 16);
          ctx.fillText(time, x - 12, yAxis.bottom + 28);
          }
          
        // }
      });
      ctx.font = "12px Arial";
    }
  }]


  const options =  {
    type: 'line',
    responsive: true,
    animation: {duration: loading ? 0 : 1000},
    tooltips: {
      enabled: true
    },
    legend: {
      display: false
    },
    elements: {
      line: {
          tension: .4
      }
    },
    layout: {
        padding: {
            bottom: 80,
            left: 20,
            right: 20
        }
    },
    scales: {
      x: {
        ticks: {
            display: false,
        },
        grid: {
          display: true
        },
      },
      y: {
        min: 0,
        beginAtZero: true
      },
      y1: {
        position: 'right',
        grid: {
          drawOnChartArea: false, // only want the grid lines for one axis to show up
        },
      },
    },
    plugins: {
      tooltip: {
        callbacks: {
          title: function(tooltipItem){
            return '';
          },
          label: function(tooltipItem) {
            const datetime = datetimesRef.current[tooltipItem.dataIndex].split(' ')
            let date = datetime[0].split('-')
            date = date[2] + "-" + date[1]
            let time = datetime[1].split(':')
            time = time[0] + ":" + time[1]
            return ["Date: "+date+" "+time,"Irradiance: "+round(tooltipItem.raw,1) + ' W/m2']
          }
        }
      },
      legend: {
        display: false
      },
    }
  };


  return (

      <CCard>

      <CCardHeader>
          <CRow>
            <CCol sm="4">
              <h3 id="traffic" className="card-title mb-0">
                Climate - Forecast
              </h3>
              {/* <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div> */}
            </CCol>

            <CCol sm="8" className="text-right d-flex flex-center flex-justify-end">
              {/* <h6 className="mr-2 ml-4 m-0" style={{lineHeight:1.2}}>Period</h6>
              <DateFilter options={['y','cm','cy','x','xx']} disabled={loading} onChange={(value) => { setDateRange(value); }} /> */}
            </CCol>
          </CRow>

        </CCardHeader>

        <CCardBody>

            {!loading || dataLoadError ? 

              <div>

                <CRow className={'mb-5'}>
                  <CCol>
                    <h4 className="pb-2 mb-4 border-bottom" >Global Horizontal Irradiance</h4>
                    <div>W/m2</div>
                    <div className="text-left float-left" style={{width: '50%'}}>W/m2</div>
                    <div className="text-right float-right" style={{width: '50%'}}>Â¬C</div>
                    <Line
                      data={lineChartOneData}
                      options={options}
                      plugins={plugins}
                    />
                  </CCol>
                </CRow>


              </div>
            : 
              <div className='text-center'>
                <CSpinner 
                  className="loading-spinner"
                  color='#321fdb'
                />
              </div>
            }

        </CCardBody>
      </CCard>

  )
}

export default SolarClimateForecast
