sourcesContent:
import React, { useState, useEffect } from 'react'


import {
  CCard,
  CCardBody,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CSelect,
  CDataTable,
  CBadge,
  CSpinner,
} from '@coreui/react'

import DataAPI from '../../../../helpers/DataAPI.js'
import {formatNumber, formatDate2, round, getDateLabel, DateFilter} from '../../../../helpers/utils.js'
import {setCookie,getCookie} from '../../../../helpers/sessionCookie.js'

import { Line, Scatter, Bar } from 'react-chartjs-2';

import ChartLoading from '../../../others/ChartLoading.js'

const rand = () => {
  return 2;
}


const ctx = document.getElementById('x');

const Charts = () => {


  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });
  const [graphData2, setGraphData2] = useState({
    labels: [],
    datasets: []
  });
  const [graphData3, setGraphData3] = useState({
    labels: [],
    datasets: []
  });
  const [graphData4, setGraphData4] = useState({
    labels: [],
    datasets: []
  });

  const [tableData1, setTableData1] = useState([]);
  const [tableFields1, setTableFields1] = useState([]);
  const [tableData2, setTableData2] = useState([]);
  const [tableFields2, setTableFields2] = useState([]);
  const [tableData3, setTableData3] = useState([]);
  const [tableFields3, setTableFields3] = useState([]);
  const [tableData4, setTableData4] = useState([]);
  const [tableFields4, setTableFields4] = useState([]);
  const [tableData5, setTableData5] = useState([]);
  const [tableFields5, setTableFields5] = useState([]);
  const [tableData6, setTableData6] = useState([]);
  const [tableFields6, setTableFields6] = useState([]);
  const [generator, setGenerator] = useState('');
  const [generators, setGenerators] = useState([]);
  const [generatorsLoaded, setGeneratorsLoaded] = useState(false);
  const [dateRange, setDateRange] = useState('cm');
  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoaded2, setDataLoaded2] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [showSideTables, setShowSideTables] = useState(true);
  const [timesPitchWindspeed, setTimesPitchWindspeed] = useState([]);
  const [timesPowerRPM, setTimesPowerRPM] = useState([]);

  useEffect(() => {
    loadGenerators();
  }, []);


  const fetchData = () => {

    setLoading(true)
    setDataLoaded(false)
    setDataLoaded2(false)

    const body = {}
    body.location = getCookie('location');
    if (generator) body.generators = [generator];

    if (dateRange && dateRange.split('--').length==2) {
      body.from = dateRange.split('--')[0]
      body.to = dateRange.split('--')[1]
    } else {
      body.period = dateRange
    }
    
    DataAPI({
      'endpoint': 'chart/pitchWindspeed',
      'method': 'POST',
      'body': body
    }).then(
      response => {


        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

        const generatorData = response.generator && response.generator[0].data;

        setDataLoaded(true);

        if (generatorData.curves.length==0) return;

        const labels = [];

        const curves = generatorData.curves;

        const graphData = {
          labels: [],
          datasets: [
            {
              label:  curves[0].label,
              data: curves[0].x.map((x, i) => ({ x, y: curves[0].y[i] })),
              backgroundColor: '#374c80a3',
              yAxisID: 'yDeviationMean',
            },
            {
              label:  curves[1].label,
              data: curves[1].x.map((x, i) => ({ x, y: curves[0].y[i] })),
              backgroundColor: '#ffa600a3',
            },
          ],
          // backgroundColor: ["red", "blue", "green", "blue", "red", "blue"]
      
        }

        setGraphData(graphData);


        if (generatorData){
          if(generatorData.hasOwnProperty('dataUse')) processTableData1(generatorData.dataUse);
          if(generatorData.hasOwnProperty('dataFilteredDueTo')) processTableData2(generatorData.dataFilteredDueTo);
          // if(generatorData.hasOwnProperty('performance')) setPerformance(generatorData.performance);
        }

        const times = curves[0].date.map((t, i) =>  t );
        setTimesPitchWindspeed(times);

      }
    );


    DataAPI({
      'endpoint': 'chart/powerRpm',
      'method': 'POST',
      'body': body
    }).then(
      response => {


        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

        const generatorData = response.generator && response.generator[0].data;

        setDataLoaded2(true);

        if (generatorData.curves.length==0) return;

        const curves = generatorData.curves;

        const graphData = {
          labels: [],
          datasets: [
            {
              label:  curves[0].label,
              data: curves[0].x.map((x, i) => ({ x, y: curves[0].y[i] })),
              backgroundColor: '#bc50907a',
            }
          ],
      
        }

        setGraphData2(graphData);


        if (generatorData){
          if(generatorData.hasOwnProperty('dataUse')) processTableData3(generatorData.dataUse);
          if(generatorData.hasOwnProperty('dataFilteredDueTo')) processTableData4(generatorData.dataFilteredDueTo);
        }

        const times = curves[0].date.map((t, i) =>  t );
        setTimesPowerRPM(times);

    });


    DataAPI({
      'endpoint': 'chart/nacelleDeviation',
      'method': 'POST',
      'body': body
    }).then(
      response => {


        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

        const generatorData = response.generator && response.generator[0].data;

        setDataLoaded(true);

        if (generatorData.curves.length==0) return;

        const curves = generatorData.curves;

        const graphData = {
          labels: curves.map((x, i) => { return x.label }),
          datasets: [
            {
              label: 'Average Deviation (Degrees)',
              data: curves.map((x, i) => { return x.deviation_mean }),
              borderColor: '#ffa600',
              backgroundColor: '#ffa600',
              type: 'line',
              yAxisID: 'yDeviationMean',
              order: 0
            },
            {
              label: '% of Time',
              data: curves.map((x, i) => { return x.deviation }),
              borderColor: '#7a5195',
              backgroundColor: '#7a5195',
              yAxisID: 'yDeviation',
              order: 1
            },
          ],
        }

        setGraphData3(graphData);

        if (generatorData){
          if(generatorData.hasOwnProperty('dataUse')) processTableData5(generatorData.dataUse);
          if(generatorData.hasOwnProperty('dataFilteredDueTo')) processTableData6(generatorData.dataFilteredDueTo);
        }

      }
    );

    body.groupBy = 'hour';
    DataAPI({
      'endpoint': 'chart/windMeasurements',
      'method': 'POST',
      'body': body
    }).then(
      response => {

        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

        const generatorData = response.graphs && response.graphs[0].data;

        setDataLoaded(true);

        if (generatorData.length==0) return;

        const graphData = {
          labels: generatorData.map((x, i) => {
            return x.datetimeFrom; //generatorData.length > 48 ? x.datetimeFrom.split(' ')[0] : x.datetimeFrom;
          }),
          datasets: [
            {
              label: 'Average Wind Speed',
              data: generatorData.map((x, i) => { return x.W_avg }),
              borderColor: '#7a5195',
              pointBackgroundColor: 'transparent',
              pointBorderColor: 'transparent',
              backgroundColor: 'transparent',
            },
            {
              label: 'Wind Sensor 1',
              data: generatorData.map((x, i) => { return x.W_avg_ws1 }),
              borderColor: '#ef5675',
              pointBackgroundColor: 'transparent',
              pointBorderColor: 'transparent',
              backgroundColor: 'transparent',
            },
            {
              label: 'Wind Sensor 2',
              data: generatorData.map((x, i) => { return x.W_avg_ws2 }),
              borderColor: '#ffa600',
              pointBackgroundColor: 'transparent',
              pointBorderColor: 'transparent',
              backgroundColor: 'transparent',
            },
          ],
      
        }

        setGraphData4(graphData);

      }
    );



  }


  const loadGenerators = () => {

    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        
        if (response && response.error) {
          setCookie('lastTimeStamp', '');
          setCookie('name', '');
          window.location.reload();
        }
        else if (!dataLoaded && response  && !response.error) {

          if (response.generators != null) {
            setGenerators(response.generators);
            setGenerator(response.generators[0].id)
          }
          setGeneratorsLoaded(true)

        }
      }
    );
  }


  const processTableData1 = (dataUse) => {

    const tableData = [];

    // var usedData = (Math.round(dataUse.usedData)).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ".");
    // var filteredData = (Math.round(dataUse.filteredData)).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ".");
    // var total = (Math.round(dataUse.total)).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ".");

    tableData.push({
      '': dataUse.info,
      'Used Data': round(dataUse.usedData)+'%',
      'Filtered Data': round(dataUse.filteredData)+'%',
      'Total': round(dataUse.total)+'%'
    })

    setTableData1(tableData);
    setTableFields1(['', 'Used Data', 'Filtered Data', 'Total']);

  }

  const processTableData2 = (dataFilteredDueTo) => {

    const tableData = [];

    tableData.push({
          '': dataFilteredDueTo.info,
          'Missing Data': round(dataFilteredDueTo.missingData)+'%',
          'Alarms': round(dataFilteredDueTo.alarms)+'%',
          'Power < 0': round(dataFilteredDueTo.powerLowerThanZero)+'%',
          'Curtailments': round(dataFilteredDueTo.curtailment)+'%',
          'Total': round(dataFilteredDueTo.total)+'%',
        })

    setTableData2(tableData);
    setTableFields2(['', 'Missing Data','Alarms','Power < 0','Curtailments','Total']);
    
  }

  const processTableData3 = (dataUse) => {

    const tableData = [];

    tableData.push({
      '': dataUse.info,
      'Used Data': round(dataUse.usedData)+'%',
      'Filtered Data': round(dataUse.filteredData)+'%',
      'Total': round(dataUse.total)+'%'
    })

    setTableData3(tableData);
    setTableFields3(['', 'Used Data', 'Filtered Data', 'Total']);

  }


  const processTableData4 = (dataFilteredDueTo) => {

    const tableData = [];

    tableData.push({
          '': dataFilteredDueTo.info,
          'Missing Data': round(dataFilteredDueTo.missingData)+'%',
          'Alarms': round(dataFilteredDueTo.alarms)+'%',
          'Curtailments': round(dataFilteredDueTo.curtailment)+'%',
          'Total': round(dataFilteredDueTo.total)+'%',
        })

    setTableData4(tableData);
    setTableFields4(['', 'Missing Data','Alarms','Curtailments','Total']);
    
  }

  const processTableData5 = (dataUse) => {

    const tableData = [];

    tableData.push({
      '': dataUse.info,
      'Used Data': round(dataUse.usedData)+'%',
      'Filtered Data': round(dataUse.filteredData)+'%',
      'Total': round(dataUse.total)+'%'
    })

    setTableData5(tableData);
    setTableFields5(['', 'Used Data', 'Filtered Data', 'Total']);

  }


  const processTableData6 = (dataFilteredDueTo) => {

    const tableData = [];

    tableData.push({
          '': dataFilteredDueTo.info,
          'Missing Data': round(dataFilteredDueTo.missingData)+'%',
          'Alarms': round(dataFilteredDueTo.alarms)+'%',
          'Power < 0': round(dataFilteredDueTo.powerLowerThanZero)+'%',
          'Curtailments': round(dataFilteredDueTo.curtailment)+'%',
          'Total': round(dataFilteredDueTo.total)+'%',
        })

    setTableData6(tableData);
    setTableFields6(['', 'Missing Data','Alarms','Power < 0','Curtailments','Total']);
    
  }


  const getBadge = status => {
    switch (status) {
      case 'Active': return 'success'
      case 'Inactive': return 'secondary'
      case 'Pending': return 'warning'
      case 'Banned': return 'danger'
      default: return 'primary'
    }
  }

  const options = {
    responsive: true,
    animation: {duration: loading ? 0 : 1000},
    tooltips: {
      enabled: true
    }
  };
  const optionsScatter = {
    ...options,
    legend: {
      display: false
   },
   elements: {
    point: {
      radius: 2
    }
  },
  };

  const optionsGraph1 = {
                  ...optionsScatter,
                  scales: {
                    y:
                      {
                        min: -5,
                        max: 20,
                        stepSize: 1,
                      },
                    yMedian: {
                        type: 'linear',
                        display: true,
                        // position: 'right',
                        grid: {
                          drawOnChartArea: false, // only want the grid lines for one axis to show up
                        },
                      },
                    x:
                      {
                        min: 3,
                        max: 25,
                        stepSize: 1,
                      },
                  },
                  plugins: {
                    tooltip: {
                      callbacks: {
                        label: function(tooltipItem, data) {
                            let ret = [];
                            ret.push(formatDate2(timesPitchWindspeed[tooltipItem.dataIndex]));
                            ret.push('Wind Speed: ' + round(tooltipItem.raw.x,1) + ' m/s');
                            ret.push('Pitch: ' + round(tooltipItem.raw.y,1));
                            return ret;
                        }
                      }
                    }
                  }
                };

  const optionsGraph2 =  {
                  ...optionsScatter,
                  plugins: {
                    tooltip: {
                      callbacks: {
                        label: function(tooltipItem, data) {
                            let ret = [];
                            ret.push(formatDate2(timesPowerRPM[tooltipItem.dataIndex]));
                            ret.push('RPM: ' + formatNumber(round(tooltipItem.raw.x,1)));
                            ret.push('Power: ' + round(tooltipItem.raw.y,1) + " KWh");
                            return ret;
                        }
                      }
                    }
                  }
                };

  const optionsGraph3 =  {
                  ...options,
                  scales: {
                    yDeviationMean: {
                      type: 'linear',
                      display: true,
                      position: 'left',
                      beginAtZero: false,
                    },
                    yDeviation: {
                      type: 'linear',
                      display: true,
                      position: 'right',
                      grid: {
                        drawOnChartArea: false, // only want the grid lines for one axis to show up
                      },
                    },
                    
                  },
                  plugins: {
                    tooltip: {
                      mode: 'index',
                      callbacks: {
                        title: function(tooltipItem){
                          return 'Wind Speed: ' + tooltipItem[0].label + " m/s";
                        },
                        label: function(tooltipItem, data) {
                            return tooltipItem.dataset.label + ': ' + round(tooltipItem.raw,1) + (tooltipItem.dataset.label == '% of Time' ? ' %' : ' °');
                        }
                      }
                    }
                  }
                };

  const optionsGraph4 =  {
                  ...options,
                  elements: {
                    line: {
                        tension: .4
                    }
                  },
                  plugins: {
                    tooltip: {
                      mode: 'index',
                      callbacks: {
                        label: function(tooltipItem, data) {
                            return tooltipItem.dataset.label + ': ' + round(tooltipItem.raw,1) + " m/s";
                        }
                      }
                    }
                  }
                };



  return (
      <CCard className="mb-4">

      <CCardHeader>
          <CRow>
            <CCol sm="6">
              <h3 id="diagnostics" className="card-title mb-0">
                Diagnostics
              </h3>
              <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div>
            </CCol>

            <CCol sm="6" className="text-right d-flex flex-center flex-justify-end">
                <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4,minWidth:'64px'}}>Turbine</h6>
                  <CSelect className={'input-md'} disabled={loading} onChange={(ev) => {setGenerator(ev.target.value);}} custom name="generator" id="generator">
                      {generators.map((oGenerator) => (
                          <option value={oGenerator.id}>
                            {oGenerator.code}
                          </option>
                        ))}</CSelect>
                <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4}}>Period</h6>
                <DateFilter value={dateRange} options={['y','cm','cy','x','xx']} disabled={loading} onChange={(value) => { setDateRange(value);}} />
                <CButton color="primary" disabled={loading || !generatorsLoaded} className="mr-2 ml-3" onClick={() => { fetchData({generator: generator, period: dateRange});}} >Submit</CButton>
            </CCol>
            
          </CRow>

        </CCardHeader>

        <CCardBody>

         { (loading || dataLoaded) &&
          <div>     
            <CRow>
              <CCol className={'text-right'}>
                <CButton color="primary" style={{marginBottom:'-14px',marginTop:'-6px'}} onClick={() => setShowSideTables(!showSideTables)}>
                    { showSideTables ? 'Hide side tables' : 'Show side tables' }
                </CButton>
              </CCol>
            </CRow>
                    

            <CRow className="mb-4">
            <CCol sm={showSideTables?"7":"12"}>

                <h5 className="text-center card-title mb-2">Wind Speed vs Blade Pitch</h5>
                {!loading || dataLoadError ?
                  <div style={{marginBottom:'50px'}}>
                    <div className="text-left" style={{width: '100%'}}>Turbine Average Blade Pitch</div>
                      <Scatter data={graphData} options={optionsGraph1} />
                    <div className="text-center" style={{width: '100%'}}>Turbine Average Wind Speed (m/s)</div>
                  </div>
                : 
                  <div className='text-center'>
                    <CSpinner 
                      className="loading-spinner"
                      color='#321fdb'
                    />
                  </div>
                }

              </CCol>

              <CCol sm="5" hidden={!showSideTables}>

                <h4 >Data Use</h4>
                {/* <CButton onClick={()=>{setDataUseShowing(!dataUseShowing)}}>Hide</CButton> */}
                <div class="mb-4" style={{textAlign:'right'}}>
                    {dataLoaded || dataLoadError
                      ? <CDataTable
                      items={tableData1}
                      fields={tableFields1}
                      striped
                      itemsPerPage={5}
                      pagination
                      scopedSlots = {{
                        'status':
                          (item)=>(
                            <td>
                              <CBadge color={getBadge(item.status)}>
                                {item.status}
                              </CBadge>
                            </td>
                          )

                      }}
                    />
                      : 
                      <ChartLoading/>
                  
                }
                </div>

                <h4>Reasons for Filtered Data</h4>
                <div style={{textAlign:'right'}}>
                  {dataLoaded || dataLoadError
                      ? 
                      <CDataTable
                      items={tableData2}
                      fields={tableFields2}
                      striped
                      itemsPerPage={5}
                      pagination
                      scopedSlots = {{
                        'status':
                          (item)=>(
                            <td>
                              <CBadge color={getBadge(item.status)}>
                                {item.status}
                              </CBadge>
                            </td>
                          )

                      }}
                    />
                    : 
                    <ChartLoading/>
                } 
                </div>


              </CCol>
            </CRow>


            <CRow>
              <CCol sm={showSideTables?"7":"12"}>

                <h5 className="text-center card-title mb-2"> Generator RPM vs. Power</h5>
                {!loading || dataLoadError ?
                  <div style={{marginBottom:'50px'}}>
                    <div className="left-center" style={{width: '100%'}}>Power (KWh)</div>
                      <Scatter data={graphData2} options={optionsGraph2} />
                    <div className="text-center" style={{width: '100%'}}>Turbine Generator Average RPM</div>
                  </div>
                : 
                  <div className='text-center'>
                    <CSpinner 
                      className="loading-spinner"
                      color='#321fdb'
                    />
                  </div>
                }

              </CCol>
              <CCol sm="5" hidden={!showSideTables}>

                <h4>Data Use</h4>
                <div class="mb-4" style={{textAlign:'right'}}>
                    {dataLoaded || dataLoadError
                      ? <CDataTable
                      items={tableData3}
                      fields={tableFields3}
                      striped
                      itemsPerPage={5}
                      pagination
                      scopedSlots = {{
                        'status':
                          (item)=>(
                            <td>
                              <CBadge color={getBadge(item.status)}>
                                {item.status}
                              </CBadge>
                            </td>
                          )

                      }}
                    />
                      : 
                      <ChartLoading/>
                  
                }
                </div>

                <h4>Reasons for Filtered Data</h4>
                <div style={{textAlign:'right'}}>
                  {dataLoaded || dataLoadError
                      ? 
                      <CDataTable
                      items={tableData4}
                      fields={tableFields4}
                      striped
                      itemsPerPage={5}
                      pagination
                      scopedSlots = {{
                        'status':
                          (item)=>(
                            <td>
                              <CBadge color={getBadge(item.status)}>
                                {item.status}
                              </CBadge>
                            </td>
                          )

                      }}
                    />
                    : 
                    <ChartLoading/>
                } 
                </div>


              </CCol>
            </CRow>

            <CRow>
              <CCol sm={showSideTables?"7":"12"}>

                <h5 className="text-center card-title mb-2">Average Wind and Nacelle Deviation</h5>
                {!loading || dataLoadError ?
                  <div style={{marginBottom:'50px'}}>
                    <div className='d-flex'>
                      <div className="text-left" style={{width: '50%'}}>Average Deviation (Degrees)</div>
                      <div className="text-right" style={{width: '50%'}}>% of Time</div>
                    </div>
                      <Bar 
                            data={graphData3}
                            options={optionsGraph3} 
                          />
                    <div className="text-center" style={{width: '100%'}}>Average Wind Speed (m/s)</div>
                  </div>
                : 
                  <div className='text-center'>
                    <CSpinner 
                      className="loading-spinner"
                      color='#321fdb'
                    />
                  </div>
                }
                

              </CCol>
              <CCol sm="5" hidden={!showSideTables}>

                <h4>Data Use</h4>
                <div class="mb-4" style={{textAlign:'right'}}>
                    {dataLoaded || dataLoadError
                      ? <CDataTable
                      items={tableData5}
                      fields={tableFields5}
                      striped
                      itemsPerPage={5}
                      pagination
                      scopedSlots = {{
                        'status':
                          (item)=>(
                            <td>
                              <CBadge color={getBadge(item.status)}>
                                {item.status}
                              </CBadge>
                            </td>
                          )

                      }}
                    />
                      : 
                      <ChartLoading/>
                  
                }
                </div>

                <h4>Reasons for Filtered Data</h4>
                <div style={{textAlign:'right'}}>
                  {dataLoaded || dataLoadError
                      ? 
                      <CDataTable
                      items={tableData6}
                      fields={tableFields6}
                      striped
                      itemsPerPage={5}
                      pagination
                      scopedSlots = {{
                        'status':
                          (item)=>(
                            <td>
                              <CBadge color={getBadge(item.status)}>
                                {item.status}
                              </CBadge>
                            </td>
                          )

                      }}
                    />
                    : 
                    <ChartLoading/>
                } 
                </div>


              </CCol>
            </CRow>

            <CRow>
              <CCol sm={"12"}>

                <h5 className="text-center card-title mb-2">Wind measurements</h5>
                {!loading || dataLoadError ?
                  <div style={{marginBottom:'50px'}}>
                    <div className="text-left" style={{width: '100%'}}>Wind Speed (m/s)</div>
                    <Line 
                        data={graphData4}
                        options={optionsGraph4} 
                      />
                  </div>
                : 
                  <div className='text-center'>
                    <CSpinner 
                      className="loading-spinner"
                      color='#321fdb'
                    />
                  </div>
                }

              </CCol>
            </CRow>
          </div>
         }
          
        </CCardBody>

      </CCard>



  )
}

export default Charts
