    key: "tag",
    label: ""
  }
  ];


const Alarms = () => {

  const [period, setPeriod] = useState('cm');
  const [groupBy, setGroupBy] = useState('day');
  const [generators, setGenerators] = useState([]);
  const [generator, setGenerator] = useState(null);
  const [generatorsLoaded, setGeneratorsLoaded] = useState(false);

  const [dataLoaded, setDataLoaded] = useState(false);

  const [loading, setLoading] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);

  const [tableData, setTableData] = useState([]);
  const [scopedSlots, setScopedSlots] = useState([]);
  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });
  const [graphData2, setGraphData2] = useState({
    labels: [],
    datasets: []
  });
  const [fieldsAlarmsByGenerator, setFieldsAlarmsByGenerator] = useState(null);
  
  const colors =  ['#003f5c', '#7a5195', '#bc5090', '#ef5675', '#ff764a', '#ffa600','#9ceb01']

  
  const fetchData = () => {

    const body = {}
    body.location = getCookie('location');
    if (generator && generator !== '0') body.generators = [generator];
    
    if (period && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }
    body.groupBy = groupBy

    setLoading(true);



    DataAPI(
      {
      'endpoint': 'chart/alarms_v2',
      'body': body
    }).then(function (responseData) {

      setLoading(false);
      setDataLoaded(true);
      if (responseData.error) {
        setDataLoadError(true);
          if (responseData.error.message) {
          return(alert(responseData.error.message))
          } else {
          return(alert(responseData.error)) 
          }
      }

      const data = {
        labels: [],
        datasets: []
      }

      const data2 = {
        labels: [],
        datasets: []
      }

      responseData.data.forEach((rD,index) => {
        const rDfrom = rD.from.split(' ')[0]//formatDate(rD.from);
        const rDto = rD.to.split(' ')[0]//formatDate(rD.to);
        const label = (groupBy == "day") ? `${rDfrom}` : `${rDfrom} - ${rDto}`
        data.labels.push(label);
        data2.labels.push(label);

      });

      const alarmKeys = []
      const alarmDescriptions = []
      
      responseData.data.forEach((dK,index) => {
        dK.alarms.forEach((a,index2) => {
          if (!alarmKeys.includes(a.code)) {
            alarmKeys.push(a.code)
            alarmDescriptions[a.code] = a.description
          }
        })
      })

      alarmKeys.sort(function (a, b) {
        return a.localeCompare(b); 
      });

      alarmKeys.forEach((dK,index) => {
        let color = colors[index%colors.length];
        if (dK == 'Missing') color = "#d9d9d9"
        else if (dK == 'Other') color = "#7c7c7c"
        if (dK != 'Ok') {
          const dataset = {
            label:  ((dK != 'Missing' && dK != 'Other') ? round(dK,0) + " - " : "" ) + alarmDescriptions[dK],
            backgroundColor: color,
            data: []
          };
          responseData.data.forEach((rD,index2) => {
            let hours = 0
            rD.alarms.forEach((rA,index3) => {
              if (dK == rA.code)
                hours = rA.hours
            })
            dataset.data.push(hours)

          });
          data.datasets.push(dataset);
        }
      });

      setGraphData(data);



      generators.forEach((gen,index) => {

        const dataset2 = {
          label:  gen.code,
          backgroundColor: colors[index%colors.length],
          data: []
        };

        responseData.data.forEach((rD,index2) => {
          let hours = 0
          rD.alarms.forEach((rA,index3) => {
            if (rA.code != "Missing" && rA.code != "Ok")
            rA.generators.forEach((rG,index4) => {
              if (rG.id == gen.id)
                hours += rG.hours
            });
          });
          dataset2.data.push(hours);
        });

        data2.datasets.push(dataset2);

      });

      setGraphData2(data2);




      const tableData = [];

      const fieldsAlarmsByGenerator = [{
        key: "alarm",
        label: "Alarm Code"
      },
      {
        key: "generator",
        label: "Turbine"
      },
      {
        key: "tag",
        label: "Events and Duration (hours)",
        _style: { minWidth: '100px'}
      }]
      responseData.data.forEach((rD,index) => {
        const rDfrom = rD.from.split(' ')[0]//formatDate(rD.from);
        const rDto = rD.to.split(' ')[0]//formatDate(rD.to);
        const label = (groupBy == "day") ? `${rDfrom}` : `${rDfrom} - ${rDto}`
        fieldsAlarmsByGenerator.push(
          {
            key: "period-"+index,
            label: label
          }
        )

      })

      const scopedSlots = []
      scopedSlots["alarm"] = (item)=>(
        <td>
          <div style={{fontWeight: 'bold'}}>
            {item.alarm}
          </div>
        </td>
      )
      scopedSlots["generator"] = (item)=>(
        <td>
          <div style={{fontWeight: 'bold'}}>
            {item.generator}
          </div>
        </td>
      )

      alarmKeys.forEach((alarm,index) => {
        let alarmFirstLine = true
        generators.forEach((gen,index2) => {
          const rowEvents = {
            alarm: '',
            generator: gen.code,
            tag: "Events",
          }
          const rowDuration = {
            alarm: '',
            generator: '',
            tag: "Duration",
          }
          let hasAlarm = false
          
          responseData.data.forEach((rD,index3) => {
            let occurrences = 0
            let hours = 0
            rD.alarms.forEach((rA,index4) => {
              if (rA.code != "Missing" && rA.code != "Ok") {
                rA.generators.forEach((rG,index5) => {
                  if (rA.code == alarm && rG.id == gen.id) {
                    occurrences = rG.occurrences
                    hours = round(rG.hours,1)
                    hasAlarm = true
                    scopedSlots["period-"+index3] = (item)=>(
                      <td>
                        <div className={""} style={
                          item["period-"+index3] >= 20 ?
                            {color: "red"}
                          :
                            item["period-"+index3] >= 10 ?
                              {color: "orange"}
                            :
                              item["period-"+index3] >= 5 ?
                                {color: "yellow"}
                              :
                                {color: "black"}
                          }>
                          {item["period-"+index3]}
                        </div>
                      </td>
                    )
                  }
                });
              }
            })
            
            rowEvents["period-"+index3] = occurrences
            rowDuration["period-"+index3] = hours
           
          })
          if(hasAlarm) {
            if (alarmFirstLine) {
              rowEvents['alarm'] = alarm
              alarmFirstLine = false
            }
            tableData.push(rowEvents)
            tableData.push(rowDuration)
          }
        })
      })

      setFieldsAlarmsByGenerator(fieldsAlarmsByGenerator)
      setTableData(tableData)

      setScopedSlots(scopedSlots)



    }).catch(
      e => {
        setLoading(false);
        alert(e);
      }
    );






    // DataAPI(
    //   {
    //   'endpoint': 'chart/alarms',
    //   'body': body
    // }).then(function (responseData) {

    //   setLoading(false);
