  CSpinner,
  CLabel,
} from '@coreui/react'

import { Line } from 'react-chartjs-2'

import { OPEN_WEATHER_API } from 'constants'


const ClimateForecast = () => {

  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [loading, setLoading] = useState(false);

  const [lineChartOneData, setLineChartOneData] = useState({
    labels: [],
    datasets: []
  });
  const [lineChartTwoData, setLineChartTwoData] = useState({
    labels: [],
    datasets: []
  });
  const [lineChartThreeData, setLineChartThreeData] = useState({
    labels: [],
    datasets: []
  });
  const [lineChartFourData, setLineChartFourData] = useState({
    labels: [],
    datasets: []
  });

  const colors =  ['#003f5c', '#7a5195', '#bc5090', '#ef5675', '#ff764a', '#ffa600','#9ceb01']


  useEffect(() => {
    loadGenerators();
  }, []);
  

  const fetchData = (period) => {

    setLoading(true);

    DataAPI(
      {
      'endpoint': OPEN_WEATHER_API,
      'method': 'POST',
      'body': body
    })
      .then(function (responseData) {

        if (responseData.error && responseData.error.message) {
          setDataLoadError(true)
          alert(responseData.error.message)
        } 

        setLoading(false);

        if (responseData && responseData.graphs) {

          const labels = responseData.graphs[0].data.map( (x,i) => {
            return x.datetimeFrom;
          })
          const graphData1 = {
            labels: labels,
            datasets: []
          }
          

          responseData.graphs[0].gen_code.forEach((gen,index) => {
            const dataset1 = {
              label:  gen,
              borderColor: generatorColors[gen],
              pointBackgroundColor: 'transparent',
              pointBorderColor: 'transparent',
              backgroundColor: 'transparent',
              data: responseData.graphs[0].data.map( (x,i) => {
                return x.genWindSpeed[index];
              })
            }
          
            graphData1.datasets.push(dataset1)
          });

          setLineChartOneData(graphData1)

        }
      });

  }

  const loadGenerators = () => {

    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        
        if (response && response.error) {
          setCookie('lastTimeStamp', '');
          setCookie('name', '');
          window.location.reload();
        }
        else if (!dataLoaded && response  && !response.error) {

          if (response.generators != null) {
            setGenerators(response.generators);
            // setSelectedGenerators(response.generators.map((gen) => (gen.id)));
            let colorIndex = 0
            response.generators.forEach((gen) => {
              generatorColors[gen.code] = colors[colorIndex%colors.length];
              setGeneratorColors(generatorColors);
              colorIndex++
            });
          }

          setGeneratorsLoaded(true);

        }
      }
    );

  }

  const selectGenerator = (id) => {

    let newSelected = selectedGenerators;
    newSelected = newSelected.includes(id)
                          ? newSelected.filter(i => i !== id) // remove item
                          : [ ...newSelected, id ]; // add item
    setSelectedGenerators(newSelected.sort());
  }

  const filterGenerators = () => {

    setDataLoaded(false);
    setGeneratorsSelected(false);
    setFilterSubmitted(true);
    if (selectedGenerators.length > 0) {
      setLoading(true);
      setGeneratorsSelected(true);
      fetchData(dateRange);
    }

  }


  const options =  {
    responsive: true,
    animation: {duration: loading ? 0 : 1000},
    tooltips: {
      enabled: true
    },
    elements: {
      line: {
          tension: .4
      }
    },
  }

  const options1 = {
    ...options,
    plugins: {
      tooltip: {
        callbacks: {
          label: function(tooltipItem, data) {
            return tooltipItem.dataset.label + ": " + round(tooltipItem.raw,1) + ' m/s';
          }
        }
      }
    }
  };

  const options2 =  {
    ...options,
    stacked: false,
    scales: {
      yTemperature: {
        type: 'linear',
        display: true,
        position: 'left',
      },
      yAirDensity: {
        type: 'linear',
        display: true,
        position: 'right',
        grid: {
          drawOnChartArea: false, // only want the grid lines for one axis to show up
        },
      },
    },
    plugins: {
      tooltip: {
        callbacks: {
          label: function(tooltipItem, data) {
            let unit = tooltipItem.dataset.yAxisID === "yAirDensity" ? ' kg/m3' : ' 째C'
            let decimals = tooltipItem.dataset.yAxisID === "yAirDensity" ? 2 : 1
            let label = tooltipItem.dataset.yAxisID === "yAirDensity" ? 'Air Density: ' : 'Temperature: '
            return label + round(tooltipItem.raw,decimals) + unit;
          }
        }
      }
    },
  };

  const options3 = {
    ...options,
    plugins: {
      tooltip: {
        callbacks: {
          label: function(tooltipItem, data) {
            return tooltipItem.dataset.label + ": " + round(tooltipItem.raw,1) + '째';
          }
        }
      }
    },
    scales: {
      y: {
          max: 360,
          ticks: {
              stepSize: 45,
          }
      }
    }
  };

  const options4 = {
    ...options,
    plugins: {
      tooltip: {
        callbacks: {
          label: function(tooltipItem, data) {
            return tooltipItem.dataset.label + ": " + round(tooltipItem.raw,1) + ' %';
          }
        }
      }
    },
  };


  return (

      <CCard>

      <CCardHeader>
          <CRow>
            <CCol sm="4">
              <h3 id="traffic" className="card-title mb-0">
                Climate - Trends
              </h3>
              <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div>
            </CCol>

            <CCol sm="8" className="text-right d-flex flex-center flex-justify-end">
              <h6 className="mr-2 ml-4 m-0" style={{lineHeight:1.2}}>Period</h6>
              <DateFilter options={['y','cm','cy','x','xx']} disabled={loading} onChange={(value) => { setDateRange(value); }} />
            </CCol>
          </CRow>

        </CCardHeader>

        <CCardBody>

          <CRow className={"py-3 mb-4 mx-0 bg-light"} style={{borderRadius:"3px"}}>
            <CCol sm="10" className={"d-flex "} >
              <h6 className="mx-2 my-2 pt-1" style={{lineHeight:1.2,minWidth:'110px'}}>Select turbines:</h6>   
              {generatorsLoaded ? 
                <div>
                  <CButton 
                    style={{backgroundColor:'#f32e2e',color: 'white'}} 
                    className={(metMastSelected ? "selected" : "") + " btn-generator mx-1 my-1"}
                    onClick={() => setMetMastSelected(!metMastSelected)}
                    >
                    Met
                  </CButton>
                  <CButton 
                    style={{backgroundColor:'#0400ff',color: 'white'}} 
                    className={(allSelected ? "selected" : "") + " btn-generator mx-1 my-1"}
                    onClick={() => setAllSelected(!allSelected)}
                    >
                    ALL
                  </CButton>
                  { generators.map((gen, index) => (  
                    <CButton 
                      style={{backgroundColor:generatorColors[gen.code],color: 'white'}} 
                      className={(selectedGenerators.includes(gen.id) ? "selected" : "") + " btn-generator mx-1 my-1"}
                      onClick={() => selectGenerator(gen.id)} 
                      id={gen.id}
                      >
                        {gen.code}
                    </CButton>
                  )) }
                </div>
                :
                <CSpinner 
                  size="sm"
                  className="loading-spinner"
                  color='#321fdb'
                />
              }
            </CCol>
            <CCol sm="2" className="text-right d-flex flex-end flex-justify-end ">
              <CButton color="primary" className="mx-2 mb-1" onClick={() => filterGenerators()} >Submit</CButton>
            </CCol>
        </CRow>

        { generatorsSelected ?

          <div>

            {!loading || dataLoadError ? 

              <div>

                <CRow className={'mb-5'}>
                  <CCol>
                    <h4 className="pb-2 mb-4 border-bottom" >Wind Speed</h4>
                    <div>m/s</div>
                    <Line
                      data={lineChartOneData}
                      options={options1}
                    />
                  </CCol>
                </CRow>

                <CRow className={'mb-5'}>
                  <CCol>
                    <h4 className="pb-2 mb-4 border-bottom">Temperature and Air Density</h4>
                    <div className='d-flex'>
                      <div className="text-left" style={{width: '50%'}}>째C</div>
                      <div className="text-right" style={{width: '50%'}}>kg/m3</div>
                    </div>
                    <Line 
                      data={lineChartTwoData}
                      options={options2} />
                  </CCol>
                </CRow>

                <CRow className={'mb-3'}>
                  <CCol xs="12">
                    <h4 className="pb-2 mb-4 border-bottom" >Wind Direction</h4>
                    <div>Degrees</div>
                    <Line
                      data={lineChartThreeData}
                      options={options3}
                    />
                  </CCol>
                  <CCol xs="12" className={'mt-4'}>
                    <CCard className={'pl-3 pr-2'} style={{maxWidth: '480px', marginLeft: 'auto', marginRight: 'auto'}}>
                      <CCardBody>
                        <CRow>
                          <CCol sm="6" xs="12">
                            <CLabel className={'w-100 mb-0'}>0-45 North to Northeast</CLabel>
                            <CLabel className={'w-100 mb-0'}>45-90 NorthEast to East</CLabel>
                            <CLabel className={'w-100 mb-0'}> 90-135 East to SouthEast</CLabel>
                            <CLabel className={'w-100 mb-0'}>135-180 SouthEast to South</CLabel>
                          </CCol>
                          <CCol sm="6" xs="12">
                            <CLabel className={'w-100 mb-0'}> 180-225 South to SouthWest</CLabel>
                            <CLabel className={'w-100 mb-0'}>225-270 SouthWest to West</CLabel>
                            <CLabel className={'w-100 mb-0'}> 270-315 West to NorthWest</CLabel>
                            <CLabel className={'w-100 mb-0'}> 315-360 NorthWest to North</CLabel>
                          </CCol>
                        </CRow>
                      </CCardBody>
                    </CCard>
                    
                  </CCol>
                </CRow>

                <CRow className={'mb-5'}>
                  <CCol>
                    <h4 className="pb-2 mb-4 border-bottom" >Turbulence Intensity</h4>
                    <div>%</div>
                    <Line
                      data={lineChartFourData}
                      options={options4}
                    />
                  </CCol>
                </CRow>

              </div>
            : 
              <div className='text-center'>
                <CSpinner 
                  className="loading-spinner"
                  color='#321fdb'
                />
              </div>
            }

          </div>

        :

        <div>
          { filterSubmitted &&
            <CRow>
              <CCol className="text-center">
                Select one or more generators
              </CCol>
            </CRow>
          }
        </div>

}
        </CCardBody>
      </CCard>

  )
}

export default ClimateForecast
