import React, { useState, useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CButtonGroup,
  CProgress,
  CCardFooter,
  CFormSelect,
  CSelect,
  CInput,
  CDataTable,
  CLink
} from '@coreui/react'

import { Bar, Chart, Line, Scatter } from 'react-chartjs-2';

import DataAPI from '../../../helpers/DataAPI.js'

import {formatDate, round, getDateLabel, DateFilter} from '../../../helpers/utils.js'

import {
  CChartBar,
  CChartLine,
  CChartDoughnut,
  CChartRadar,
  CChartPie,
  CChartPolarArea
} from '@coreui/react-chartjs'

import { DocsLink } from 'src/reusable'

const Charts = () => {

  const [tableData, setTableData] = useState([]);
  const [term, setTerm] = useState([]);
  const [generator, setGenerator] = useState();
  const [generators, setGenerators] = useState([]);
  const [generatorsLoaded, setGeneratorsLoaded] = useState(false);
  const [park, setPark] = useState();
  const [date, setDate] = useState();
  const [dateRange, setDateRange] = useState('cm');
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);

  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });


   useEffect(() => {

      fetchGenerators();
      
      refreshChart();

  }, []);


  const refreshChart = () => {
    fetchData();
  }

  const fetchGenerators = () => {

    DataAPI({
      'endpoint': 'admin/generators/current/location',
      'method': 'GET'
    }).then(
      response => {
        
        if (!generatorsLoaded) {
          setGenerators(response);
          setGeneratorsLoaded(true);
        }

      }
    );

  }

  const fetchData = () => {

    setLoading(true);

    let endpoint;

    if (generator && generator !== '0') {
      endpoint = `admin/documents/generator/${generator}?term=${term}&period=${dateRange}&size=-1`;
    } else {
      endpoint = `admin/documents?term=${term}&period=${dateRange}&size=-1`;
    }

    DataAPI({
      endpoint: endpoint,
      method: 'GET'
    })
      .then(function (responseData) {

        const tableData = [];

        if (!responseData) return;
        if (responseData.error) return(alert(`Error! ${responseData.error}`));



        responseData.forEach(
          row => {

            console.log('row!', row);

            tableData.push({
              serviceOrder: '#'+row.id,
              turbineId: generator && generator!='0' ? generator : row.generators.map(generator => generator.code).join(', '),
              startDateEndDate: formatDate(row.from)+' - '+formatDate(row.to),
              issue: row.name,
              download: 'http://p2fr.redirectme.net:8080'+row.downloadLink,
            });

          }
        );

        setTableData(tableData);

  })


    const APIPayload = {
        'period' : dateRange,
      }

    if (generator) APIPayload.generators = [generator]

    DataAPI({
      endpoint: 'chart/performanceIndex',
      method: 'POST',
      body: APIPayload,
    })
      .then(function (responseData) {

        console.log('responseData PI >', responseData)

        setLoading(false);

        const tableData = [];

        responseData.data.forEach(
          generatorData => {

            tableData.push({
              turbineId: generatorData.name
            });

          }
        );

        // setTableData(tableData);

        const data = {
          labels: [],
          datasets: [],
          backgroundColor: ["red", "blue", "green", "blue", "red", "blue"]

        }

        const colors = [
            '#003f5c',
            '#374c80',
            '#7a5195',
            '#bc5090',
            '#ef5675',
            '#ff764a',
            '#ffa600'
          ];


          const dataset100 = {
            labels: [''],
            label: '',
            backgroundColor: colors.pop(),
            data: []
          };

        responseData.data.forEach((rDs) => {

          const dataset = {
            labels: [],
            label: rDs.name,
            backgroundColor: colors.pop(),
            data: [],
            hidden: rDs.name!='Total'
          };

          rDs.data.graph.forEach((rD) => {
            // const dsData = rD.dataSet.find((dS) => dS.id === rDs.id);
            dataset.labels.push(`${formatDate(rD.from)} - ${formatDate(rD.to)}`);
            dataset.data.push(rD.value)

            dataset100.data.push(100)

          });

          dataset.labels.reverse();
          dataset.data.reverse();

          data.datasets.push(dataset);
          // data.datasets.push(dataset100);

        });

        responseData.data[0].data.graph.forEach((rD) => {
          const rDfrom = rD.from;
          const rDto = rD.to;
          data.labels.push(`${formatDate(rD.from, 'noyear')} - ${formatDate(rD.to, 'noyear')}`);

        });

        data.labels.reverse();

        setGraphData(data)

      })
      .catch(
        err => console.warn(`ERROR! ${err}`)
        // err => alert(`ERROR! ${err}`)
      );

  }


  useEffect(() => {


    fetchData();


  }, []);



  const fields = [
    'turbineId',
    'serviceOrder',
    'startDateEndDate',
    'issue',
    { label: 'Open Report', key: 'download' },


  ];


  return (
    <CCard>
      <CCardHeader>
          <CRow>
            <CCol sm="4">
              <h4 id="traffic" className="card-title mb-0">
                Maintenance History
              </h4>
              <br />
              </CCol>

              </CRow>
              <CRow>

              <CCol sm="3" class="text-right">

            <DateFilter options={'y,cm,cy,12w,12m,x'.split(',')} value={dateRange} onChange={(value) => { setDateRange(value);}} />

                            </CCol>

              <CCol sm="3" class="text-right">
            <CSelect onChange={(ev) => {setGenerator(ev.target.value); }} custom name="ccmonth" id="ccmonth">

            <option value="0">All generators</option>

            {generators.map((oGenerator) => (
                                <option value={oGenerator.id}>
                                  {oGenerator.id}
                                </option>
                              ))}</CSelect>
            </CCol>

               

            
            <CCol sm="3" class="text-right">
              <CInput type="text" placeholder="Search term" autoComplete="username" onChange={(ev) => { setTerm(ev.target.value);}} />
            </CCol>

            <CCol sm="3" class="text-right">
              <CButton disabled={loading}  onClick={ () => { refreshChart(); } } color="primary" className="px-4">Search</CButton>
            </CCol>
                            </CRow>


        </CCardHeader>
      <CCardBody>
        <CDataTable
              items={tableData}
              fields={fields}
              striped
              itemsPerPage={50}
              pagination
              scopedSlots = {{
                'download':
                  (item, index)=>{

                    return (
                      <td className="py-2">
                        <a href={item.download}>Click Here</a>
                      </td>
                      )
                  }
              }}
            />

        <Line data={graphData} />

      </CCardBody>
    </CCard>
  )
}

export default Charts
