import React, { useState, useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardHeader,
  CDataTable,
  CRow,
  CCol,
  CSpinner,
  CBadge
} from '@coreui/react'

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

import { freeSet } from '@coreui/icons'
import DataAPI from '../../helpers/DataAPI.js'
import { formatDate2, round, formatNumber } from '../../helpers/utils.js'
import CIcon from '@coreui/icons-react';
import {setCookie} from '../../helpers/sessionCookie.js'

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = () => {

  // const [dateRange, setDateRange] = useState('cm');
  // const [groupBy, setGroupBy] = useState('day');
  // const [generators, setGenerators] = useState([]);
  // const [generator, setGenerator] = useState(null);
  const [lastUpdated, setLastUpdated] = useState('');

  // const [dataLoaded, setDataLoaded] = useState(false);

  const [loading, setLoading] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);

  const [turbinesTablesData, setTurbinesTablesData] = useState([]);
  const [tableDataCurrentStatus, setTableDataCurrentStatus] = useState(false);

  const fetchData = () => {

    const body = {}
    setLoading(true);

    DataAPI(
      {
      'endpoint': 'chart/dashboard',
      'body': body
    }).then(function (responseData) {

      setLoading(false);
      if (responseData.error) {
        setDataLoadError(true);
          if (responseData.error.message) {
          return(alert(responseData.error.message))
          } else {
          return(alert(responseData.error)) 
          }
      }

      if (responseData.dateData != undefined)
        setLastUpdated(formatDate2(responseData.dateData));

      const tableDataCurrentStatus = {
        items: [{
                  item:"Production:",
                  now:formatNumber(round(responseData.dataNow.production,1))+" MWh",
                  today:formatNumber(round(responseData.dataToday.production,1))+" MWh"
                },
                {
                  item:"Turbine average wind speed:",
                  now:round(responseData.dataNow.windSpeed,1)+" m/s",
                  today:round(responseData.dataToday.windSpeed,1)+" m/s"
                },
                {
                  item:"Turbines in operation:",
                  now:responseData.amountGeneratorsOk+"/"+responseData.amountGenerators,
                  today:""
                },
              ],
        fields:  [
          { key: 'item', label: '' },
          'now',
          'today'
        ]
      }
      setTableDataCurrentStatus(tableDataCurrentStatus)

      const turbinesTablesData = [];
      // let amountGeneratorsOk = 0;
      responseData.locations.forEach((rL,index) => {
        if(rL.generators.length > 0) {
          const park = rL.name;
          // let productionTotal = 0;
          // let windSpeedTotal = 0;
          const turbinesStatus = rL.generators.map((d) => {
            let line = {}
            line.turbine = d.code;
            line.indicator = d.dataNow.indicator;
            line.production = (d.dataNow.production!=undefined?formatNumber(round(d.dataNow.production,3)):"");
            line.windSpeed = (d.dataNow.windSpeed!=undefined?formatNumber(round(d.dataNow.windSpeed,1)):"");
            line.status = d.dataNow.status;
            // productionTotal += (d.dataNow.production!=undefined?d.dataNow.production:0);
            if (line.windSpeed!=undefined) {
              // windSpeedTotal +=(d.dataNow.windSpeed!=undefined?d.dataNow.windSpeed:0);
              // amountGeneratorsOk++;
            }
            return line;
          });

          const total =  {
            turbine: "Total",
            indicator: "",
            production: formatNumber(round(rL.dataNow.production,3)),
            windSpeed: formatNumber(round(rL.dataNow.windSpeed,1)),
            status: ""
          };
          // turbinesStatus.push(total)
          // [total].concat(turbinesStatus);
      
          const tableData = {
            park: park,
            items: [total].concat(turbinesStatus),
            fields:  [
              'turbine',
              { key: 'indicator', label: '' },
              { key: 'production', label: 'Production (MWh)'},
              { key: 'windSpeed', label: 'Wind Speed (m/s)' },
              'status'
            ]
          }
    
          turbinesTablesData.push(tableData);
        }
        
      });

      setTurbinesTablesData(turbinesTablesData);

    })




  }

  useEffect(() => {
    loadGenerators();
  }, []);


  const loadGenerators = () => {
    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        if (response && response.error) {
          setCookie('lastTimeStamp', '');
          setCookie('name', '');
          window.location.reload();
        }
        else if (response  && !response.error) {
          // setGenerators(response.generators);
          fetchData();
        }
      }
    );
  }

  const getBadge = status => {
    switch (status) {
      case 'green': return 'success'
      case 'red': return 'danger'
      default: return ''
    }
  }


  return (
    <CCard>
      <CCardHeader>
          <CRow>
            <CCol xs="10">
              <h3 id="traffic" className="card-title mb-0">
                Current Status
              </h3>
              <div className="text-medium-emphasis">Last updated on: {lastUpdated}</div>
            </CCol>
            <CCol xs="2" className="text-right d-flex flex-center flex-justify-end">
              <CIcon content={freeSet.cilReload} className={"cursor-pointer animate-rotate"} size={'lg'} title={'Refresh'}
                onClick={() => fetchData()}/>
            </CCol>
            
          </CRow>


        </CCardHeader>
        {!loading || dataLoadError
                    ? 
          <CCardBody>


            <div style={{marginBottom:'30px'}}>
              <CRow>
              
                { tableDataCurrentStatus &&
                  <CCol xl="6" className="px-sm-4 mb-4">
                    {/* <h5>Current Status</h5> */}
                    <CDataTable
                        addTableClasses="current-status-table"
                        striped
                        items={tableDataCurrentStatus.items}
                        fields={tableDataCurrentStatus.fields}
                      />
                  </CCol>
                }

              </CRow>
            </div>
            
            <div style={{marginBottom:'50px'}}>
              <CRow>
              
                { turbinesTablesData.length > 0 &&
                  turbinesTablesData.map(park => (  
                  <CCol xl="6" className="px-sm-4 mb-4" key={'col-'+park.park}>
                    <h5>{park.park}</h5>
                    <CDataTable
                        key={park.park}
                        addTableClasses="monitor-park-table table-header-center"
                        striped
                        items={park.items}
                        fields={park.fields}
                        // itemsPerPage={15}
                        // pagination
                        sorter
                        scopedSlots = {{
                          'indicator':
                            (item)=>(
                              <td>
                                <CBadge color={getBadge(item.indicator)}>
                                </CBadge>
                              </td>
                            ),
                            'production':
                            (item)=>(
                              <td className="text-right">
                                {item.production}
                              </td>
                            ),
                            'windSpeed':
                            (item)=>(
                              <td className="text-right">
                                {item.windSpeed}
                              </td>
                            )
                        }}
                      />
                  </CCol>
                  ))
                }

              </CRow>
            </div>

          </CCardBody>
          :
          <CCardBody style={{textAlign:'center'}}>
            <CSpinner 
              className="loading-spinner"
              color='#321fdb'
            />
          </CCardBody>
        }
    </CCard>
  )
}

export default Dashboard
