import React, { useEffect, useState } from 'react'
import DataAPI from '../../../../helpers/DataAPI.js'
import {DateFilter, getDateLabel, formatDate, formatDate2, round, transparentize} from '../../../../helpers/utils.js'
import {setCookie,getCookie} from '../../../../helpers/sessionCookie.js'

import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CSelect,
  CButton,
  CRow,
  CCol,
  CLabel,
  CSpinner,

} from '@coreui/react'

import { Bar, Radar } from 'react-chartjs-2';

import { DocsLink } from 'src/reusable'

const ClimateSummary = () => {

  const [dateRange, setDateRange] = useState('y');
  const [groupBy, setGroupBy] = useState('day');
  const [loading, setLoading] = useState(false);
  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [generatorsLoaded, setGeneratorsLoaded] = useState(false);
  const [selectedGenerators, setSelectedGenerators] = useState([]);
  const [filterSubmitted, setFilterSubmitted] = useState(false);
  const [generators, setGenerators] = useState([]);
  const [generatorColors, setGeneratorColors] = useState([]);
  const [generatorsSelected, setGeneratorsSelected] = useState(false);
  const [metMastSelected, setMetMastSelected] = useState(true);
  const [allSelected, setAllSelected] = useState(true);
  const [windSpeedAvgs, setWindSpeedAvgs] = useState()

  const [barChartOneData, setBarChartOneData] = useState({
    labels: [],
    datasets: []
  });
  const [barChartTwoData, setBarChartTwoData] = useState({
    labels: [],
    datasets: []
  });
  const [barChartThreeData, setBarChartThreeData] = useState({
    labels: [],
    datasets: []
  });
  const [barChartFourData, setBarChartFourData] = useState({
    labels: [],
    datasets: []
  });
  const [barChartFiveData, setBarChartFiveData] = useState({
    labels: [],
    datasets: []
  });
  const [windRoseChartData, setWindRoseChartData] = useState({
    labels: [],
    datasets: []
  });

  const colors =  ['#003f5c', '#7a5195', '#bc5090', '#ef5675', '#ff764a', '#ffa600','#9ceb01']

  useEffect(() => {
    loadGenerators();
  }, []);
  

  const fetchData = () => {

    setLoading(true);

    const body = {}
    body.location = getCookie('location');
    if (dateRange && dateRange.split('--').length==2) {
      body.from = dateRange.split('--')[0]
      body.to = dateRange.split('--')[1]
    } else {
      body.period = dateRange
    }
    body.groupBy = groupBy;
    body.generators = selectedGenerators;
    
    DataAPI(
      {
      'endpoint': 'chart/climate',
      'method': 'POST',
      'body': body
    })
      .then(function (responseData) {

        if (responseData.error && responseData.error.message) {
          setDataLoadError(true)
          alert(responseData.error.message)
        } 

        setLoading(false);

        if (responseData && responseData.graphs) {

          const labels = responseData.graphs[0].data.map( (x,i) => {
            return x.datetimeFrom.split(' ')[0];
          })
          const graphData1 = {
            labels: labels,
            datasets: []
          };
          const graphData2 = {
            labels: labels,
            datasets: []
          };
          const graphData3 = {
            labels: labels,
            datasets: []
          };
          const graphData4 = {
            labels: labels,
            datasets: []
          };
          const graphData5 = {
            labels: labels,
            datasets: []
          };
          const graphData6 = {
            labels: ['0 North',
                    '45 NorthEast',
                    '90 East',
                    '135 SouthEast',
                    '180 South',
                    '225 SouthWest',
                    '270 West',
                    '315 NorthWest'],
            datasets: []
          };
            

          if (metMastSelected) {
            const datasetMetMastDefault = {
              label: 'Met Mast Average',
              backgroundColor: '#f32e2e',
            }
            const dataset1MetMast = {
              ...datasetMetMastDefault,
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.mmWindSpeed;
                      }),
            }
            const dataset2MetMast = {
              ...datasetMetMastDefault,
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.mmTemperature;
                      }),
            }
            const dataset3MetMast = {
              ...datasetMetMastDefault,
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.mmAirDensity;
                      }),
            }
            const dataset4MetMast = {
              ...datasetMetMastDefault,
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.mmWindDirection;
                      }),
            }
            const dataset5MetMast = {
              ...datasetMetMastDefault,
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.mmTurbulenceIntensity;
                      }),
            }
            const dataset6MetMast = {
              ...datasetMetMastDefault,
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.mmWindDirection;
                      }),
            }
            graphData1.datasets.push(dataset1MetMast)
            graphData2.datasets.push(dataset2MetMast)
            graphData3.datasets.push(dataset3MetMast)
            graphData4.datasets.push(dataset4MetMast)
            graphData5.datasets.push(dataset5MetMast)
            graphData6.datasets.push(dataset6MetMast)
          }

          if (allSelected) {
            const datasetAllDefault = {
              label: 'Turbine Average',
              backgroundColor: '#0400ff',
            }
            const dataset1All = {
              ...datasetAllDefault,
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.taWindSpeed;
                      }),
            }
            const dataset2All = {
              ...datasetAllDefault,
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.taTemperature;
                      }),
            }
            const dataset4All = {
              ...datasetAllDefault,
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.taWindDirection;
                      }),
            }
            const dataset5All = {
              ...datasetAllDefault,
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.taTurbulenceIntensity;
                      }),
            }
            const dataset6All = {
              ...datasetAllDefault,
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.taTurbulenceIntensity;
                      }),
            }
            graphData1.datasets.push(dataset1All)
            graphData2.datasets.push(dataset2All)
            graphData4.datasets.push(dataset4All)
            graphData5.datasets.push(dataset5All)
            graphData6.datasets.push(dataset6All)
          }


          responseData.graphs[0].gen_code.forEach((gen,index) => {
            const dataset1 = {
              label:  gen,
              backgroundColor: generatorColors[gen],
              data: responseData.graphs[0].data.map( (x,i) => {
                return x.genWindSpeed[index];
              })
            };
            const dataset4 = {
              label:  gen,
              backgroundColor: generatorColors[gen],
              data: responseData.graphs[0].data.map( (x,i) => {
                return x.genWindDirection[index];
              })
            };
            const dataset5 = {
              label:  gen,
              backgroundColor: generatorColors[gen],
              data: responseData.graphs[0].data.map( (x,i) => {
                return x.genTurbulenceIntensity[index];
              })
            };
            const dataset6 = {
                label: gen,
                borderColor: generatorColors[gen],
                backgroundColor: transparentize(generatorColors[gen], 0.5),
                data :responseData.graphs[0].data.map( (x,i) => {
                  return x.genTurbulenceIntensity[index];
                })
            };
            graphData1.datasets.push(dataset1);
            graphData4.datasets.push(dataset4);
            graphData5.datasets.push(dataset5);
            graphData6.datasets.push(dataset6);
          });

          

          setBarChartOneData(graphData1);
          setBarChartTwoData(graphData2);
          setBarChartThreeData(graphData3);
          setBarChartFourData(graphData4);
          setBarChartFiveData(graphData5);
          // setWindRoseChartData(graphData6);
        
        }
      });


      DataAPI(
        {
        'endpoint': 'wind/windRose',
        'method': 'POST',
        'body': body
      })
        .then(function (responseData) {
  
          if (responseData.error && responseData.error.message) {
            setDataLoadError(true)
            alert(responseData.error.message)
          } 
  
          setLoading(false);
  
          if (responseData) {
  
            const graphData = {
              labels: ['0 North',
                      '45 Northeast',
                      '90 East',
                      '135 SouthEast',
                      '180 South',
                      '225 SouthWest',
                      '270 West',
                      '315 NorthWest'],
              datasets: []
            };
              
  
            if (metMastSelected) {
              const datasetMetMast = {
                label: 'Met Mast Average',
                backgroundColor: '#f32e2e2b',
                borderColor: '#f32e2e',
                pointBackgroundColor: '#f32e2e',
                // pointBorderColor: '#f32e2e',
                borderWidth: .9,
                data: responseData.data.map( (x,i) => {
                          return x.met_mast;
                        }),
              }
              graphData.datasets.push(datasetMetMast)
            }
  
            if (allSelected) {
              const datasetAll = {
                label: 'Turbine Average',
                backgroundColor: '#0400ff2b',
                borderColor: '#0400ff',
                pointBackgroundColor: '#0400ff',
                // pointBorderColor: '#0400ff',
                borderWidth: .9,
                data: responseData.data.map( (x,i) => {
                          return x.total_turbine_avg;
                        }),
              }
              graphData.datasets.push(datasetAll)
            }
  
  
            responseData.data[0].turbine_data.forEach((gen,index) => {

              const dataset = {
                  label: gen.gen_code,
                  borderColor: generatorColors[gen.gen_code],
                  pointBackgroundColor: generatorColors[gen.gen_code],
                  backgroundColor: transparentize(generatorColors[gen.gen_code], 0.5),
                  borderWidth: .9,
                  data: responseData.data.map( (x,i) => {
                    return x.turbine_data[index].turbine_avg;
                  })
              };
              graphData.datasets.push(dataset);
            })

            const windSpeedAvgs = responseData.data.map( (x,i) => { return x.windspeed_avg; });
            setWindSpeedAvgs(windSpeedAvgs);

            setWindRoseChartData(graphData);
          
          }
        });

  }


  const loadGenerators = () => {

    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        
        if (response && response.error) {
          setCookie('lastTimeStamp', '');
          setCookie('name', '');
          window.location.reload();
        }
        else if (!dataLoaded && response  && !response.error) {

          if (response.generators != null) {
            setGenerators(response.generators);
            // setSelectedGenerators(response.generators.map((gen) => (gen.id)));
            let colorIndex = 0
            response.generators.forEach((gen) => {
              generatorColors[gen.code] = colors[colorIndex%colors.length];
              setGeneratorColors(generatorColors);
              colorIndex++
            });
          }

          setGeneratorsLoaded(true);

        }
      }
    );

  }

  const selectGenerator = (id) => {

    let newSelected = selectedGenerators;
    newSelected = newSelected.includes(id)
                          ? newSelected.filter(i => i !== id) // remove item
                          : [ ...newSelected, id ]; // add item
    setSelectedGenerators(newSelected.sort());
  }

  const filterGenerators = () => {

    setDataLoaded(false);
    setGeneratorsSelected(false);
    setFilterSubmitted(true);
    if (selectedGenerators.length > 0 || allSelected || metMastSelected) {
      setLoading(true);
      setGeneratorsSelected(true);
      fetchData(dateRange);
    }

  }

  const optionsBarGraph = {
    animation: {duration: loading ? 0 : 1000},
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
    }
  };
  const options1 = {
    ...optionsBarGraph,
    plugins: {
      tooltip: {
        callbacks: {
          label: function(tooltipItem, data) {
            return tooltipItem.dataset.label + ": " + round(tooltipItem.raw,1) + ' m/s';
          }
        }
      }
    }
  };
  const options2 = {
    plugins: {
      tooltip: {
        callbacks: {
          label: function(tooltipItem, data) {
            return tooltipItem.dataset.label + ": " + round(tooltipItem.raw,1) + ' °C';
          }
        }
      }
    }
  };
  const options3 = {
    plugins: {
      tooltip: {
        callbacks: {
          label: function(tooltipItem, data) {
            return tooltipItem.dataset.label + ": " + round(tooltipItem.raw,2) + ' kg/m3';
          }
        }
      }
    }
  };
  const options4 = {
    plugins: {
      tooltip: {
        callbacks: {
          label: function(tooltipItem, data) {
            return tooltipItem.dataset.label + ": " + round(tooltipItem.raw,1) + '°';
          }
        }
      }
    }
  };
  const options5 = {
    plugins: {
      tooltip: {
        callbacks: {
          label: function(tooltipItem, data) {
            return tooltipItem.dataset.label + ": " + round(tooltipItem.raw,1) + ' %';
          }
        }
      }
    }
  };
  const optionsWindRoseChart = {
    plugins: {
      tooltip: {
        callbacks: {
          label: function(tooltipItem, data) {
            return tooltipItem.dataset.label + ": " + round(tooltipItem.raw,1) + ' %';
          }
        }
      }
    }
  }


  return (

      <CCard>

      <CCardHeader>
          <CRow>
            
            <CCol sm="4">
              <h3 id="traffic" className="card-title mb-0">
                Climate - Summary
              </h3>
              <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div>
            </CCol>
            
            <CCol sm="8" className="text-right d-flex flex-center flex-justify-end">
                <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4,minWidth:'60px'}}>Group by</h6>
                <CSelect className={'input-md'} value={groupBy} disabled={loading} onChange={(ev) => { setGroupBy(ev.target.value); }} custom name="groupby" id="groupby">
                    <option value="day">Day</option>
                    <option value="week">Week</option>
                    <option value="month">Month</option>
                </CSelect>
                <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4}}>Period</h6>
              <DateFilter value={dateRange} options={['y','cm','cy','x','xx']} disabled={loading} onChange={(value) => { setDateRange(value);}} />
              {/* <CButton color="primary" disabled={loading} className="mr-2 ml-3" onClick={() => { fetchData({period: dateRange, groupBy: groupBy});}} >Submit</CButton> */}
            </CCol>

          </CRow>

        </CCardHeader>

        <CCardBody>

          <CRow className={"py-3 mb-4 mx-0 bg-light"} style={{borderRadius:"3px"}}>
            <CCol sm="10" className={"d-flex "} >
              <h6 className="mx-2 my-2 pt-1" style={{lineHeight:1.2,minWidth:'110px'}}>Select turbines:</h6>   
              {generatorsLoaded ? 
                <div>
                  <CButton 
                    style={{backgroundColor:'#f32e2e',color: 'white'}} 
                    className={(metMastSelected ? "selected" : "") + " btn-generator mx-1 my-1"}
                    onClick={() => setMetMastSelected(!metMastSelected)}
                    >
                    Met
                  </CButton>
                  <CButton 
                    style={{backgroundColor:'#0400ff',color: 'white'}} 
                    className={(allSelected ? "selected" : "") + " btn-generator mx-1 my-1"}
                    onClick={() => setAllSelected(!allSelected)}
                    >
                    ALL
                  </CButton>
                  { generators.map((gen, index) => (  
                    <CButton 
                      style={{backgroundColor:generatorColors[gen.code],color: 'white'}} 
                      className={(selectedGenerators.includes(gen.id) ? "selected" : "") + " btn-generator mx-1 my-1"}
                      onClick={() => selectGenerator(gen.id)} 
                      id={gen.id}
                      >
                        {gen.code}
                    </CButton>
                  )) }
                </div>
                :
                <CSpinner 
                  size="sm"
                  className="loading-spinner"
                  color='#321fdb'
                />
              }
            </CCol>
            <CCol sm="2" className="text-right d-flex flex-end flex-justify-end ">
              <CButton color="primary" className="mx-2 mb-1" onClick={() => filterGenerators()} >Submit</CButton>
            </CCol>
          </CRow>

          { generatorsSelected ?

            <div>

            {!loading || dataLoadError ? 

              <div>
                <CRow className={'mb-5'}>
                  <CCol>
                    <h4 className="pb-2 mb-4 border-bottom" >Average Wind Speed</h4>
                    <div>m/s</div>
                    <Bar 
                      data={barChartOneData}
                      options={options1} 
                    />
                  </CCol>
                </CRow>
                <CRow className={'mb-5'}>
                  <CCol>
                    <h4 className="pb-2 mb-4 border-bottom">Average Temperature</h4>
                    <div>°C</div>
                    <Bar 
                      data={barChartTwoData}
                      options={options2} 
                    />
                  </CCol>
                </CRow>

                <CRow className={'mb-5'}>
                  <CCol xs="12">
                    <h4 className="pb-2 mb-4 border-bottom" >Average Air Density</h4>
                    <div>kg/m3</div>
                    <Bar 
                      data={barChartThreeData}
                      options={options3} 
                    />
                  </CCol>
                </CRow>
                
                <CRow className={'mb-5'}>
                  <CCol>
                    <h4 className="pb-2 mb-4 border-bottom" >Wind Direction</h4>
                    <div>Degrees</div>
                    <Bar 
                      data={barChartFourData}
                      options={options4} 
                    />
                  </CCol>
                  <CCol xs="12" className={'mt-4'}>
                    <CCard className={'pl-3 pr-2'} style={{maxWidth: '480px', marginLeft: 'auto', marginRight: 'auto'}}>
                      <CCardBody>
                        <CRow>
                          <CCol sm="6" xs="12">
                            <CLabel className={'w-100 mb-0'}>0-45 North to NorthEast</CLabel>
                            <CLabel className={'w-100 mb-0'}>45-90 NorthEast to East</CLabel>
                            <CLabel className={'w-100 mb-0'}> 90-135 East to SouthEast</CLabel>
                            <CLabel className={'w-100 mb-0'}>135-180 SouthEast to South</CLabel>
                          </CCol>
                          <CCol sm="6" xs="12">
                            <CLabel className={'w-100 mb-0'}> 180-225 South to SouthWest</CLabel>
                            <CLabel className={'w-100 mb-0'}>225-270 SouthWest to West</CLabel>
                            <CLabel className={'w-100 mb-0'}> 270-315 West to NorthWest</CLabel>
                            <CLabel className={'w-100 mb-0'}> 315-360 NorthWest to North</CLabel>
                          </CCol>
                        </CRow>
                      </CCardBody>
                    </CCard>
                  </CCol>
                </CRow>

                <CRow className={'mb-5'}>
                  <CCol>
                    <h4 className="pb-2 mb-4 border-bottom" >Wind Rose</h4>
                    <div>%</div>
                    <div style={{maxWidth:'900px',margin:'0 auto'}}>
                      <Radar 
                        data={windRoseChartData}
                        options={optionsWindRoseChart} 
                      />
                    </div>
                  </CCol>
                </CRow>

                <CRow className={'mb-5'}>
                  <CCol>
                    <h4 className="pb-2 mb-4 border-bottom" >Turbulence Intensity</h4>
                    <div>%</div>
                    <Bar 
                      data={barChartFiveData}
                      options={options5} 
                    />
                  </CCol>
                </CRow>

              </div>
            : 
              <div className='text-center'>
                <CSpinner 
                  className="loading-spinner"
                  color='#321fdb'
                />
              </div>
            }

          </div>

        :

        <div>
          { filterSubmitted &&
            <CRow>
              <CCol className="text-center">
                Select one or more generators
              </CCol>
            </CRow>
          }
        </div>

}
        </CCardBody>
      </CCard>

  )
}

export default ClimateSummary
