import React, { useState, useEffect } from 'react'

import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CButtonGroup,
  CProgress,
  CCardFooter,
  CFormSelect,
  CSelect,
  CDataTable,
  CBadge
} from '@coreui/react'

import CIcon from '@coreui/icons-react'

import DataAPI from '../../../helpers/DataAPI.js'

import {
  CChartBar,
  CChartLine,
  CChartDoughnut,
  CChartRadar,
  CChartPie,
  CChartPolarArea
} from '@coreui/react-chartjs'
import { DocsLink } from 'src/reusable'

import {formatDate, round, getDateLabel, DateFilter} from '../../../helpers/utils.js'

import { Scatter } from 'react-chartjs-2';

import ChartLoading from '../../others/ChartLoading.js'

const rand = () => {
  return 2;
}


const ctx = document.getElementById('x');

const Charts = () => {


  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });
  const [graphData2, setGraphData2] = useState({
    labels: [],
    datasets: []
  });

  const [tableData1, setTableData1] = useState([]);
  const [tableFields1, setTableFields1] = useState([]);
  const [tableData2, setTableData2] = useState([]);
  const [tableFields2, setTableFields2] = useState([]);
  const [generator, setGenerator] = useState('');
  const [generators, setGenerators] = useState([]);
  const [generatorsLoaded, setGeneratorsLoaded] = useState(false);
  const [park, setPark] = useState();
  const [date, setDate] = useState();
  const [dateRange, setDateRange] = useState('cm');
  const [data, setData] = useState([]);
  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [curves, setCurves] = useState([]);
  const [performance, setPerformance] = useState({});

  useEffect(() => {
    loadGenerators();
  }, []);


  const fetchData = (options = {}) => {

    const {period, generator} = options

    setLoading(true);

    const body = {}

    if (generator) body.generators = [generator];

    // console.log('.....', period, period.split('-').length)

    if (period && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }
    
    DataAPI({
      'endpoint': 'chart/pitchWindspeed',
      'method': 'POST',
      'body': body
    }).then(
      response => {


        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

          // if (!response.generator) return(alert('No generator datasets'));

          const generatorData = response.generator && response.generator[0].data;

          setDataLoaded(true);
          setData(response);

          if (generatorData.curves.length==0) return;

          const labels = [];

          const curves = generatorData.curves;

          curves[0].x.forEach(
            x => {
              labels.push(round(x))
            }
          );

          const graphData = {
            labels: [],
            datasets: [
              {
                label:  curves[0].label,
                data: curves[0].x.map((x, i) => ({ x, y: curves[0].y[i] })),
                backgroundColor: '#374c80',
              },
              {
                label:  curves[1].label,
                data: curves[1].x.map((x, i) => ({ x, y: curves[0].y[i] })),
                backgroundColor: '#ffa600',
              },
            ],
            backgroundColor: ["red", "blue", "green", "blue", "red", "blue"]
        
          }

          setGraphData(graphData);


          if (generatorData){
            if(generatorData.hasOwnProperty('dataUse')) processTableData1(generatorData.dataUse);
            if(generatorData.hasOwnProperty('dataFilteredDueTo')) processTableData2(generatorData.dataFilteredDueTo);
            // if(generatorData.hasOwnProperty('performance')) setPerformance(generatorData.performance);
          }

        }
    );


    DataAPI({
      'endpoint': 'chart/pitchWindspeed',
      'method': 'POST',
      'body': body
    }).then(
      response => {


        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

          // if (!response.generator) return(alert('No generator datasets'));

          const generatorData = response.generator && response.generator[0].data;

          setDataLoaded(true);
          setData(response);

          if (generatorData.curves.length==0) return;

          const labels = [];

          const curves = generatorData.curves;

          curves[0].x.forEach(
            x => {
              labels.push(round(x))
            }
          );

          const graphData = {
            labels: [],
            datasets: [
              {
                label:  curves[0].label,
                data: curves[0].x.map((x, i) => ({ x, y: curves[0].y[i] })),
                backgroundColor: '#374c80',
              },
              {
                label:  curves[1].label,
                data: curves[1].x.map((x, i) => ({ x, y: curves[0].y[i] })),
                backgroundColor: '#ffa600',
              },
            ],
            backgroundColor: ["red", "blue", "green", "blue", "red", "blue"]
        
          }

          setGraphData(graphData);


          if (generatorData){
            if(generatorData.hasOwnProperty('dataUse')) processTableData1(generatorData.dataUse);
            if(generatorData.hasOwnProperty('dataFilteredDueTo')) processTableData2(generatorData.dataFilteredDueTo);
            // if(generatorData.hasOwnProperty('performance')) setPerformance(generatorData.performance);
          }

        }
    );


  }


  const loadGenerators = () => {

    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        
        if (!dataLoaded && response  && !response.error) {

          setGenerator(response.generators[0].id);
          setGenerators(response.generators);

          fetchData({period: dateRange,  generator: response.generators[0].id});

        }
      }
    );
  }


  const processTableData1 = (dataUse) => {

    const tableData = [];

    // var usedData = (Math.round(dataUse.usedData)).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ".");
    // var filteredData = (Math.round(dataUse.filteredData)).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ".");
    // var total = (Math.round(dataUse.total)).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ".");

    tableData.push({
      '': dataUse.info,
      'Used Data': round(dataUse.usedData)+'%',
      'Filtered Data': round(dataUse.filteredData)+'%',
      'Total': round(dataUse.total)+'%'
    })

    setTableData1(tableData);
    setTableFields1(['', 'Used Data', 'Filtered Data', 'Total']);

  }


  const processTableData2 = (dataFilteredDueTo) => {

    const tableData = [];

    tableData.push({
          '': dataFilteredDueTo.info,
          'Missing Data': round(dataFilteredDueTo.missingData)+'%',
        })

    setTableData2(tableData);
    setTableFields2(['', 'Missing Data']);
    
  }


  const getBadge = status => {
    switch (status) {
      case 'Active': return 'success'
      case 'Inactive': return 'secondary'
      case 'Pending': return 'warning'
      case 'Banned': return 'danger'
      default: return 'primary'
    }
  }

  const change = (ev) => {
  }

  const optionsScatter = {
    animation: {duration: loading ? 0 : 1000},
    tooltips: {
      enabled: false,
    },
    legend: {
      display: false
   },
  };

  return (
      <CCard className="mb-4">

      <CCardHeader>
          <CRow>
            <CCol sm="9">
              <h4 id="traffic" className="card-title mb-0">
                Diagnostics
              </h4>
              <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div>
            </CCol>

            <CCol sm="3" class="text-right">
              <DateFilter value={dateRange} options={['cm',/*'cy','12w','12m',*/'x']} disabled={loading} onChange={(value) => { setDateRange(value); fetchData({generator: generator, period: value});}} />
            </CCol>
            
          </CRow>

        </CCardHeader>

        <CCardBody>


        <CRow>
          <CCol sm="8">

          <h5 id="traffic" className="card-title mb-0">Wind Speed vs Blade Pitch</h5>

            <div style={{marginBottom:'50px'}}>
              <div className="left-center" style={{width: '100%'}}>Turbine Average Wind Speed (m/s)</div>
                <Scatter data={graphData} options={optionsScatter} />
              <div className="text-center" style={{width: '100%'}}>Turbine Average Blade Pitch</div>
            </div>

            <h5 id="traffic" className="card-title mb-0"> Generator RPM vs. Power</h5>

            <div style={{marginBottom:'50px'}}>
              <div className="left-center" style={{width: '100%'}}>Turbine Generator Average RPM</div>
                <Scatter data={graphData2} options={optionsScatter} />
              <div className="text-center" style={{width: '100%'}}>Power (KWh)</div>
            </div>

          </CCol>
          <CCol sm="4">


            {/* % data above Reference Power Curve: {round(performance['Percentage data above manufacturer power curve']) }%<br />
            % data below Reference Power Curve: {round(performance['Percentage data below manufacturer power curve']) }%<br />
            Performance index {round(performance['Performance index'])}%<br /><br /> */}

            <h4>Data Use</h4>
            <div class="mb-4" style={{textAlign:'right'}}>
                {dataLoaded || dataLoadError
                  ? <CDataTable
                  items={tableData1}
                  fields={tableFields1}
                  striped
                  itemsPerPage={5}
                  pagination
                  scopedSlots = {{
                    'status':
                      (item)=>(
                        <td>
                          <CBadge color={getBadge(item.status)}>
                            {item.status}
                          </CBadge>
                        </td>
                      )

                  }}
                />
                  : 
                  <ChartLoading/>
              
            }
            </div>

            <h4>Reasons for Filtered Data</h4>
            <div style={{textAlign:'right'}}>
              {dataLoaded || dataLoadError
                  ? 
                  <CDataTable
                  items={tableData2}
                  fields={tableFields2}
                  striped
                  itemsPerPage={5}
                  pagination
                  scopedSlots = {{
                    'status':
                      (item)=>(
                        <td>
                          <CBadge color={getBadge(item.status)}>
                            {item.status}
                          </CBadge>
                        </td>
                      )

                  }}
                />
                : 
                <ChartLoading/>
            } 
            </div>


          </CCol>
        </CRow>

          
          {/* <Scatter data={graphData} options={optionsScatter} /> */}
        </CCardBody>

      </CCard>



  )
}

export default Charts
