import React, { useState, useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CAlert,
  CLabel
} from '@coreui/react'
import {
  CChartBar,
  CChartLine,
  CChartDoughnut,
  CChartRadar,
  CChartPie,
  CChartPolarArea
} from '@coreui/react-chartjs'
import { DocsLink } from 'src/reusable'
import Tables from 'src/views/base/tables/Tables'


const Reports = () => {

  const [parksRows, setParksRows] = useState('');


  useEffect(() => {
    loadConfiguration();
  }, []);


  const loadConfiguration = () => {

    setLoading(true);
    
    DataAPI({
      'endpoint': 'report/configure',
      'method': 'GET'
    }).then(
      response => {

        setLoading(false);

        if (response.error) {
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

        setName(response.name)
        setEmail(response.email)

      }
    );


  }


  <CRow>
          <CCol sm="6">
            <h3 id="traffic" className="card-title mb-0">
              Reports
            </h3>
            <div className="small text-medium-emphasis">Settings</div>
          </CCol>
        </CRow>


  return (
    <CCard>
      <CCardHeader>
        <CRow>
          <CCol sm="6">
            <h3 id="traffic" className="card-title mb-0">
              Reports
            </h3>
            <div className="small text-medium-emphasis">Settings</div>
          </CCol>
        </CRow>
      </CCardHeader>
      <CCardBody>
        <CLabel>Please select reports you wish to receive by e-mail</CLabel>

        {parksRows}

      </CCardBody>
    </CCard>
  )
}

export default Reports
