function quantilesInterpolate(arr, length, interpolate) {
    const n1 = length - 1;
    const compute = (q) => {
        const index = q * n1;
        const lo = Math.floor(index);
        const h = index - lo;
        const a = arr[lo];
        return h === 0 ? a : interpolate(a, arr[Math.min(lo + 1, n1)], h);
    };
    return {
        q1: compute(0.25),
        median: compute(0.5),
        q3: compute(0.75),
