import React, { useState, useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CButtonGroup,
  CProgress,
  CCardFooter,
  CFormSelect,
  CSelect
} from '@coreui/react'
import { CMultiSelect } from '@coreui/react-pro'
import Multiselect from 'multiselect-react-dropdown';



import CIcon from '@coreui/icons-react'

import DataAPI from '../../../helpers/DataAPI.js'

import {
  CChartBar,
  CChartLine,
  CChartDoughnut,
  CChartRadar,
  CChartPie,
  CChartPolarArea
} from '@coreui/react-chartjs'
import { DocsLink } from 'src/reusable'

import {formatDate, round, getDateLabel, DateFilter} from '../../../helpers/utils.js'

import { Bar, Chart, Line, Scatter } from 'react-chartjs-2';
import { generatePath } from 'react-router-dom/cjs/react-router-dom.min';

const rand = () => {
  return 2;
}


const ctx = document.getElementById('x');

// var mixedChart = new Chart(ctx, {
//     data: {
//         datasets: [
//         {
//             type: 'line',
//             label: 'Line Dataset',
//             data: [50, 50, 50, 50],
//         }],
//         labels: ['January', 'February', 'March', 'April']
//     },
//     // options: options
// });


const Charts = () => {


  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });

  const [generator, setGenerator] = useState(null);
  const [generators, setGenerators] = useState([]);
  const [generatorsLoaded, setGeneratorsLoaded] = useState(false);
  const [selectedGenerators, setSelectedGenerators] = useState([]);
  const [park, setPark] = useState();
  const [date, setDate] = useState();
  const [dateRange, setDateRange] = useState('12w');
  const [groupBy, setGroupBy] = useState('week');
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [dataLoaded, setDataLoaded] = useState(false);
  const [generatorsButtons, setGeneratorsButtons] = useState(null);
  const colors = [
    '#003f5c',
    '#374c80',
    '#7a5195',
    '#bc5090',
    '#ef5675',
    '#ff764a',
    '#ffa600'
  ];

  const fetchData = (options = {}) => {

    const {generator, period, groupBy} = options

    setLoading(true);

    const body = {}

    if (generator) body.generators = [generator];
    if (period && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }
    body.groupBy = groupBy

    DataAPI({
      endpoint: 'chart/performanceIndex',
      method: 'POST',
      body: body
    })
      .then(function (responseData) {

        setLoading(false);

        if (responseData.error) {
           if (responseData.error.message) {
            return(alert(responseData.error.message))
           } else {
            return(alert(responseData.error)) 
           }
        }

        const data = {
          labels: [],
          datasets: [],
          backgroundColor: ["red", "blue", "green", "blue", "red", "blue"]

        }

        const colors = [
            '#003f5c',
            '#374c80',
            '#7a5195',
            '#bc5090',
            '#ef5675',
            '#ff764a',
            '#ffa600'
          ];


          const dataset100 = {
            labels: [''],
            label: '',
            backgroundColor: colors.pop(),
            data: []
          };

        responseData.data.forEach((rDs) => {

          const dataset = {
            labels: [],
            label: rDs.name,
            backgroundColor: colors.pop(),
            data: [],
            hidden: rDs.name!='Total'
          };

          rDs.data.graph.forEach((rD) => {
            // const dsData = rD.dataSet.find((dS) => dS.id === rDs.id);
            const label = (groupBy == "day") ? `${formatDate(rD.from)}` : `${formatDate(rD.from)} - ${formatDate(rD.to)}`
            dataset.labels.push(label);
            dataset.data.push(rD.value)

            dataset100.data.push(100)

          });

          dataset.labels.reverse();
          dataset.data.reverse();

          data.datasets.push(dataset);
          // data.datasets.push(dataset100);

        });

        responseData.data[0].data.graph.forEach((rD) => {
          const rDfrom = rD.from;
          const rDto = rD.to;
          const label = (groupBy == "day") ? `${formatDate(rD.from, 'noyear')}` : `${formatDate(rD.from, 'noyear')} - ${formatDate(rD.to, 'noyear')}`
          data.labels.push(label);

        });

        data.labels.reverse();

        setGraphData(data);
        setDataLoaded(true);

      })
      .catch(
        err => {
          console.warn(`ERROR! ${err}`)
          setLoading(false);
        }

        // err => alert(`ERROR! ${err}`)
      );

  }

  // useEffect(() => {
  //   fetchData({period: period,  groupBy: groupBy});
  // }, []);

  useEffect(() => {
    loadGenerators();
  }, []);
  
  const loadGenerators = () => {
  
    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        
        if (!dataLoaded && response  && !response.error) {
  
          // setGenerator(response.generators[0].id);
          setGeneratorsLoaded(true);
          setGenerators(response.generators);
  
          fetchData({period: dateRange, groupBy: groupBy});
  
        }
      }
    );
  }



  const dataSets = [];


    const parks = [
    {
        "id": 2,
        "code": "location #2",
        "name": "location sample",
        "address": "Location for sample",
        "latitude": -34.288322967923214,
        "longitude": -54.050271841125806,
        "outputCapacity": 3.0,
        "dataDefinitionId": 2,
        "dataDefinition": null,
        "stations": null,
        "generators": null
    }
]



  const dates = [
    'Hoy',
    'Ayer',
    'Esta semana',
    'Este mes',
    'Este año'
  ]


  const change = (ev) => {
  }

  // const refreshChart = (period) => {
  //   fetchData({generator: generator, period: dateRange,  groupBy: groupBy});
  // }

  const options = {animation: {duration: loading ? 0 : 1000}};


  const selectGenerator = (id) => {

    let i = id;
    let newSelected = selectedGenerators.includes(item)
                          ? selectedGenerators.filter(i => i !== item) // remove item
                          : [ ...selectedGenerators, item ]; // add item
    setSelectedGenerators(newSelected);
    // if (modal) {
    //   let sdate = "";
    //   let edate = "";
    //   if (startDate !== null) {
    //     sdate =  `${startDate.getFullYear()}-${("0" + (startDate.getMonth() + 1)).slice(-2)}-${("0" + startDate.getDate()).slice(-2)}`;
    //   }
    //   if (endDate !== null) {
    //     edate =  `${endDate.getFullYear()}-${("0" + (endDate.getMonth() + 1)).slice(-2)}-${("0" + endDate.getDate()).slice(-2)}`;
    //   }
    //   props.onChange(`${sdate}--${edate}`);
    //   setFrom(sdate);
    //   setTo(edate);
    //   setValue('xx');

    // } else {
    //   props.onChange(value);
    // }

  }



  return (
    <div>
      <CCard className="mb-4">

      <CCardHeader>
        <h4 id="traffic" className="card-title mb-0">
          Choose de generators
        </h4>
      </CCardHeader>
      <CCardBody>
        <CRow>
          <CCol sm="9">
          {generatorsLoaded && 
            generators.map((gen, index) => (  
                <CButton 
                  style={{backgroundColor:colors[index],color: 'white'}} 
                  className={selectedGenerators.includes} 
                  onClick={selectGenerator(gen.id)} 
                  id={gen.id}
                  >
                    {gen.name}
                </CButton>
            ))
          }
          </CCol>
          <CCol sm="3" className="text-right">
            <CButton color="primary" className="mx-2">Submit</CButton>
          </CCol>
        </CRow>
      </CCardBody>

      </CCard>

      <CCard className="mb-4">

      <CCardHeader>
          <CRow>
            <CCol sm="7">
              <h4 id="traffic" className="card-title mb-0">
                Performance Index
              </h4>
              <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div>
              </CCol>

              <CCol sm="5" className="text-right d-flex">
              {/* <h6 className="mr-2 ml-4" style={{lineHeight:2.4,minWidth:'64px'}}>Generator</h6> */}

              {/* <Multiselect
                options={state.options} // Options to display in the dropdown
                selectedValues={state.selectedValue} // Preselected value to persist in dropdown
                //onSelect={this.onSelect} // Function will trigger on select event
                //onRemove={this.onRemove} // Function will trigger on remove event
                displayValue="name" // Property name to display in the dropdown options
                /> */}

                {/* <CSelect disabled={loading} style={{maxWidth:'150px'}} onChange={(ev) => {setGenerator(ev.target.value); fetchData({period: dateRange, groupBy: groupBy, generator: ev.target.value});}} custom name="generator" id="generator">
                  <option value="0">All generators</option>
                  {generators.map((oGenerator) => (
                      <option value={oGenerator.id}>
                        {oGenerator.code}
                      </option>
                    ))}
                </CSelect> */}
                <h6 className="mr-2 ml-4" style={{lineHeight:2.4,minWidth:'60px'}}>Group by</h6>
                <CSelect value={groupBy} style={{maxWidth:'150px'}} disabled={loading} onChange={(ev) => { setGroupBy(ev.target.value); fetchData({generator: generator, period: dateRange, groupBy: ev.target.value});}} custom name="period" id="period">
                  <option value="day">Day</option>
                  <option value="week" selected>Week</option>
                  <option value="month">Month</option>
                </CSelect>
                <h6 className="mr-2 ml-4" style={{lineHeight:2.4}}>Period</h6>
                <DateFilter value={dateRange} options={['cm','cy','12w','12m','x','xx']} disabled={loading} onChange={(value) => { setDateRange(value); fetchData({generator: generator, period: value, groupBy: groupBy});}} />
              </CCol>

          </CRow>


        </CCardHeader>

        <CCardBody>
         
          <Line data={graphData} options={options} />
        </CCardBody>

      </CCard>
      </div>
  )
}

export default Charts
