import React, { useState, useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardHeader,
  CRow,
  CCol,
  CSpinner,
  CSelect,
  CButton,
  CAlert
} from '@coreui/react'

import { Bar } from 'react-chartjs-2';

import DataAPI from '../../../helpers/DataAPI.js'
import {setCookie,getCookie} from '../../../helpers/sessionCookie.js'
import {DateFilter, round, formatDate, getDateLabel} from '../../../helpers/utils.js'

const AvailabilityAndLosses = () => {

  const [period, setPeriod] = useState('cm');
  const [groupBy, setGroupBy] = useState('day');
  const [generators, setGenerators] = useState([]);
  const [generator, setGenerator] = useState(null);
  const [generatorsLoaded, setGeneratorsLoaded] = useState(false);

  const [dataLoaded, setDataLoaded] = useState(false);

  const [loading, setLoading] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);

  const [tableData, setTableData] = useState([]);
  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });
  
  const colors =  ['#003f5c', '#7a5195', '#bc5090', '#ef5675', '#ff764a', '#ffa600','#9ceb01']

  const fetchData = () => {

    const body = {}
    body.location = getCookie('location');
    if (generator && generator !== '0') body.generators = [generator];
    
    if (period && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }
    body.groupBy = groupBy

    setLoading(true);

    DataAPI(
      {
      'endpoint': 'chart/availability',
      'body': body
    }).then(function (responseData) {

      setLoading(false);
      setDataLoaded(true);
      if (responseData.error) {
        setDataLoadError(true);
          if (responseData.error.message) {
          return(alert(responseData.error.message))
          } else {
          return(alert(responseData.error)) 
          }
      }

      const data = {
        labels: [],
        datasets: []
      }
      const tableFields = [{
        key: "field",
        label: "",
        _style: { width: '100px'}
      }]
      const rowTimeBasedAvailability = {
        field: 'rowTimeBasedAvailability',
        generator: gen.code,
        tag: "Events",
      }
      const rowProductionBasedAvailability = {
        alarm: '',
        generator: '',
        tag: "Duration",
      }

      responseData.data.forEach((rD,index) => {
        const rDfrom = rD.from.split(' ')[0]
        const rDto = rD.to.split(' ')[0]
        const label = (groupBy == "day") ? `${rDfrom}` : `${rDfrom} - ${rDto}`
        data.labels.push(label);
        tableFields.push(
          {
            key: "period-"+index,
            label: label
          }
        )
        rowTimeBasedAvailability["period-"+index3] = occurrences
            rowDuration["period-"+index3] = hours
      });

      const dataset1 = {
        label:  "Production",
        backgroundColor: '#00354e',
        data: responseData.data.map((x, i) => { return x.productionMwh }),
        stack: 'Production'
      };
      const dataset2 = {
        label:  "Manufacturer Lost Energy",
        backgroundColor: '#72124b',
        data: responseData.data.map((x, i) => { return x.powerLostManufacturer }),
        stack: 'Lost Energy'
      };
      const dataset3 = {
        label:  "Utility Lost Energy",
        backgroundColor: '#7c285b',
        data: responseData.data.map((x, i) => { return x.powerLostUtility }),
        stack: 'Lost Energy'
      };
      const dataset4 = {
        label:  "Environmental Lost Energy",
        backgroundColor: '#953c72',
        data: responseData.data.map((x, i) => { return x.powerLostEnvironmental }),
        stack: 'Lost Energy'
      };
      const dataset5 = {
        label:  "Service Lost Energy",
        backgroundColor: '#b2578e',
        data: responseData.data.map((x, i) => { return x.powerLostService }),
        stack: 'Lost Energy'
      };
      const dataset6 = {
        label:  "Curtailment Lost Energy",
        backgroundColor: '#dd7db7',
        data: responseData.data.map((x, i) => { return x.powerLostCurtailment }),
        stack: 'Lost Energy'
      };
      const dataset7 = {
        label:  "Performance",
        backgroundColor: '#ff764a',
        data: responseData.data.map((x, i) => { return x.performance }),
        stack: 'Performance'
      };

      data.datasets.push(dataset1);
      data.datasets.push(dataset2);
      data.datasets.push(dataset3);
      data.datasets.push(dataset4);
      data.datasets.push(dataset5);
      data.datasets.push(dataset6);
      data.datasets.push(dataset7);

      setGraphData(data);

    }).catch(
      e => {
        setLoading(false);
        alert(e);
      }
    );
  
    
  }

  useEffect(() => {
    loadGenerators();
  }, []);


  const loadGenerators = () => {
    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        if (response && response.error) {
          setCookie('lastTimeStamp', '');
          setCookie('name', '');
          window.location.reload();
        }
        else if (!dataLoaded && response && !response.error) {
          if (response.generators != null)
            setGenerators(response.generators);
          setGeneratorsLoaded(true)
        }
      }
    );
  }


  const optionsStackedBar = {
    responsive: true,
    scales: {
      x: {
        stacked: true,
      },
      y: {
        stacked: true,
      },
    },
    plugins: {
      tooltip: {
        callbacks: {
          label: function(tooltipItem, data) {
            return tooltipItem.dataset.label + ": " + round(tooltipItem.raw,1) + ' MWh';
          }
        }
      }
    },
  };

  return (
    <CCard>
      <CCardHeader>
        <CRow>
          <CCol sm="3">
            <h3 id="traffic" className="card-title mb-0">
              Availability and Losses
            </h3>
            <div className="small text-medium-emphasis">{getDateLabel(period)}</div>
          </CCol>
          
          <CCol sm="9" className="text-right d-flex flex-center flex-justify-end">
              <h6 className="mr-2 ml-4 mb-0" style={{lineHeight:2.4,minWidth:'64px'}}>Turbine</h6>
              <CSelect className={'input-md'} disabled={loading} onChange={(ev) => {setGenerator(ev.target.value); }} custom name="generator" id="generator">
                <option value="0">All turbines</option>
              {generators.map((oGenerator) => (
                                  <option value={oGenerator.id}>
                                    {oGenerator.code}
                                  </option>
                                ))}</CSelect>
              <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4,minWidth:'60px'}}>Group by</h6>
              <CSelect className={'input-md'} value={groupBy} disabled={loading} onChange={(ev) => { setGroupBy(ev.target.value); }} custom name="groupby" id="groupby">
                  <option value="day" selected>Day</option>
                  <option value="week">Week</option>
                  <option value="month">Month</option>
              </CSelect>
              <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4}}>Period</h6>
            <DateFilter value={period} options={['y','cm','cy','x','xx']} disabled={loading} onChange={(value) => { setPeriod(value);}} />
            <CButton color="primary" disabled={loading || !generatorsLoaded} className="mr-2 ml-3" onClick={() => { fetchData()}} >Submit</CButton>
          </CCol>
        </CRow>
      </CCardHeader>
      <CCardBody>
        { (loading || dataLoaded) &&
            
          <div>
            <div style={{marginBottom:'50px'}}>
              {/* <h4 className="pb-2 mb-4 border-bottom" >Alarms by alarm code</h4> */}
              { (dataLoaded || dataLoadError) ?
              <div>
                <div className="mt-5">
                  <CDataTable
                    items={tableData}
                    fields={tableFields}
                    striped
                  />
                </div>
                <div class="ml-2">MWh</div>
                <Bar options={optionsStackedBar} data={graphData} />
                <CRow className="text-center d-block" style={{textTransform: 'capitalize'}}>{groupBy}</CRow>
              </div>
              :
                <div className='text-center'>
                  <CSpinner className="loading-spinner" color='#321fdb'/>
                </div>
              }
            </div>
          </div>
        }
      </CCardBody>
    </CCard>
  )
}

export default AvailabilityAndLosses
