import React, { useState, useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CAlert
} from '@coreui/react'
import {
  CChartBar,
  CChartLine,
  CChartDoughnut,
  CChartRadar,
  CChartPie,
  CChartPolarArea
} from '@coreui/react-chartjs'
import { DocsLink } from 'src/reusable'

const Reports = () => {

  const [tableData, setTableData] = useState([]);
  const [configData, _setConfigData] = useState([]);
  const configDataRef = useRef(configData);
  const setConfigData = data => {
    configDataRef.current = data;
    _setConfigData(data);
  };
  const [tableFields, setTableFields] = useState([]);
  const [loaded, setLoaded] = useState(false);
  const [scopedSlots, setScopedSlots] = useState(false);
  const [reports, setReports] = useState([]);
  const [locations, setLocations] = useState([]);

  const [sending, setse] = useState(false);
  const [changesMade, setChangesMade] = useState(false);
  const [saved, setSaved] = useState(false);
