import React, { useState, useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CButtonGroup,
  CProgress,
  CCardFooter,
  CFormSelect,
  CDataTable,
  CBadge,
  CSelect,
  CModal,
  CModalHeader,
  CModalFooter,
  CModalBody,
  CModalTitle,
  CSpinner
} from '@coreui/react'
import {
  CChartBar,
  CChartLine,
  CChartDoughnut,
  CChartRadar,
  CChartPie,
  CChartPolarArea
} from '@coreui/react-chartjs'
import { DocsLink } from 'src/reusable'

import DataAPI from '../../../helpers/DataAPI.js'
import {DateFilter, formatDate, formatDate2, round, getDateLabel, formatNumber} from '../../../helpers/utils.js'

const Budget = () => {

  const currentYear = new Date().getFullYear()

  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState();
  const [curves, setCurves] = useState([]);
  const [filtersSelected, setFiltersSelected] = useState(false);
  const [fiscalYear, setFiscalYear] = useState(currentYear);
  const [addEstimatedCurtailments, setAddEstimatedCurtailments] = useState(true);
  const [parks, setParks] = useState([]);
  const [parksLoaded, setParksLoaded] = useState(false);
  const [park, setPark] = useState(null);
  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });
  const [graphData2, setGraphData2] = useState({
    labels: [],
    datasets: []
  });


  useEffect(() => {
    fetchParks();
  });


  const fetchParks = () => {

    DataAPI({
      'endpoint': 'security/authenticate/location',
      'method': 'GET'
    }).then(
      response => {
        
        if (!parksLoaded) {
          setParks(response);
          setParksLoaded(true);
        }

      }
    );

  }

  const fetchData = (options = {}) => {

    const {period, generator, filter, windSectors} = options

    setLoading(true);

    const body = {}

    if (generator) body.generators = [generator];
    if (windSectors.length > 0) body.angles = windSectors
    if (period && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }


    DataAPI({
      'endpoint': 'chart/powerCurve',
      'method': 'POST',
      'body': body
    }).then(
      response => {


        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

          const generatorData = response.generator && response.generator[0].data;

          setDataLoaded(true);
          setData(response);
          

          if(generatorData && generatorData.hasOwnProperty('curves')){
            setCurves(generatorData.curves);
            refreshChart(generatorData);
          } else {
            setCurves([]);
          }

          if (generatorData){
            if(generatorData.hasOwnProperty('dataUse')) processTableData1(generatorData.dataUse);
            if(generatorData.hasOwnProperty('dataFilteredDueTo')) processTableData2(generatorData.dataFilteredDueTo);
            if(generatorData.hasOwnProperty('performance')) setPerformance(generatorData.performance);
          }
      }
    );


  }

  

  const filterSubmit = () => {

    setDataLoaded(false)
    setFiltersSelected(true);
    // fetchData({generator: generator, period: dateRange, windSectors: windSectors})
  
  }



  return (
    <CCard>
      <CCardHeader>
        <CRow>
          <CCol sm="6">
            <h3 id="traffic" className="card-title mb-0">
              Budget
            </h3>
            {/* <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div> */}
          </CCol>
          </CRow>
      </CCardHeader>
      <CCardBody>

        <CRow className={"py-3 mb-4 mx-0 bg-light"} style={{borderRadius:"3px"}}>
          <CCol xl="9" md="10" className="">

            <CRow className={"w-100"}>
              <CCol xl="3" md="6" className="d-flex flex-center px-md-1 py-2 pl-4 pr-0">
                  <h6 className="mr-2 ml-md-4 m-0 col-md-5 text-right col-4 px-0 px-sm-1" style={{minWidth:'64px'}}>Fiscal Year</h6>
                  <CSelect disabled={loading} custom name="fiscalYear" id="fiscalYear" className="col-md-7" onChange={(ev) => {setFiscalYear(ev.target.value);}}>
                      {[0,1,2,3,4].map((x) => (
                                        <option value={currentYear-x}>
                                          {currentYear-x}
                                        </option>
                                      ))}</CSelect>
              </CCol>

              <CCol xl="5" md="6" className="d-flex flex-center px-md-1 py-2 pl-4 pr-0">
                    <h6 className="mr-2 ml-md-4 m-0 col-md-8 text-right col-4 px-0 px-sm-1">Add Estimated Curtailments</h6>
                    <CSelect disabled={loading} custom name="addEstimatedCurtailments" id="addEstimatedCurtailments" className="col-md-4" onChange={(ev) => {setAddEstimatedCurtailments(ev.target.value);}}>
                      <option value="1">Yes</option>
                      <option value="0">No</option>
                    </CSelect>
              </CCol>

              <CCol xl="4" md="9" className="d-flex flex-center px-md-1 py-2 pl-4 pr-0">
                <h6 className="mr-2 ml-md-4 m-0 col-md-5 text-right col-4 px-0 px-sm-1" style={{minWidth:'64px'}}>Park</h6>
                <CSelect disabled={loading} custom name="park" id="park" className="col-md-7" onChange={(ev) => {setPark(ev.target.value);}}>
                    {parks && parks.map((park) => (
                                  <option key={park.id} value={park.id}>
                                    {park.name}
                                  </option>
                                    ))}</CSelect>
              </CCol>
            </CRow>
          </CCol>

          <CCol xl="3" md="2" className="text-right d-flex flex-center flex-justify-end px-4 py-2">
            <CButton color="primary" className="mx-2" onClick={() => filterSubmit()} >Submit</CButton>
          </CCol>
        </CRow>











      </CCardBody>
    </CCard>
  )
}

export default Budget
