import React, { useState, useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CAlert,
  CRow
} from '@coreui/react'
import {
  CChartBar,
  CChartLine,
  CChartDoughnut,
  CChartRadar,
  CChartPie,
  CChartPolarArea
} from '@coreui/react-chartjs'
import { DocsLink } from 'src/reusable'

const AvailabilityAndLosses = () => {

  const [period, setPeriod] = useState('cm');
  const [groupBy, setGroupBy] = useState('day');
  const [generators, setGenerators] = useState([]);
  const [generator, setGenerator] = useState(null);
  const [generatorsLoaded, setGeneratorsLoaded] = useState(false);

  const [dataLoaded, setDataLoaded] = useState(false);

  const [loading, setLoading] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);

  const [tableData, setTableData] = useState([]);
  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });
  const [graphData2, setGraphData2] = useState({
    labels: [],
    datasets: []
  });
  
  const colors =  ['#003f5c', '#7a5195', '#bc5090', '#ef5675', '#ff764a', '#ffa600','#9ceb01']

  const fetchData = () => {

    const body = {}
    body.location = getCookie('location');
    if (generator && generator !== '0') body.generators = [generator];
    
    if (period && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }
    body.groupBy = groupBy

    setLoading(true);

    DataAPI(
      {
      'endpoint': 'chart/availabilityAndL',
      'body': body
    }).then(function (responseData) {

      setLoading(false);
      setDataLoaded(true);
      if (responseData.error) {
        setDataLoadError(true);
          if (responseData.error.message) {
          return(alert(responseData.error.message))
          } else {
          return(alert(responseData.error)) 
          }
      }

      const data = {
        labels: [],
        datasets: []
      }

      responseData.data.forEach((rD,index) => {
        const rDfrom = formatDate(rD.from);
        const rDto = formatDate(rD.to);
        const label = (groupBy == "day") ? `${rDfrom}` : `${rDfrom} - ${rDto}`
        data.labels.push(label);
      });

      responseData.dataKeys.forEach((dK,index) => {
        let color = colors[index%colors.length];
        if (dK == 'missing') color = "#d9d9d9"
        else if (dK == 'other') color = "#7c7c7c"
        if (dK != 'total' && dK != 'turbineOK') {
          const dataset = {
            label:  ((dK != 'missing' && dK != 'other') ? round(dK,0) + " - " : "" ) + responseData.dataKeysDescription[index],
            backgroundColor: color,
            data: []
          };
          responseData.data.forEach((rD,index2) => {
            rD.alarms.forEach((alarm,index3) => {
              if (index == index3) {
                dataset.data.push(alarm);
              }
            });
          });
          data.datasets.push(dataset);
        }
      });

      setGraphData(data);

    }).catch(
      e => {
        setLoading(false);
        alert(e);
      }
    );;
  

    
    DataAPI(
      {
      'endpoint': 'chart/alarmsGenerator',
      'body': body
    }).then(function (responseData) {

      setLoading(false);
      if (responseData.error) {
        setDataLoadError(true);
          if (responseData.error.message) {
          return(alert(responseData.error.message))
          } else {
          return(alert(responseData.error)) 
          }
      }

      const data = {
        labels: [],
        datasets: []
      }

      responseData.data.forEach((rD,index) => {
        const rDfrom = formatDate(rD.from);
        const rDto = formatDate(rD.to);
        const label = (groupBy == "day") ? `${rDfrom}` : `${rDfrom} - ${rDto}`
        data.labels.push(label);
      });

      responseData.dataKeys.forEach((dK,index) => {
        if (dK != 'total' && dK != 'turbineOK') {
          const dataset = {
            label: responseData.dataKeysCode[index],
            backgroundColor: colors[index%colors.length],
            data: []
          };
          responseData.data.forEach((rD,index2) => {
            rD.alarms.forEach((alarm,index3) => {
              if (index == index3) {
                dataset.data.push(alarm);
              }
            });
          });
          data.datasets.push(dataset);
        }
      });

      setGraphData2(data);

    });

  }

  useEffect(() => {
    loadGenerators();
  }, []);


  const loadGenerators = () => {
    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        if (response && response.error) {
          setCookie('lastTimeStamp', '');
          setCookie('name', '');
          window.location.reload();
        }
        else if (!dataLoaded && response && !response.error) {
          if (response.generators != null)
            setGenerators(response.generators);
          setGeneratorsLoaded(true)
        }
      }
    );
  }


  // const optionsStackedBar = {
  //   responsive: true,
  //   scales: {
  //     x: {
  //       stacked: true,
  //     },
  //     y: {
  //       stacked: true,
  //     },
  //   },
  //   plugins: {
  //     tooltip: {
  //       callbacks: {
  //         label: function(tooltipItem, data) {
  //           return tooltipItem.dataset.label + ": " + round(tooltipItem.raw,1) + ' hours';
  //         }
  //       }
  //     }
  //   },
  // };

  return (
    <CCard>
      <CCardHeader>
        <CRow>
          <CCol sm="3">
            <h3 id="traffic" className="card-title mb-0">
              Availability and Losses
            </h3>
            <div className="small text-medium-emphasis">{getDateLabel(period)}</div>
          </CCol>
          
          <CCol sm="9" className="text-right d-flex flex-center flex-justify-end">
              <h6 className="mr-2 ml-4 mb-0" style={{lineHeight:2.4,minWidth:'64px'}}>Turbine</h6>
              <CSelect className={'input-md'} disabled={loading} onChange={(ev) => {setGenerator(ev.target.value); }} custom name="generator" id="generator">
                <option value="0">All turbines</option>
              {generators.map((oGenerator) => (
                                  <option value={oGenerator.id}>
                                    {oGenerator.code}
                                  </option>
                                ))}</CSelect>
              <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4,minWidth:'60px'}}>Group by</h6>
              <CSelect className={'input-md'} value={groupBy} disabled={loading} onChange={(ev) => { setGroupBy(ev.target.value); }} custom name="groupby" id="groupby">
                  <option value="day" selected>Day</option>
                  <option value="week">Week</option>
                  <option value="month">Month</option>
              </CSelect>
              <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4}}>Period</h6>
            <DateFilter value={period} options={['y','cm','cy','x','xx']} disabled={loading} onChange={(value) => { setPeriod(value);}} />
            <CButton color="primary" disabled={loading || !generatorsLoaded} className="mr-2 ml-3" onClick={() => { fetchData()}} >Submit</CButton>
          </CCol>
        </CRow>
      </CCardHeader>
      <CCardBody>
        
              <CAlert color="primary">Coming Soon</CAlert>
      </CCardBody>
    </CCard>
  )
}

export default AvailabilityAndLosses
