import React, { useState, useEffect } from 'react'
import {
  TheContent,
  TheSidebar,
  TheFooter,
  TheHeader
} from './index'
import {getCookie} from '../helpers/sessionCookie.js'
import { useLocation } from "react-router-dom";


const TheLayout = () => {

  const [parkType, setParkType] = useState('wind');
  const location = useLocation()

  useEffect(() => {
    fetchParks();
  });
  
  setParkType(getCookie('parkType'))

  if (location.pathname != '/modules/solar-battery') {
    setParkType('solar')
  }

  return (
    <div className={"c-app c-default-layout "+parkType}>
      <TheSidebar/>
      <div className="c-wrapper">
        <TheHeader/>
        <div className="c-body">
          <TheContent/>
        </div>
        <TheFooter/>
      </div>
    </div>
  )
}

export default TheLayout
