import React, { useState, useEffect } from 'react'

import {
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CButtonGroup,
  CProgress,
  CCardFooter,
  CFormSelect,
  CDataTable,
  CBadge,
  CSelect,
  CModal,
  CModalHeader,
  CModalFooter,
  CModalBody,
  CModalTitle,
  CSpinner
} from '@coreui/react'

import {
  CCard,
} from '@coreui/react'

import {
  CChartBar,
  CChartLine,
  CChartDoughnut,
  CChartRadar,
  CChartPie,
  CChartPolarArea
} from '@coreui/react-chartjs'

import { Line } from 'react-chartjs-2';

import DataAPI from '../../../helpers/DataAPI.js'

import {DateFilter, formatDate, round, getDateLabel} from '../../../helpers/utils.js'

import ChartLoading from '../../others/ChartLoading.js'

const Charts = () => {

  const [tableData1, setTableData1] = useState([]);
  const [tableFields1, setTableFields1] = useState([]);
  const [tableData2, setTableData2] = useState([]);
  const [tableFields2, setTableFields2] = useState([]);
  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState();
  const [curves, setCurves] = useState([]);
  const [dateRange, setDateRange] = useState('cm');
  const [labels, setLabels] = useState([]);
  const [generator, setGenerator] = useState('');
  const [generators, setGenerators] = useState([]);
  const [performance, setPerformance] = useState({});
  const [performanceItems, setPerformanceItems] = useState([]);

  const [graphOptions, setGraphOptions] = useState({});

  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });
  const [graphData2, setGraphData2] = useState({
    labels: [],
    datasets: []
  });


  useEffect(() => {
    loadGenerators();
  }, []);


  const fetchData = (options = {}) => {

    const {period, generator} = options

    setLoading(true);

    const body = {}

    if (generator) body.generators = [generator];

    // console.log('.....', period, period.split('-').length)

    if (period && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }


    DataAPI({
      'endpoint': 'chart/powerCurve',
      'method': 'POST',
      'body': body
    }).then(
      response => {


        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

          // if (!response.generator) return(alert('No generator datasets'));


          const generatorData = response.generator && response.generator[0].data;

          setDataLoaded(true);
          setData(response);
          

          if(generatorData && generatorData.hasOwnProperty('curves')){
            setCurves(generatorData.curves);
            refreshChart(generatorData.curves);
          } else {
            setCurves([]);
          }

          if (generatorData){
            if(generatorData.hasOwnProperty('dataUse')) processTableData1(generatorData.dataUse);
            if(generatorData.hasOwnProperty('dataFilteredDueTo')) processTableData2(generatorData.dataFilteredDueTo);
            if(generatorData.hasOwnProperty('performance')) setPerformance(generatorData.performance);
          }
      }
    );


  }


  const loadGenerators = () => {

    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        
        if (!dataLoaded && response  && !response.error) {

          setGenerator(response.generators[0].id);
          setGenerators(response.generators);

          fetchData({period: dateRange,  generator: response.generators[0].id});

        }
      }
    );


  }


    const processTableData1 = (dataUse) => {

      const tableData = [];

      dataUse.forEach(
        item => {

          const appendSymbol = (item.info=='As percentage of time' || item.info=='As percentage of MWh')?'%':'';

          if (item.info == 'As MWh generated') {

            var usedData = (Math.round(item.usedData)+appendSymbol).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ".");
            var filteredData = (Math.round(item.filteredData)+appendSymbol).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ".");
            var total = (Math.round(item.total)+appendSymbol).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ".");

          } else {

            var usedData = (round(item.usedData)+appendSymbol).replace('.',',');
            var filteredData = (round(item.filteredData)+appendSymbol).replace('.',',');
            var total = (round(item.total)+appendSymbol).replace('.',',');

          }

          

          tableData.push({
            '': item.info,
            'Used Data': usedData,
            'Filtered Data': filteredData,
            'Total': total
          })

        }
      );

      setTableData1(tableData);
      setTableFields1(['', 'Used Data', 'Filtered Data', 'Total']);

    }


    const processTableData2 = (dataFilteredDueTo) => {

      const tableData = [];

      tableData.push({
            '': dataFilteredDueTo.info,
            'Missing Data': round(dataFilteredDueTo.missingData)+'%',
            'Curtailment': round(dataFilteredDueTo.curtailment)+'%',
            'Other': round(dataFilteredDueTo.other)+'%',
            'Total': round(dataFilteredDueTo.total)+'%'
          })

      setTableData2(tableData);
      setTableFields2(['', 'Missing Data', 'Curtailment', 'Other', 'Total']);
      
    }

    const refreshChart = (curves) => {


          if (curves.length==0) return;
          

            const graphData = {
              labels: [],
              datasets: [],
              backgroundColor: ["red", "blue", "green", "blue", "red", "blue"]
            }

          
            const dataset = {
              title: '',
              labels: [],
              label: "Reference Power Curve",
              backgroundColor: 'red',
              data: []
            };
 
            const dataset2 = {
              labels: [],
              label: "Turbine Power Curve",
              backgroundColor: 'blue',
              data: []
            };

            const dataset3 = {
              labels: [],
              label: "Time percentage",
              backgroundColor: 'blue',
              data: []
            };

            const labels = [];

            const labels2 = [];


            const graphData2 = {
              labels: [],
              datasets: [],
              backgroundColor: ["red", "blue", "green", "blue", "red", "blue"]
            }


            curves[0].x.forEach(
              x => {
                labels.push(round(x))
              }
            );

            dataset.data = curves[0].y;
            dataset2.data = curves[1].y;


            const max = Math.ceil(Math.max(...curves[2].x));
            const windSpeeds = Array(max - 1 + 1).fill().map((_, idx) => 1 + idx)
            const percentages = [];
            let aux;
            let qty;
            let percentage;
            const qtyTotal = curves[2].x.length;
            windSpeeds.forEach(
              function process(windSpeed) { 
                aux = curves[2].x.filter(x => x < windSpeed && x > windSpeed-1);
                qty = aux.length;
                percentage = (Math.round((qty*100/qtyTotal) * 100) / 100).toFixed(2);
                percentages.push(percentage);
                graphData2.labels.push(windSpeed-1 + " - " + windSpeed);
              }
            );

            dataset3.data = percentages;


            graphData.datasets.push(dataset);
            graphData.datasets.push(dataset2);
            graphData2.datasets.push(dataset3);

            graphData.labels = labels;

            setGraphData(graphData);

            setGraphData2(graphData2);


    }


    const getBadge = status => {
  switch (status) {
    case 'Active': return 'success'
    case 'Inactive': return 'secondary'
    case 'Pending': return 'warning'
    case 'Banned': return 'danger'
    default: return 'primary'
  }


  setGraphOptions({
                animation: {
        duration: 0
    },
                responsive: true,
                scales: {
                  x: {
                    display: true,
                    title: {
                      display: true,
                      text: 'Month',
                      color: '#911',
                      font: {
                        family: 'Comic Sans MS',
                        size: 20,
                        weight: 'bold',
                        lineHeight: 1.2,
                      },
                      padding: {top: 20, left: 0, right: 0, bottom: 0}
                    }
                  },
                  y: {
                    display: true,
                    title: {
                      display: true,
                      text: 'Value',
                      color: '#191',
                      font: {
                        family: 'Times',
                        size: 20,
                        style: 'normal',
                        lineHeight: 1.2
                      },
                      padding: {top: 30, left: 0, right: 0, bottom: 0}
                    }
                  }
                }
              });
}


const performanceFields = [
  'a', 'b', 'c'
];

const fields = [
  '',
  'Used Data',
  'Filtered Data',
  'Total'
]

const options = {animation: {duration: loading ? 0 : 1000}};

  return (
    <CCard>

    <CCardHeader>
          <CRow>
            <CCol sm="6">
              <h3 id="traffic" className="card-title mb-0">
                Power Curve
              </h3>
              <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div>
            </CCol>
            {/* <CCol sm="6" className="text-right d-flex flex-center">
              <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4,minWidth:'64px'}}>Generator</h6>
              <CSelect disabled={loading} onChange={(ev) => {setGenerator(ev.target.value); fetchData({period: dateRange, generator: ev.target.value});}} custom name="generator" id="generator">

              {generators.map((oGenerator) => (
                                  <option value={oGenerator.id}>
                                    {oGenerator.code}
                                  </option>
                                ))}</CSelect>
              <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4}}>Period</h6>
              <DateFilter value={dateRange} options={['cm','cy','12w','12m','x','xx']} disabled={loading} onChange={(value) => { setDateRange(value); fetchData({generator: generator, period: value});}} />

            </CCol> */}
           </CRow>


        </CCardHeader>
      
      <CCardBody>


        <CRow className={"py-3 mb-4 mx-2 bg-light"} style={{borderRadius:"3px"}}>
          <CCol sm="9">

            <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4,minWidth:'64px'}}>Generator</h6>
            <CSelect disabled={loading} custom name="generator" id="generator">

            {generators.map((oGenerator) => (
                                <option value={oGenerator.id}>
                                  {oGenerator.code}
                                </option>
                              ))}</CSelect>
            <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4}}>Period</h6>
            <DateFilter value={dateRange} options={['cm','cy','12w','12m','x','xx']} disabled={loading} onChange={(value) => { setDateRange(value); fetchData({generator: generator, period: value});}} />

            <CSelect onChange={(ev) => {setGenerator(ev.target.value); fetchData({period: dateRange, generator: ev.target.value});}} custom name="generator" id="generator">

{generators.map((oGenerator) => (
                    <option value={oGenerator.id}>
                      {oGenerator.code}
                    </option>
                  ))}</CSelect>
            Filter:
i. Curtailments
ii. Alarms
iii. Other (Frozen sensors, outliers)

          {generatorsLoaded && 
            generators.map((gen, index) => (  
                <CButton 
                  style={{backgroundColor:generatorColors[gen.id],color: 'white'}} 
                  className={(selectedGenerators.includes(gen.id) ? "selected" : "") + "btn-generator mx-2"}
                  onClick={() => selectGenerator(gen.id)} 
                  id={gen.id}
                  >
                    {gen.name}
                </CButton>
            ))
          }
          </CCol>
          <CCol sm="3" className="text-right">
            <CButton color="primary" className="mx-2" onClick={() => filterGenerators()} >Submit</CButton>
          </CCol>
        </CRow>
        
        <CRow>
          <CCol sm="7">
            <div style={{marginBottom:'50px'}}>
              <div className="left-center" style={{width: '100%'}}>Power (KWh)</div>
              <Line data={graphData} options={options} />
              <div className="text-center" style={{width: '100%'}}>Wind speed (m/s)</div>
            </div>

            <div style={{marginBottom:'50px'}}>
              <h4 className="pb-2 mb-4 border-bottom" >Operational data</h4>
              <div className="left-center" style={{width: '100%'}}>Time (%)</div>
              <CChartBar
                datasets={graphData2.datasets}
                labels={graphData2.labels}
                options={{
                  tooltips: {
                    enabled: true
                  }
                }}
              />
              <div className="text-center" style={{width: '100%'}}>Wind speed (m/s)</div>
            </div>

          </CCol>
          <CCol sm="5">


            % data above Reference Power Curve: {round(performance['Percentage data above manufacturer power curve']) }%<br />
            % data below Reference Power Curve: {round(performance['Percentage data below manufacturer power curve']) }%<br />
            Performance index {round(performance['Performance index'])}%<br /><br />



              <h4>Data Use</h4>

              <div style={{textAlign:'right'}}>
                  {dataLoaded || dataLoadError
                    ? <CDataTable
                    items={tableData1}
                    fields={tableFields1}
                    striped
                    itemsPerPage={5}
                    pagination
                    scopedSlots = {{
                      'status':
                        (item)=>(
                          <td>
                            <CBadge color={getBadge(item.status)}>
                              {item.status}
                            </CBadge>
                          </td>
                        )
  
                    }}
                  />
                    : 
                    <ChartLoading/>
                
              }
              </div>

              <div style={{textAlign:'right'}}>
              <h4>Reasons for Filtered Data</h4>

              {dataLoaded || dataLoadError
                    ? 
                    <CDataTable
                    items={tableData2}
                    fields={tableFields2}
                    striped
                    itemsPerPage={5}
                    pagination
                    scopedSlots = {{
                      'status':
                        (item)=>(
                          <td>
                            <CBadge color={getBadge(item.status)}>
                              {item.status}
                            </CBadge>
                          </td>
                        )

                    }}
                  />
                  : 
                  <ChartLoading/>
              } 
              </div>

          </CCol>
        </CRow>

      </CCardBody>



    </CCard>
  )
}

export default Charts
