  CRow,
  CCol,
  CButton,
  CSelect,
  CSpinner,
  Fade
} from '@coreui/react'

import CIcon from '@coreui/icons-react'

import DataAPI from '../../../helpers/DataAPI.js'

import {
  CChartBar,
  CChartLine,
  CChartDoughnut,
  CChartRadar,
  CChartPie,
  CChartPolarArea
} from '@coreui/react-chartjs'
import { DocsLink } from 'src/reusable'

import {formatDate, round, getDateLabel, DateFilter, formatNumber} from '../../../helpers/utils.js'
import {setCookie,getCookie} from '../../../helpers/sessionCookie.js'

import { Line } from 'react-chartjs-2';
import { generatePath } from 'react-router-dom/cjs/react-router-dom.min';

const rand = () => {
  return 2;
}

const ctx = document.getElementById('x');

const Charts = () => {


  const [graphData, setGraphData] = useState({ labels: [], datasets: [] });
  const [graphData2, setGraphData2] = useState({labels: [],datasets: [] });
  const [graphData3, setGraphData3] = useState({labels: [],datasets: [] });
  const [generator, setGenerator] = useState(null);
  const [generators, setGenerators] = useState([]);
  const [generatorColors, setGeneratorColors] = useState([]);
  const [generatorsLoaded, setGeneratorsLoaded] = useState(false);
  const [generatorsSelected, setGeneratorsSelected] = useState(false);
  const [selectedGenerators, setSelectedGenerators] = useState([]);
  const [park, setPark] = useState();
  const [date, setDate] = useState();
  const [dateRange, setDateRange] = useState('12w');
  const [groupBy, setGroupBy] = useState('week');
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [generatorsButtons, setGeneratorsButtons] = useState(null);
  const colors =  ['#003f5c', '#7a5195', '#bc5090', '#ef5675', '#ff764a', '#ffa600','#9ceb01']

  const fetchData = (options = {}) => {

    const {period, groupBy} = options

    setLoading(true);

    const body = {}
    body.location = getCookie('location')
    body.generators = selectedGenerators;
    if (period && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }
    body.groupBy = groupBy

    DataAPI({
      endpoint: 'chart/performanceIndex',
      method: 'POST',
      body: body
    })
      .then(function (responseData) {

        setLoading(false);

        if (responseData.error) {
          setDataLoadError(true);
          if (responseData.error.message) {
          return(alert(responseData.error.message))
          } else {
          return(alert(responseData.error)) 
          }
        }

        const data = {
          labels: [],
          datasets: []
        }
        const data2 = {
          labels: [],
          datasets: []
        }
        const data3 = {
          labels: [],
          datasets: []
        }


        responseData.data.forEach((rDs) => {

          const dataset = {
            labels: [],
            label: rDs.code,
            backgroundColor: generatorColors[rDs.code],
            data: [],
          };
          const dataset2y = {
            labels: [],
            label: "Production: "+rDs.code,
            backgroundColor: generatorColors[rDs.code],
            borderColor: generatorColors[rDs.code],
            borderWidth: 1,
            pointBorderColor: generatorColors[rDs.code],
            pointBorderWidth: 2,
            data: [],
            yAxisID: 'y',
            pointRadius: 4,
            pointHoverRadius: 5,
          };
          const dataset2y1 = {
            labels: [],
            label: "Wind speed: "+rDs.code,
            borderColor: generatorColors[rDs.code],
            borderWidth: 1,
            pointBorderColor: generatorColors[rDs.code],
            pointBorderWidth: 2,
            pointBackgroundColor: '#d8dbe0',
            data: [],
            yAxisID: 'y1',
            pointRadius: 4,
            pointHoverRadius: 5,
          };

          const dataset3 = {
            labels: [],
            label: rDs.code,
            backgroundColor: generatorColors[rDs.code],
            data: [],
          };

          rDs.data.graph.forEach((rD) => {
            const label = (groupBy == "day") ? `${formatDate(rD.from)}` : `${formatDate(rD.from)} - ${formatDate(rD.to)}`
            dataset.labels.push(label)
            dataset2y.labels.push(label)
            dataset2y1.labels.push(label)
            dataset3.labels.push(label)
            dataset.data.push(rD.performance)
            dataset2y.data.push(rD.power/1000)
            dataset2y1.data.push(rD.windspeed)
            dataset3.data.push(rD.availability)

            if (rDs.name == "Total" && rDs.id.length > 1) {
              dataset.label = "Total"
              dataset2y.label = "Production: Total"
              dataset2y1.label = "Wind Speed: Total"
              dataset3.label = "Total"
              dataset.backgroundColor = generatorColors[rDs.name]
              dataset2y.backgroundColor = generatorColors[rDs.name]
              dataset2y.borderColor = generatorColors[rDs.name]
              dataset2y1.borderColor = generatorColors[rDs.name]
              dataset3.backgroundColor = generatorColors[rDs.name]
            }
          });

          dataset.labels.reverse();
          dataset.data.reverse();
          dataset2y.labels.reverse();
          dataset2y.data.reverse();
          dataset2y1.labels.reverse();
          dataset2y1.data.reverse();
          dataset3.labels.reverse();
          dataset3.data.reverse();

          if (rDs.name != "Total" || rDs.id.length > 1) {
            data.datasets.push(dataset);
            data2.datasets.push(dataset2y);
            data2.datasets.push(dataset2y1);
            data3.datasets.push(dataset3);
          }

        });

        responseData.data[0].data.graph.forEach((rD) => {
          const rDfrom = rD.from;
          const rDto = rD.to;
          const label = (groupBy == "day") ? `${formatDate(rD.from, 'noyear')}` : `${formatDate(rD.from, 'noyear')} - ${formatDate(rD.to, 'noyear')}`
          data.labels.push(label);
          data2.labels.push(label);
          data3.labels.push(label);

        });

        data.labels.reverse();
        data2.labels.reverse();
        data3.labels.reverse();

        setGraphData(data);
        setGraphData2(data2);
        setGraphData3(data3);
        setDataLoaded(true);

      })
      .catch(
        err => {
          console.warn(`ERROR! ${err}`)
          setLoading(false);
        }
      );

  }

  useEffect(() => {
    loadGenerators();
  }, []);
  
  const loadGenerators = () => {
  
    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        
        if (response && response.error) {
          setCookie('lastTimeStamp', '');
          setCookie('name', '');
          window.location.reload();
        }
        else if (!dataLoaded && response  && !response.error) {
          
          if (response.generators != null) {
            setGenerators(response.generators);
          
          
            let colorIndex = 0
            response.generators.forEach((gen) => {
              generatorColors[gen.code] = colors[colorIndex%colors.length];
              generatorColors["Total"] = "#010023";
              setGeneratorColors(generatorColors);
              colorIndex++
            });
          }

          setGeneratorsLoaded(true);
  
        }
      }
    );
  }

  const options = {
    responsive: true,
    animation: {duration: loading ? 0 : 1000},
    // interaction: {
    //   mode: 'index',
    //   intersect: false,
    // },
    plugins: {
      tooltip: {
        callbacks: {
          label: function(tooltipItem, data) {
              return tooltipItem.dataset.label + ": " + round(tooltipItem.raw,1)+"%";
          }
        }
      }
    },
  };

  const options2 =  {
    responsive: true,
    animation: {duration: loading ? 0 : 1000},
    // interaction: {
    //   mode: 'index',
    //   intersect: false,
    // },
    stacked: false,
    plugins: {
      tooltip: {
        callbacks: {
          label: function(tooltipItem, data) {
            if (tooltipItem.dataset.yAxisID == "y")
              return tooltipItem.dataset.label + ": " + formatNumber(round(tooltipItem.raw,1)) + ' MW';
            else
              return tooltipItem.dataset.label + ": " + formatNumber(round(tooltipItem.raw,1)) + ' m/s';
          }
        }
      },
      legend: {
        lineWidth: 4,
        borderRadius: 10,
        usePointStyle: true,
        pointStyle: {
          borderWidth: 5,
          pointBorderWidth: 5,
        }
      },
    },
    scales: {
      y: {
        type: 'linear',
        display: true,
        position: 'left',
      },
      y1: {
        type: 'linear',
        display: true,
        position: 'right',
        grid: {
          drawOnChartArea: false, // only want the grid lines for one axis to show up
        },
      },
    },
  };

  const selectGenerator = (id) => {

    let newSelected = selectedGenerators;
    newSelected = newSelected.includes(id)
                          ? newSelected.filter(i => i !== id) // remove item
                          : [ ...newSelected, id ]; // add item
    setSelectedGenerators(newSelected);
  }

  const filterGenerators = () => {

    setGeneratorsSelected(true);
    fetchData({period: dateRange, groupBy: groupBy})

  }

  return (

        <CCard className="mb-4">
          <CCardHeader>
            <CRow>

              <CCol sm="7">
                <h3 id="traffic" className="card-title mb-0">
                  Performance
                </h3>
                <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div>
                </CCol>

                <CCol sm="5" className="text-right d-flex flex-center flex-justify-end">
                  <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4,minWidth:'60px'}}>Group by</h6>
                  <CSelect value={groupBy} style={{maxWidth:'150px'}} disabled={loading} onChange={(ev) => { setGroupBy(ev.target.value); }} custom name="period" id="period">
                    <option value="day">Day</option>
                    <option value="week" selected>Week</option>
                    <option value="month">Month</option>
                  </CSelect>
                  <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4}}>Period</h6>
                  <DateFilter value={dateRange} options={['y','cm','cy','x','xx']} disabled={loading} onChange={(value) => { setDateRange(value); }} />
                
                </CCol>
            </CRow>

          </CCardHeader>

          <CCardBody>

            <CRow className={"py-3 mb-4 mx-0 bg-light"} style={{borderRadius:"3px"}}>
              <CCol sm="9" className={"d-flex "} >
                <h6 className="mx-2 my-2 pt-1" style={{lineHeight:1.2,minWidth:'110px'}}>Select turbines:</h6>
              {generatorsLoaded && 
                generators.map((gen, index) => (  
                    <CButton 
                      style={{backgroundColor:generatorColors[gen.code],color: 'white'}} 
                      className={(selectedGenerators.includes(gen.id) ? "selected" : "") + "btn-generator mx-1 my-1"}
                      onClick={() => selectGenerator(gen.id)} 
                      id={gen.id}
                      >
                        {gen.code}
                    </CButton>
                ))
              }
              </CCol>
              <CCol sm="3" className="text-right d-flex flex-center flex-justify-end">
                <CButton disabled={selectedGenerators.length==0} color="primary" className="mx-2" onClick={() => filterGenerators()} >Submit</CButton>
              </CCol>
            </CRow>

            {generatorsSelected && 
            
              <div>
                {(!loading || dataLoadError) ?

                <div>
                  <div style={{marginBottom:'50px'}}>
                    <h4 className="pb-2 mb-4 border-bottom" >Performance Index</h4>
                    <div className="text-left float-left" style={{width: '50%'}}>%</div>
                    <div className="text-left" style={{width: '100%'}}></div>
                    <Line data={graphData} options={options} />
                    <CRow className="text-center d-block mt-2" style={{textTransform: 'capitalize'}}>{groupBy}</CRow>
                  </div>

                  <div style={{marginBottom:'50px'}}>
                    <h4 className="pb-2 mb-4 border-bottom" >Production and Wind Speed</h4>
                    <div className="text-left float-left" style={{width: '50%'}}>MWh</div>
                    <div className="text-right float-right" style={{width: '50%'}}>m/s</div>
                    <Line data={graphData2} options={options2} />
                    <CRow className="text-center d-block mt-2" style={{textTransform: 'capitalize'}}>{groupBy}</CRow>
                  </div>

                  <div style={{marginBottom:'50px'}}>
                    <h4 className="pb-2 mb-4 border-bottom" >Time-Based Availability</h4>
                    <div className="text-left" style={{width: '100%'}}>%</div>
                    <Line data={graphData3} options={options} />
                    <CRow className="text-center d-block mt-2" style={{textTransform: 'capitalize'}}>{groupBy}</CRow>
                  </div>
                </div>

                :
                <CRow style={{justifyContent:'center'}}>
                  <CSpinner 
                    className="loading-spinner"
                    color='#321fdb'
                  />
                </CRow>
                }
              </div>
            }
            
          </CCardBody>

        </CCard>
  )
}

export default Charts
