import React, { useState, useEffect } from 'react'

import {
  CButton,
  CCardGroup,
  CCard,
  CCardHeader,
  CCardBody,
  CCol,
  CForm,
  CFormGroup,
  CInput,
  CInputGroup,
  CInputGroupPrepend,
  CInputGroupText,
  CAlert,
  CLabel,
  CSelect,
  CRow,
} from '@coreui/react'

import CIcon from '@coreui/icons-react'

import DataAPI from '../../../helpers/DataAPI.js'


const Settings = () => {
  
  const [loading, setLoading] = useState(false);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [estimations, setEstimations] = useState([]);
  const [estimationsLoaded, setEstimationsLoaded] = useState(false);
  const [newPassword, setNewPassword] = useState('');
  const [newPasswordConfirm, setNewPasswordConfirm] = useState('');
  const [passwordChanged, setPasswordChanged] = useState(false);
  const [changePassword, setChangePassword] = useState(false);
  const [changing, setChanging] = useState(false);
  const [message, setMessage] = useState('');
  const [differentPassword, setDifferentPassword] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [preferencesChanged, setPreferencesChanged] = useState(false);
  const [fiscalYearEndMonth, setFiscalYearEndMonth] = useState(1);
  const [preferencesSaved, setPreferencesSaved] = useState(false);
  
  const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];

  useEffect(() => {
    loadUser();
    fetchEstimations();
  }, []);


  const loadUser = () => {

    setLoading(true);
    
    DataAPI({
      'endpoint': 'security/authenticate/current',
      'method': 'GET'
    }).then(
      response => {

        setLoading(false);

        if (response.error) {
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

        setName(response.name)
        setEmail(response.email)
        response.settings.forEach(setting => {
          if (setting.name === "fiscalYearEndMonth") setFiscalYearEndMonth(setting.value)
        });
        

      }
    );


  }


  const fetchEstimations = () => {

    DataAPI({
      'endpoint': 'admin/locations/estimation',
      'method': 'GET'
    }).then(
      response => {
        
        if (!estimationsLoaded) {
          setEstimations(response);
          setEstimationsLoaded(true);
        }
      }
    );

  }


  const enableSave = () => {

  }

  const saveNewPassword = () => {

    if (newPassword !== newPasswordConfirm) {
      setDifferentPassword(true);
    } else {
      setChanging(true);
      DataAPI({
        'endpoint': 'security/authenticate/reset',
        'method': 'POST',
        'body': {
          "newPassword": newPassword,
          "newPasswordConfirm": newPasswordConfirm
      }
      }).then(
        response => {

          if (response.changed) {          
            
            setPasswordChanged(true)
            setChangePassword(false)
            setMessage('Your password was updated.')

          } else {
            setErrorMessage(response.error)
            // alert(response.error)
          }

        }
      );
    }

  }


  const savePreferences = () => {
    
    setPreferencesChanged(false)

    const body = {}
    body.settings = [
      {"name":"fiscalYearEndMonth",
      "value":fiscalYearEndMonth
    }]

    DataAPI({
      'endpoint': 'security/authenticate/current',
      'method': 'POST',
      'body': body
    }).then(
      response => {

        if (response.error) {
           if (response.error.message) {
            return(alert(response.error.message))
           } else {
            return(alert(response.error)) 
           }
        }     
        
        setPreferencesSaved(true);
          
      }
    );

  }


  return (
    <div>
      <CRow>
        <CCol>
          <CCard sm="7" className="mb-4">
            <CCardHeader>
              <CRow>
                <CCol sm="6">
                  <h3 id="settings" className="card-title mb-0">
                    User settings
                  </h3>
                </CCol>
              </CRow>
            </CCardHeader>
            <CCardBody className={'px-md-5 pb-md-5 pt-md-4'}>
              <CRow className="">
                <CCol md="6" >
                  <CForm>
                    <CInputGroup className="mb-3">
                      <CInputGroupPrepend>
                        <CInputGroupText>
                          <CIcon name="cil-user" />
                        </CInputGroupText>
                      </CInputGroupPrepend>
                      <CInput onChange={enableSave} value={name} type="text" placeholder="Name" disabled />
                    </CInputGroup>
                    <CInputGroup className="mb-3">
                      <CInputGroupPrepend>
                        <CInputGroupText>
                          <CIcon name="cil-envelope-closed" />
                        </CInputGroupText>
                      </CInputGroupPrepend>
                      <CInput onChange={enableSave} value={email} type="text" placeholder="E-mail" disabled />
                    </CInputGroup>

                    { passwordChanged &&
                      <CAlert color="success" >{message}</CAlert>
                    }
                    { errorMessage !== '' &&
                      <CAlert color="danger" >{errorMessage}</CAlert>
                    }

                    { changePassword ?
                      <div>
                        <CInputGroup className="mb-3">
                          <CInputGroupPrepend>
                            <CInputGroupText>
                              <CIcon name="cil-lock-locked" />
                            </CInputGroupText>
                          </CInputGroupPrepend>
                          <CInput onChange={(ev) => {setDifferentPassword(false);setNewPassword(ev.target.value)}} type="password" placeholder="New Password" maxLength={100} />
                        </CInputGroup>
                        <CInputGroup className="mb-3">
                          <CInputGroupPrepend>
                            <CInputGroupText>
                              <CIcon name="cil-lock-locked" />
                            </CInputGroupText>
                          </CInputGroupPrepend>
                          <CInput onChange={(ev) => {setDifferentPassword(false);setNewPasswordConfirm(ev.target.value)}} type="password" placeholder="Confirm New Password" maxLength={100}  />
                          { differentPassword &&
                            <CLabel className={'text-danger mt-1 w-100'}>Please make sure your passwords match.</CLabel>
                          }
                        </CInputGroup>
                        <CRow>
                          <CCol xs="12">
                            <CButton onClick={saveNewPassword} color="primary" className="px-4 mr-3" disabled={newPassword==='' || changing}>Save new password</CButton>
                            <CButton onClick={() => setChangePassword(false)} color="secondary" className="px-4" disabled={changing}>Cancel</CButton>
                          </CCol>
                        </CRow>
                      </div>
                      :
                      <CRow className={'mt-4'}>
                        <CCol xs="12">
                          <CButton onClick={() => {setChangePassword(true); setPasswordChanged(false);} } color="primary" className="px-4 mr-3">Change Password</CButton>
                        </CCol>
                      </CRow>
                    }
                  </CForm>
                </CCol>
              </CRow>
            </CCardBody>
          </CCard>
        </CCol>
      </CRow>

      <CRow>
        <CCol>
          <CCard sm="7" className="mb-4">
            <CCardHeader>
              <CRow>
                <CCol sm="6">
                  <h3 id="settings" className="card-title mb-0">
                    Estimated MWh
                  </h3>
                </CCol>
              </CRow>
            </CCardHeader>
      
            <CCardBody className={'px-md-5 pb-md-5 pt-md-4'}>
              <CRow className="">
                <CCol md="6" >
                  <CForm>
                    <CFormGroup>
                      <table>
                        <tr>
                          <td></td>
                          {estimations && estimations.map((park) => (
                            <td colspan="2">{park.name}</td>
                          ))}
                        </tr>
                        <tr>
                          <td>Estimated title</td>
                          {estimations && estimations.map((park) => (
                            park.estimations.map((est) => (
                              <td><CInput value={est.title}/></td>        
                            ))
                          ))}
                        </tr>
                        { [...range(1, 12)].map((m) => (
                          <tr>
                            <td>{months[m-1]}</td>
                            {estimations && estimations.map((park) => {
                              let e = 0
                              park.estimations.map((est) => (
                                <td><CInput name={park.id+"-"+'month'+(m<10?"0"+m:m)+"-"} value={JSON.parse(est)['month'+(m<10?"0"+m:m)]}/></td>        
                              ))
                            })}
                          </tr>
                        ))}
                        <tr>
                          <td>Estimated title</td>
                          {estimations && estimations.map((park) => (
                            park.estimations.map((est) => (
                              <td><CInput value={est.title}/></td>        
                            ))
                          ))}
                        </tr>
                      </table>
                      <CRow>
                        <CCol lg="5" className="bg-success py-3">
                          
                        </CCol>
                       
                        <CCol md="4" className="bg-warning py-3">
                          col-md-4
                        </CCol>
                        <CCol sm="3" className="bg-danger py-3">
                          col-sm-3
                        </CCol>
                      </CRow>
                    </CFormGroup>
                    <CFormGroup className={"mt-4"}>
                      <CButton onClick={() => {savePreferences();} } color="primary" className="px-4 mr-3" disabled={!preferencesChanged}>Save Preferences</CButton>
                      { preferencesSaved && !preferencesChanged &&
                        <div className="text-success d-inline-block" style={{fontWeight:"500"}}>Saved!</div> 
                      }
                    </CFormGroup>
                  </CForm>
                </CCol>
              </CRow>
            </CCardBody>

          </CCard>
        </CCol>
      </CRow>

      <CRow>
        <CCol>
          <CCard sm="7" className="mb-4">
            <CCardHeader>
              <CRow>
                <CCol sm="6">
                  <h3 id="settings" className="card-title mb-0">
                    Fiscal Year settings
                  </h3>
                </CCol>
              </CRow>
            </CCardHeader>
      
            <CCardBody className={'px-md-5 pb-md-5 pt-md-4'}>
              <CRow className="">
                <CCol md="6" >
                  <CForm>
                    <CFormGroup>
                      <CInputGroup>
                        <CInputGroupPrepend>
                          <CInputGroupText >
                          Select Fiscal Year End
                          </CInputGroupText>
                        </CInputGroupPrepend>
                        <CSelect custom value={fiscalYearEndMonth} onChange={(ev) => { setPreferencesChanged(true); setFiscalYearEndMonth(ev.target.value)}} name="fiscalYearEnd" id="fiscalYearEnd" className={"input-md"} >
                          <option value="1">January</option>
                          <option value="2">February</option>
                          <option value="3">March</option>
                          <option value="4">April</option>
                          <option value="5">May</option>
                          <option value="6">June</option>
                          <option value="7">July</option>
                          <option value="8">August</option>
                          <option value="9">September</option>
                          <option value="10">October</option>
                          <option value="11">November</option>
                          <option value="12">December</option>
                        </CSelect>
                      </CInputGroup>
                    </CFormGroup>
                    <CFormGroup className={"mt-4"}>
                      <CButton onClick={() => {savePreferences();} } color="primary" className="px-4 mr-3" disabled={!preferencesChanged}>Save Preferences</CButton>
                      { preferencesSaved && !preferencesChanged &&
                        <div className="text-success d-inline-block" style={{fontWeight:"500"}}>Saved!</div> 
                      }
                    </CFormGroup>
                  </CForm>
                </CCol>
              </CRow>
            </CCardBody>

          </CCard>
        </CCol>
      </CRow>
    </div>

  )
}

export default Settings
