import React, {useEffect, useState} from 'react'

import {
  CBadge,
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CDataTable,
  CRow,
  CSelect,
  CSpinner,
  CB
} from '@coreui/react'

import {
  CChartBar,
  CChartLine,
  CChartDoughnut,
  CChartRadar,
  CChartPie,
  CChartPolarArea
} from '@coreui/react-chartjs'

import { DocsLink } from 'src/reusable'

import DataAPI from '../../../helpers/DataAPI.js'
import {formatDate, DateFilter, getDateLabel, round} from '../../../helpers/utils.js'
import {setCookie,getCookie} from '../../../helpers/sessionCookie.js'

import ChartLoading from '../../others/ChartLoading.js'

const getBadge = status => {
  switch (status) {
    case 'Active': return 'success'
    case 'Inactive': return 'secondary'
    case 'Pending': return 'warning'
    case 'Banned': return 'danger'
    default: return 'primary'
  }
}

const fields = [
  'from',
  'to',
  'metMastWindSpeed',
  'temperature',
  'metMastWindDirection',
  'turbineStatus',
  'turbineAlarmCode',
  'turbineWindDirection',
  'turbineWindPower',
  'turbineWindSpeed'
]




const Charts = () => {

  const [data, setData] = useState([]);
  const [tableData, setTableData] = useState([]);
  
  const [dateRange, setDateRange] = useState('y');
  const [groupBy, setGroupBy] = useState('week');
  const [generators, setGenerators] = useState([]);
  const [generator, setGenerator] = useState(null);

  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [loading, setLoading] = useState(false);



   const fetchData = () => {

    setLoading(true);


    const body = {}
    body.location = getCookie('location');
    if (generator && generator !== '0') body.generators = [generator];
    if (dateRange && dateRange.split('--').length==2) {
      body.from = dateRange.split('--')[0]
      body.to = dateRange.split('--')[1]
    } else {
      body.period = dateRange
    }
    body.groupBy = groupBy

    DataAPI({
      'endpoint': 'chart/dataAvailability',
      'body': body,
      'method': 'POST'
    }).then(
      response => {

        setLoading(false);


        if (response.error) {
          setDataLoadError(true);
           if (response.error.message) {
            return(alert(response.error.message))
           } else {
            return(alert(response.error)) 
           }
        }

          const tableData = [];
        if (response && response.data){

          response.data.table.forEach(
            row => {
              tableData.push({
                from: formatDate(row.from),
                to: formatDate(row.to),
                metMastWindDirection: round(row.metMast.direction)+'%',
                metMastWindSpeed: round(row.metMast.speed)+'%',
                temperature: round(row.metMast.temperature)+'%',
                turbineAlarmCode: round(row.turbineState.alarm)+'%',
                turbineStatus: round(row.turbineState.state)+'%',
                turbineWindDirection: round(row.windAverages.direction)+'%',
                turbineWindPower: round(row.windAverages.powerAverage)+'%',
                turbineWindSpeed: round(row.windAverages.speed)+'%',
              })
            }
          );

          setTableData(tableData);
          setDataLoaded(true);
        }
      }
    ).catch(
      e => {
        setLoading(false);
        alert(e);

      }
    );

   }


   useEffect(() => {
    loadGenerators();
  }, []);


   const loadGenerators = () => {

    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        
        if (response && response.error) {
          setCookie('lastTimeStamp', '');
          setCookie('name', '');
          window.location.reload();
        }
        else if (!dataLoaded && response  && !response.error) {

          setGenerators(response.generators);

          fetchData({period: dateRange, groupBy: groupBy});

        }
      }
    );


  }

  return (
          <>
          

      <CRow>

        <CCol xs="12" lg="12">
          <CCard>
            


      <CCardHeader>
          <CRow>
            <CCol sm="5">
              <h3 id="traffic" className="card-title mb-0">
                Data Availability
              </h3>
              <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div>
              </CCol>
            
            <CCol sm="7" className="text-right d-flex px-2 flex-center">
              <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4,minWidth:'64px'}}>Turbine</h6>
              <CSelect disabled={loading} onChange={(ev) => {setGenerator(ev.target.value); }} custom name="generator" id="generator">
                <option value="0">All turbines</option>
              {generators.map((oGenerator) => (
                                  <option value={oGenerator.id}>
                                    {oGenerator.code}
                                  </option>
                                ))}</CSelect>
              <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4,minWidth:'60px'}}>Group by</h6>
              <CSelect value={groupBy} disabled={loading} onChange={(ev) => { setGroupBy(ev.target.value); }} custom name="period" id="period">
                <option value="day">Day</option>
                <option value="week" selected>Week</option>
                <option value="month">Month</option>
              </CSelect>

              <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4}}>Period</h6>
              <DateFilter value={dateRange} options={['y','cm','cy','12w','12m','x','xx']} disabled={loading} onChange={(value) => { setDateRange(value);}} />
              <CButton color="primary" disabled={loading} className="mr-2 ml-3" onClick={() => { fetchData();}} >Submit</CButton>
            </CCol>
          </CRow>


        </CCardHeader>

            <CCardBody>

            <div style={{textAlign: 'right'}}>

            {dataLoaded || dataLoadError
                    ? 
                  <CDataTable
                    items={tableData}
                    fields={fields}
                    striped
                    itemsPerPage={50}
                    pagination
                    scopedSlots = {{
                      'status':
                        (item)=>(
                          <td>
                            <CBadge color={getBadge(item.status)}>
                              {item.status}
                            </CBadge>
                          </td>
                        )

                    }}
                  />
                  : 
                  <ChartLoading/>
            }
            </div>
            </CCardBody>
          </CCard>
        </CCol>
      </CRow>

    </>
  )
}

export default Charts
