import React, { useState, useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CAlert,
  CRow,
  Cc
} from '@coreui/react'
import {
  CChartBar,
  CChartLine,
  CChartDoughnut,
  CChartRadar,
  CChartPie,
  CChartPolarArea
} from '@coreui/react-chartjs'
import { DocsLink } from 'src/reusable'

const Budget = () => {

  return (
    <CCard>
      <CCardHeader>
        Budget
      </CCardHeader>
      <CCardBody>

        <CRow className={"py-3 mb-4 mx-0 bg-light"} style={{borderRadius:"3px"}}>
          <CCol xl="10" md="10" className="">

            <CRow className={"w-100"}>
              <CCol xl="3" md="6" className="d-flex flex-center px-md-1 py-2 pl-4 pr-0">
                  <h6 className="mr-2 ml-md-4 m-0 col-md-5 text-right col-4 px-0 px-sm-1" style={{minWidth:'64px'}}>Generator</h6>
                  <CSelect disabled={loading} custom name="generator" id="generator" className="col-md-7" onChange={(ev) => {setGenerator(ev.target.value);}}>
                      {generators.map((oGenerator) => (
                                        <option value={oGenerator.id}>
                                          {oGenerator.code}
                                        </option>
                                      ))}</CSelect>
              </CCol>

              <CCol xl="3" md="6" className="d-flex flex-center px-md-1 py-2 pl-4 pr-0">
                    <h6 className="mr-2 ml-md-4 m-0 col-md-5 text-right col-4 px-0 px-sm-1">Period</h6>
                    <div class="col-md-7 px-0">
                      <DateFilter disabled={loading} value={dateRange} options={['cm','30d','cy','12w','12m','x','xx']} onChange={(value) => {setDateRange(value);}}/>
                    </div>
              </CCol>

              <CCol xl="6" md="9" className="d-flex flex-center px-md-1 py-2 pl-4 pr-0">
                <h6 className="mr-2 ml-md-4 m-0 text-right col-4 col-md-3 px-0 px-sm-1">Wind sector</h6>
                  <div class="multiselect-container">
                    <Multiselect 
                      disabled={loading}
                      options={angles.map((angle) => {
                          return {id:angle,name:angle}
                        })}
                      onSelect={(selectedList) => {setWindSectors(selectedList.map((angle) => { 
                          const angleSplit = angle.id.split("-"); 
                          return {from:angleSplit[0], to:angleSplit[1]} 
                        } 
                      ))}}
                      onRemove={(selectedList) => {setWindSectors(selectedList.map((angle) =>{ 
                          const angleSplit = angle.id.split("-"); 
                          return {from:angleSplit[0], to:angleSplit[1]} 
                        } 
                      ))}}
                      displayValue="name"
                      />
                  </div>
              </CCol>
            </CRow>
          </CCol>

          <CCol xl="2" md="2" className="text-right d-flex flex-center flex-justify-end px-4 py-2">
            <CButton color="primary" className="mx-2" onClick={() => filterSubmit()} >Submit</CButton>
          </CCol>
        </CRow>











      </CCardBody>
    </CCard>
  )
}

export default Budget
