import React, { useState, useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CSelect,
  CSpinner,
  CDataTable,
  CContainer
} from '@coreui/react'

import DataAPI from '../../../../helpers/DataAPI.js'
import {round, formatNumber} from '../../../../helpers/utils.js'
import {getCookie, setCookie} from '../../../../helpers/sessionCookie.js'
import { Bar } from 'react-chartjs-2';

import * as FileSaver from 'file-saver'
import XLSX from 'sheetjs-style'
import { async } from 'regenerator-runtime'





const Budget = () => {

  const currentMonth = (new Date()).getMonth()
  const currentFiscalYear = currentMonth < getCookie('fiscalYearEndMonth') ? new Date().getFullYear() : (new Date().getFullYear())+1
  const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];

  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [fiscalYear, setFiscalYear] = useState(currentFiscalYear);
  const [addEstimatedCurtailments, setAddEstimatedCurtailments] = useState(true);
  const [parks, setParks] = useState([]);
  const [parksLoaded, setParksLoaded] = useState(false);
  const [park, setPark] = useState(null);
  const [actualProductionMwh, setActualProductionMwh] = useState(0);
  const [estimates, setEstimates] = useState([]);
  const [estimatesCumulative, setEstimatesCumulative] = useState([]);
  const [estimatesLabel1, setEstimatesLabel1] = useState("Estimate 1");
  const [estimatesLabel2, setEstimatesLabel2] = useState("Estimate 2");
  const [difference1, setDifference1] = useState([]);
  const [difference2, setDifference2] = useState([]);
  const [cumulativeDifference1, setCumulativeDifference1] = useState([]);
  const [cumulativeDifference2, setCumulativeDifference2] = useState([]);
  const [tableData1, setTableData1] = useState([]);
  const [tableData2, setTableData2] = useState([]);
  const [graphData1, setGraphData1] = useState({
    labels: [],
    datasets: []
  });
  const [graphData2, setGraphData2] = useState({
    labels: [],
    datasets: []
  });


  useEffect(() => {
    fetchParks();
  },[]);
  


  const dataTest = 
  [
    {
      'Nombre':'Nico',
      'Apellido':'Prueba'
    },
    {
      'Nombre':'Julio',
      'Apellido':'Prueba'
    }
  ];

  // const ExportExcel = ({ excelData, fileName }) => {
    const fileType = 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet;charset=UTF-8'
    const fileExtension = '.xlsx'
  
    const exportToExcel = async (excelData, fileName) => {
      // const ws = XLSX.utils.json_to_sheet(excelData);

      const today = new Date();
      const date = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate() + ' ' + today.getHours() + ':' + today.getMinutes();

      const wb = XLSX.utils.book_new();
      let rows = [
        [
          { v: "Budget", t: "s", s: { font: { bold: true, sz: 14 } } },
        ],
        [],
        [
          { v: "Date-Time of Report", t: "s", s: { font: { sz: 11 } } },
          { v: date, t: "s", s: { font: { sz: 11 } } },
        ],
        [],
        [
          { v: "Fiscal Year", t: "s", s: { font: { sz: 11 } } },
          { v: fiscalYear, t: "s", s: { font: { sz: 11 } } },
        ],
        [
          { v: "Add Estimated Curtailments", t: "s", s: { font: { sz: 11 } } },
          { v: addEstimatedCurtailments?'Yes':'No', t: "s", s: { font: { sz: 11 } } },
        ],
        [
          { v: "Park", t: "s", s: { font: { sz: 11 } } },
          { v: parks.filter(x => x.id == park)[0].name, t: "s", s: { font: { sz: 11 } } },
        ],
        [],
        [
          { v: "1 - Monthly Production", t: "s", s: { font: { bold: true, sz: 11 } } },
        ],
        [],
        [
          { v: "Month to Date", t: "s", s: { font: { bold: true, sz: 11 } } },
        ],
        [
          { v: "Actual Production (MWh)", t: "s", s: { font: { sz: 11 } } },
          { v: actualProductionMwh, t: "s", s: { font: { sz: 11 } } },
        ],
      ]
      const arrayEstimates = 
        estimates.map((x) => {
          return [
            { v: x.label, t: "s", s: { font: { sz: 11 } } },
            { v: x.estimateMwh, t: "s", s: { font: { sz: 11 } } },
          ]
        });
        rows = [...rows, ...arrayEstimates];


        // [
        //   { v: estimatesLabel1, t: "s", s: { font: { sz: 11 } } },
        //   { v: esti, t: "s", s: { font: { sz: 11 } } },
        // ],
        // [
        //   { v: "Revised P50 (MWh)", t: "s", s: { font: { sz: 11 } } },
        //   { v: actualProductionMwh, t: "s", s: { font: { sz: 11 } } },
        // ],

        



        
        
        

      // let row2 = [
      //   { v: "Courier: 24", t: "s", s: { font: { name: "Courier", sz: 24 } } },
      //   { v: "bold & color", t: "s", s: { font: { bold: true, color: { rgb: "FF0000" } } } },
      //   { v: "fill: color", t: "s", s: { fill: { fgColor: { rgb: "E9E9E9" } } } },
      //   { v: "line\nbreak", t: "s", s: { alignment: { wrapText: true } } },
      //       ]

      // const wb = { Sheets: { 'data': ws }, SheetNames: ['data'] }

      const ws = XLSX.utils.aoa_to_sheet(rows);
      XLSX.utils.book_append_sheet(wb, ws, "readme demo");

      const excelBuffer = XLSX.write(wb, { bookType: 'xlsx', type: 'array' });
      const data = new Blob([excelBuffer],  {type: fileType });
      FileSaver.saveAs(data, fileName + fileExtension);
      
    }
  // }



  const fetchParks = () => {

    DataAPI({
      'endpoint': 'security/authenticate/location',
      'method': 'GET'
    }).then(
      response => {

        if (response.error) {
          setDataLoadError(true);
          if (response.status == 401) {
            setCookie('name', ''); 
            setCookie('parkType', ''); 
            window.location.reload(); 
          } else {
            return(alert(response.error)) 
          }
        }
        
        if (!parksLoaded) {
          setParks(response);
          setParksLoaded(true);
          setPark(response[0].id)
        }

      }
    );

  }

  const fetchData = () => {

    setLoading(true);

    const body = {}
    body.locIds = [park];
    body.fiscalYear = fiscalYear
    body.includeCurtailments  = addEstimatedCurtailments

    DataAPI({
      'endpoint': 'chart/budget',
      'method': 'POST',
      'body': body
    }).then(
      response => {


        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

        setDataLoaded(true);


        // Current Data in grid
        const currentData = response.currentData;
        const tableData = [];
        setActualProductionMwh(currentData.actualProductionMwh);
        tableData.push({
          label: 'Actual Production',
          value: formatNumber(round(currentData.actualProductionMwh,1)) + " MWh"
        })
        setEstimates(currentData.estimates);
        currentData.estimates.forEach(
          x => {
            tableData.push({
              label: x.label,
              value: formatNumber(x.estimateMwh) + " MWh"
            })
          }
        )
        setTableData1(tableData);


        //Chart Budget
        const budgetData = response.budgetData;
        if (budgetData.length==0) return;
        setEstimatesLabel1(budgetData[0].estimates[0].label)
        setEstimatesLabel2(budgetData[0].estimates[1].label)

        const graphData = {
          labels: budgetData.map((x, i) => { 
            const startDate = new Date(x.startDate);
            return months[startDate.getMonth()]; 
          }),
          datasets: [
            {
              label: budgetData[0].estimates[0].label,
              data: budgetData.map((x, i) => { return x.estimates[0].estimateMwh }),
              borderColor: '#bc5090',
              backgroundColor: '#bc5090',
              order: 1
            },
            {
              label: budgetData[0].estimates[1].label,
              data: budgetData.map((x, i) => { return x.estimates[1].estimateMwh }),
              borderColor: '#7a5195',
              backgroundColor: '#7a5195',
              order: 1
            },
            {
              label: 'Actual Production',
              data: budgetData.map((x, i) => { return x.actualProductionMwh }),
              borderColor: '#ffa600',
              backgroundColor: '#ffa600',
              type: 'line',
              order: 0
            },
          ],
        }

        const dif1 = budgetData.map((x, i) => { return x.estimates[0].actualVsEstimate })
        const dif2 = budgetData.map((x, i) => { return x.estimates[1].actualVsEstimate })
        setDifference1(dif1)
        setDifference2(dif2)

        setGraphData1(graphData);

      }
    );


    DataAPI({
      'endpoint': 'chart/budgetCumulative',
      'method': 'POST',
      'body': body
    }).then(
      response => {

        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

        setDataLoaded(true);


        // Current Data in grid
        const currentData = response.currentData;
        const tableData = [];
        tableData.push({
          label: 'Actual Production',
          value: formatNumber(round(currentData.actualProductionMwh,1)) + " MWh"
        })
        setEstimatesCumulative(currentData.estimates);
        currentData.estimates.forEach(
          x => {
            tableData.push({
              label: x.label,
              value: formatNumber(x.estimateMwh) + " MWh"
            })
          }
        )
        setTableData2(tableData);


        // Chart Cumulative Budget
        const budgetData = response.budgetData;
        if (budgetData.length==0) return;
        const graphData = {
          labels: budgetData.map((x, i) => { 
            const startDate = new Date(x.startDate);
            return months[startDate.getMonth()]; 
          }),
          datasets: [
            {
              label: budgetData[0].estimates[0].label,
              data: budgetData.map((x, i) => { return x.estimates[0].estimateMwh }),
              borderColor: '#bc5090',
              backgroundColor: '#bc5090',
              order: 1
            },
            {
              label: budgetData[0].estimates[1].label,
              data: budgetData.map((x, i) => { return x.estimates[1].estimateMwh }),
              borderColor: '#7a5195',
              backgroundColor: '#7a5195',
              order: 1
            },
            {
              label: 'Actual Production',
              data: budgetData.map((x, i) => { return x.actualProductionMwh }),
              borderColor: '#ffa600',
              backgroundColor: '#ffa600',
              type: 'line',
              order: 0
            },
          ],
        }

        setCumulativeDifference1(budgetData.map((x, i) => { return x.estimates[0].actualVsEstimate }))
        setCumulativeDifference2(budgetData.map((x, i) => { return x.estimates[1].actualVsEstimate }))

        setGraphData2(graphData);

      }
    );


  }

  
  const options = {
    responsive: true,
    tooltips: {
      enabled: true
    }
  };

  const optionsGraph1 =  {
    ...options,
    plugins: {
      tooltip: {
        // mode: 'index',
        callbacks: {
          label: function(tooltipItem, data) {
            if (tooltipItem.dataset.label === "Actual Production")  {
              return [
                tooltipItem.dataset.label + ': ' + formatNumber(round(tooltipItem.raw,1)) + ' MWh',
                "% Difference vs. " + estimatesLabel1 + ": " + difference1[tooltipItem.dataIndex] + " %",
                "% Difference vs. " + estimatesLabel2 + ": " + difference2[tooltipItem.dataIndex] + " %"
            
              ];
            } else {
              return tooltipItem.dataset.label + ': ' + formatNumber(round(tooltipItem.raw,1)) + ' MWh'
            }
              
          }
        }
      }
    }
  };

  const optionsGraph2 =  {
    ...options,
    plugins: {
      tooltip: {
        // mode: 'index',
        callbacks: {
          label: function(tooltipItem, data) {
            if (tooltipItem.dataset.label === "Actual Production")  {
              return [
                tooltipItem.dataset.label + ': ' + formatNumber(round(tooltipItem.raw,1)) + 'MWh',
                "% Difference vs. " + estimatesLabel1 + ": " + cumulativeDifference1[tooltipItem.dataIndex] + " %",
                "% Difference vs. " + estimatesLabel2 + ": " + cumulativeDifference2[tooltipItem.dataIndex] + " %"
            
              ];
            } else {
              return tooltipItem.dataset.label + ': ' + formatNumber(round(tooltipItem.raw,1)) + ' MWh'
            }
          }
        }
      }
    }
  };

  const fields1=[
    { 
      key: 'label',
      label: '',
    },
    { 
      key: 'value',
      label: 'Month to Date',
    }
  ]
  const fields2=[
    { 
      key: 'label',
      label: '',
    },
    { 
      key: 'value',
      label: 'Year to Date',
    }
  ]


  return (
    <CCard>
      <CCardHeader>
        <CRow>
          <CCol sm="2">
            <h3 id="traffic" className="card-title mb-0">
              Budget
            </h3>
            {/* <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div> */}
          </CCol>

          <CCol sm="10" className="text-right d-flex px-2 flex-center flex-justify-end">
            <h6 className="mr-2 ml-3 m-0" style={{lineHeight:2.4,minWidth:'64px'}}>Fiscal Year</h6>
            <CSelect className={'input-sm'} disabled={loading} custom name="fiscalYear" id="fiscalYear" onChange={(ev) => {setFiscalYear(ev.target.value);}}>
              {[0,1,2,3,4].map((x) => (
                                <option key={currentFiscalYear-x} value={currentFiscalYear-x}>
                                  {currentFiscalYear-x}
                                </option>
                              ))}
            </CSelect>

            <h6 className="mr-2 ml-3 m-0" style={{lineHeight:2.4,minWidth:'170px'}}>Add Estimated Curtailments</h6>
            <CSelect className={'input-sm'} disabled={loading} custom defaultValue={"true"} name="addEstimatedCurtailments" id="addEstimatedCurtailments" onChange={(ev) => {setAddEstimatedCurtailments(ev.target.value);}}>
              <option key={'addEstimatedCurtailments-yes'} value="true">Yes</option>
              <option  key={'addEstimatedCurtailments-no'} value="false">No</option>
            </CSelect>

            <h6 className="mr-2 ml-3 m-0" style={{lineHeight:2.4}}>Park</h6>
            <CSelect className={'input-md'} disabled={loading} defaultValue={park} custom name="park" id="park" onChange={(ev) => {setPark(ev.target.value);}}>
                {parks && parks.map((park) => (
                    <option key={park.id} value={park.id}>
                      {park.name}
                    </option>
                      ))}
            </CSelect>

            <CButton color="primary" disabled={loading || !parksLoaded} className="mr-2 ml-3" onClick={() => { fetchData();}} >Submit</CButton>
          </CCol>

          </CRow>
      </CCardHeader>
      <CCardBody>

        { (loading || dataLoaded) &&
          <div>

            { !loading || dataLoadError ? 
              <div className='position-relative'>
                <CButton 
                  onClick={(e) => exportToExcel(dataTest,'archivoPrueba')}
                  color='primary'
                  className={'position-absolute'}
                  style={{top:'-9px', right:'0', zIndex: '1'}}
                  >
                    Download
                </CButton>
                <CRow className={'mb-5'}>
                  <CCol>
                    <h4 className="pb-2 mb-4 border-bottom" >Monthly Production</h4>

                    <CRow className={'mb-2'}>
                      <CCol sm="5">
                        <CDataTable
                          items={tableData1}
                          fields={fields1}
                          striped
                        />
                      </CCol>
                    </CRow>

                    <CRow className={'mb-2'}>
                      <CCol>
                        <div>MWh</div>
                        <Bar 
                          data={graphData1}
                          options={optionsGraph1} 
                        />
                      </CCol>
                    </CRow>
                  </CCol>
                </CRow>

                <CRow className={'mb-5'}>
                  <CCol>
                    <h4 className="pb-2 mb-4 border-bottom" >Cumulative Production</h4>
                    
                    <CRow className={'mb-2'}>
                      <CCol sm="5">
                        <CDataTable
                          items={tableData2}
                          fields={fields2}
                          striped
                        />
                      </CCol>
                    </CRow>

                    <CRow className={'mb-2'}>
                      <CCol>
                        <div>MWh</div>
                        <Bar 
                          data={graphData2}
                          options={optionsGraph2} 
                        />
                      </CCol>
                    </CRow>
                  </CCol>
                </CRow>

              </div>
            : 
              <div className='text-center'>
                <CSpinner 
                  className="loading-spinner"
                  color='#321fdb'
                />
              </div>
            }
          </div>
        }


      </CCardBody>
    </CCard>
  )
}

export default Budget
