import React, { useState, useEffect } from 'react'

import {
  CButton,
  CCard,
  CCardHeader,
  CCardBody,
  CCol,
  CForm,
  CInput,
  CInputGroup,
  CInputGroupPrepend,
  CInputGroupText,
  CLabel,
  CRow
} from '@coreui/react'

import CIcon from '@coreui/icons-react'

import DataAPI from '../../../helpers/DataAPI.js'
import {getCookie} from '../../../helpers/sessionCookie.js'


const Settings = () => {
  
  const [loading, setLoading] = useState(false);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [newPasswordConfirm, setNewPasswordConfirm] = useState('');
  const [passwordChanged, setPasswordChanged] = useState(false);
  const [canSave, setCanSave] = useState(false);
  const [changePassword, setChangePassword] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    loadUser();
  }, []);


  const loadUser = () => {

    setLoading(true);
    
    DataAPI({
      'endpoint': 'security/authenticate/current',
      'method': 'GET'
    }).then(
      response => {

        setLoading(false);

        if (response.error) {
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

        setName(response.name)
        setEmail(response.email)

      }
    );


  }


  const enableSave = () => {

  }

  const saveNewPassword = () => {

    DataAPI({
      'endpoint': 'security/authenticate/reset',
      'method': 'POST',
      'body': {
        "newPassword": newPassword,
        "newPasswordConfirm": newPasswordConfirm
    }
    }).then(
      response => {

        if (response.changed) {          
          
          setPasswordChanged(true)
          setChangePassword(false)
          setMessage('Your password was updated.')

        } else {
          alert('An error occurred!');
        }

      }
    );

  }


  return (
      <CCard className="mb-4">

      <CCardHeader>
          <CRow>
            <CCol sm="6">
              <h3 id="settings" className="card-title mb-0">
                User settings
              </h3>
            </CCol>
          </CRow>

        </CCardHeader>

        <CCardBody className={'p-5'}>

          <CRow className="">
            <CCol md="6" >

                  <CForm>
                    <CInputGroup className="mb-3">
                      <CInputGroupPrepend>
                        <CInputGroupText>
                          <CIcon name="cil-user" />
                        </CInputGroupText>
                      </CInputGroupPrepend>
                      <CInput onChange={enableSave} value={name} type="text" placeholder="Name" disabled />
                    </CInputGroup>
                    <CInputGroup className="mb-3">
                      <CInputGroupPrepend>
                        <CInputGroupText>
                          <CIcon name="cil-envelope-closed" />
                        </CInputGroupText>
                      </CInputGroupPrepend>
                      <CInput onChange={enableSave} value={email} type="text" placeholder="E-mail" disabled />
                    </CInputGroup>

                    <CLabel color="success" >{message}</CLabel>
                    
                    { passwordChanged &&
                      <CLabel color="success" >{message}</CLabel>
                    }

                    { changePassword ?
                      <div>
                        <CInputGroup className="mb-3">
                          <CInputGroupPrepend>
                            <CInputGroupText>
                              <CIcon name="cil-lock-locked" />
                            </CInputGroupText>
                          </CInputGroupPrepend>
                          <CInput onChange={(ev) => {setNewPassword(ev.target.value)}} type="password" placeholder="New Password" />
                        </CInputGroup>
                        <CInputGroup className="mb-3">
                          <CInputGroupPrepend>
                            <CInputGroupText>
                              <CIcon name="cil-lock-locked" />
                            </CInputGroupText>
                          </CInputGroupPrepend>
                          <CInput onChange={(ev) => {setNewPasswordConfirm(ev.target.value)}} type="password" placeholder="Confirm New Password"  />
                        </CInputGroup>
                        <CRow>
                          <CCol xs="12">
                            <CButton onClick={saveNewPassword} color="primary" className="px-4 mr-3">Save new password</CButton>
                            <CButton onClick={() => setChangePassword(false)} color="secondary" className="px-4">Cancel</CButton>
                          </CCol>
                        </CRow>
                      </div>
                      :
                      <CRow>
                        <CCol xs="12">
                          <CButton onClick={() => setChangePassword(true)} color="primary" className="px-4 mr-3">Change Password</CButton>
                        </CCol>
                      </CRow>
                    }

                  </CForm>

            </CCol>
          </CRow>

          
        </CCardBody>

      </CCard>



  )
}

export default Settings
