import React, { useState, useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CButtonGroup,
  CProgress,
  CCardFooter,
  CFormSelect,
  CDataTable,
  CBadge,
  CSelect,
  CModal,
  CModalHeader,
  CModalFooter,
  CModalBody,
  CModalTitle,
  CSpinner
} from '@coreui/react'
import { DocsLink } from 'src/reusable'

import DataAPI from '../../../helpers/DataAPI.js'
import {DateFilter, formatDate, formatDate2, round, getDateLabel, formatNumber} from '../../../helpers/utils.js'
import {getCookie} from '../../../helpers/sessionCookie.js'
import { months } from 'moment'
import { Line, Scatter, Bar } from 'react-chartjs-2';

const Budget = () => {

  const currentYear = new Date().getFullYear()

  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState();
  const [curves, setCurves] = useState([]);
  const [filtersSelected, setFiltersSelected] = useState(false);
  const [fiscalYear, setFiscalYear] = useState(currentYear);
  const [addEstimatedCurtailments, setAddEstimatedCurtailments] = useState(true);
  const [parks, setParks] = useState([]);
  const [parksLoaded, setParksLoaded] = useState(false);
  const [park, setPark] = useState(null);
  const [graphData1, setGraphData1] = useState({
    labels: [],
    datasets: []
  });
  const [graphData2, setGraphData2] = useState({
    labels: [],
    datasets: []
  });


  useEffect(() => {
    fetchParks();
  });


  const fetchParks = () => {

    DataAPI({
      'endpoint': 'security/authenticate/location',
      'method': 'GET'
    }).then(
      response => {
        
        if (!parksLoaded) {
          setParks(response);
          setParksLoaded(true);
        }

      }
    );

  }


  const fetchData = () => {

    setLoading(true);

    const body = {}
    body.location = getCookie('location');
    body.fiscalYear = fiscalYear
    body.addEstimatedCurtailments = addEstimatedCurtailments
    body.park = park


    DataAPI({
      'endpoint': 'chart/budget',
      'method': 'POST',
      'body': body
    }).then(
      response => {


        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

        const budgetData = response.budgetData;

        setDataLoaded(true);

        if (budgetData.length==0) return;

        const graphData = {
          labels: budgetData.map((x, i) => { 
            const startDate = new Date(x.startDate);
            return months[startDate.getMonth()]; 
          }),
          datasets: [
            {
              label: budgetData[0].estimates[0].label,
              data: budgetData.map((x, i) => { return x.estimates[0].estimateMwh }),
              borderColor: '#7a5195',
              backgroundColor: '#7a5195',
              order: 1
            },
            {
              label: budgetData[0].estimates[1].label,
              data: budgetData.map((x, i) => { return x.estimates[1].estimateMwh }),
              borderColor: '#bc5090',
              backgroundColor: '#bc5090',
              order: 1
            },
            {
              label: 'Actual Production',
              data: curves.map((x, i) => { return x.actualProductionMwh }),
              borderColor: '#ffa600',
              backgroundColor: '#ffa600',
              type: 'line',
              order: 0
            },
          ],
        }

        setGraphData1(graphData);

      }
    );


  }

  
  const options = {
    responsive: true,
    animation: {duration: loading ? 0 : 1000},
    tooltips: {
      enabled: true
    }
  };

  const optionsGraph1 =  {
    ...options,
    plugins: {
      tooltip: {
        // mode: 'index',
        callbacks: {
          label: function(tooltipItem, data) {
              let unit = tooltipItem.dataset.label === "Average Wind Speed" ? ' m/s' : ' %'
              return tooltipItem.dataset.label + ': ' + round(tooltipItem.raw,1) + unit;
          }
        }
      }
    }
  };


  return (
    <CCard>
      <CCardHeader>
        <CRow>
          <CCol sm="4">
            <h3 id="traffic" className="card-title mb-0">
              Budget
            </h3>
            {/* <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div> */}
          </CCol>

          <CCol sm="8" className="text-right d-flex px-2 flex-center">
            <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4,minWidth:'64px'}}>Fiscal Year</h6>
            <CSelect disabled={loading} custom name="fiscalYear" id="fiscalYear" className="" onChange={(ev) => {setFiscalYear(ev.target.value);}}>
              {[0,1,2,3,4].map((x) => (
                                <option value={currentYear-x}>
                                  {currentYear-x}
                                </option>
                              ))}
            </CSelect>

            <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4,minWidth:'170px'}}>Add Estimated Curtailments</h6>
            <CSelect disabled={loading} custom name="addEstimatedCurtailments" id="addEstimatedCurtailments" className="" onChange={(ev) => {setAddEstimatedCurtailments(ev.target.value);}}>
              <option value="1">Yes</option>
              <option value="0">No</option>
            </CSelect>

            <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4}}>Park</h6>
            <CSelect disabled={loading} custom name="park" id="park" className="" onChange={(ev) => {setPark(ev.target.value);}}>
                {parks && parks.map((park) => (
                    <option key={park.id} value={park.id}>
                      {park.name}
                    </option>
                      ))}
            </CSelect>

            <CButton color="primary" disabled={loading || !parksLoaded} className="mr-2 ml-3" onClick={() => { fetchData();}} >Submit</CButton>
          </CCol>

          </CRow>
      </CCardHeader>
      <CCardBody>

        {!loading || dataLoadError ? 

          <div>

            <CRow className={'mb-5'}>
              <CCol>
                <h4 className="pb-2 mb-4 border-bottom" >Monthly Production</h4>
                <div>MWh</div>
                <Bar 
                  data={graphData1}
                  options={optionsGraph1} 
                />
              </CCol>
            </CRow>

          </div>
        : 
          <div className='text-center'>
            <CSpinner 
              className="loading-spinner"
              color='#321fdb'
            />
          </div>
        }


      </CCardBody>
    </CCard>
  )
}

export default Budget
