import React, { useState, useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CDataTable,
  CRow,
  CCol,
  CSpinner,
  CSelect,
  CBadge
} from '@coreui/react'

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

import DataAPI from '../../helpers/DataAPI.js'

import { formatDate2, round } from '../../helpers/utils.js'

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = () => {

  const [dateRange, setDateRange] = useState('cm');
  const [groupBy, setGroupBy] = useState('day');
  const [generators, setGenerators] = useState([]);
  const [generator, setGenerator] = useState(null);
  const [lastUpdated, setLastUpdated] = useState('');

  const [dataLoaded, setDataLoaded] = useState(false);

  const [loading, setLoading] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);

  const [turbinesTablesData, setTurbinesTablesData] = useState([]);
  const [tableDataCurrentStatus, setTableDataCurrentStatus] = useState([]);

  const fetchData = () => {

    const body = {}
    setLoading(true);

    DataAPI(
      {
      'endpoint': 'chart/dashboard',
      'body': body
    }).then(function (responseData) {

      setLoading(false);
      if (responseData.error) {
        setDataLoadError(true);
          if (responseData.error.message) {
          return(alert(responseData.error.message))
          } else {
          return(alert(responseData.error)) 
          }
      }

      setLastUpdated(formatDate2(responseData.dateGenerated));

      const tableDataCurrentStatus = {
        items: [["Production:",responseData.dataNow.production,responseData.dataToday.production],
                ["Turbine average wind speed:",responseData.dataNow.windSpeed,responseData.dataToday.windSpeed]
                ["Turbines in operation:",responseData.amountGenerators+"/"+responseData.amountGeneratorsOk,""]],
        fields:  [
          { key: 'item', label: '' },
          'Now',
          'Today'
        ]
      }
      setTableDataCurrentStatus

      const turbinesTablesData = [];
      responseData.locations.forEach((rL,index) => {
        if(rL.generators.length > 0) {
          const park = rL.name;
          let productionTotal = 0;
          let windSpeedTotal = 0;
          const turbinesStatus = rL.generators.map((d) => {
            let line = {}
            line.turbine = d.code;
            line.indicator = d.dataNow.indicator;
            line.production = d.dataNow.production;
            line.windSpeed = d.dataNow.windSpeed;
            line.status = d.dataNow.status;
            productionTotal += d.dataNow.production;
            windSpeedTotal += d.dataNow.windSpeed;
            return line;
          });

          const total =  {
            turbine: "Total",
            indicator: "",
            production: round(productionTotal),
            windSpeed: round(windSpeedTotal / rL.generators.length),
            status: ""
          };
          // turbinesStatus.push(total)
          // [total].concat(turbinesStatus);
      
          const tableData = {
            park: park,
            items: [total].concat(turbinesStatus),
            fields:  [
              'turbine',
              { key: 'indicator', label: '' },
              { key: 'production', label: 'Production (KWh)' },
              { key: 'windSpeed', label: 'Wind Speed (m/s)' },
              'status'
            ]
          }
    
          turbinesTablesData.push(tableData);
        }
        
      });

      setTurbinesTablesData(turbinesTablesData);

    })




  }

  useEffect(() => {
    loadGenerators();
  }, []);


  const loadGenerators = () => {
    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        if (!dataLoaded && response  && !response.error) {
          setGenerators(response.generators);
          fetchData();
        }
      }
    );
  }

  const getBadge = status => {
    switch (status) {
      case 'green': return 'success'
      case 'red': return 'danger'
      default: return 'danger'
    }
  }


  return (
    <CCard>
      <CCardHeader>
          <CRow>
            <CCol sm="12">
              <h3 id="traffic" className="card-title mb-0">
                Turbines Current Status
              </h3>
              <div className="text-medium-emphasis">Last updated on: {lastUpdated}</div>
              </CCol>
            
          </CRow>


        </CCardHeader>
        {!loading || dataLoadError
                    ? 
          <CCardBody>


            <div style={{marginBottom:'50px'}}>
              <CRow>
              
                  <CCol xl="6" className="px-sm-4 mb-4">
                    <h5>Current Status</h5>
                    <CDataTable
                        addTableClasses="current-status-table"
                        striped
                        items={tableDataCurrentStatus.items}
                        fields={tableDataCurrentStatus.fields}
                      />
                  </CCol>

              </CRow>
            </div>
            
            <div style={{marginBottom:'50px'}}>
              <CRow>
              
                { turbinesTablesData.length > 0 &&
                  turbinesTablesData.map(park => (  
                  <CCol xl="6" className="px-sm-4 mb-4">
                    <h5>{park.park}</h5>
                    <CDataTable
                        addTableClasses="monitor-park-table"
                        striped
                        items={park.items}
                        fields={park.fields}
                        // itemsPerPage={15}
                        // pagination
                        sorter
                        scopedSlots = {{
                          'indicator':
                            (item)=>(
                              <td>
                                <CBadge color={getBadge(item.indicator)}>
                                </CBadge>
                              </td>
                            )
                        }}
                      />
                  </CCol>
                  ))
                }

              </CRow>
            </div>

          </CCardBody>
          :
          <CCardBody style={{textAlign:'center'}}>
            <CSpinner 
              className="loading-spinner"
              color='#321fdb'
            />
          </CCardBody>
        }
    </CCard>
  )
}

export default Dashboard
