import React, { useState, useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CAlert,
  CLabel,
  CDataTable,
  CInputCheckbox
} from '@coreui/react'

import DataAPI from '../../../helpers/DataAPI.js'


const Reports = () => {

  const [parksRows, setParksRows] = useState('');
  const [tableData, setTableData] = useState(false);
  const [tableFields, setTableFields] = useState(false);
  const [loading, setLoading] = useState(false);
  const [loaded, setLoaded] = useState(false);


  useEffect(() => {
    loadConfiguration();
  }, []);


  const loadConfiguration = () => {

    setLoading(true);
    
    DataAPI({
      'endpoint': 'report/configure',
      'method': 'GET'
    }).then(
      response => {

        if (response.error) {
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

        if (response.loaded) {
          setLoaded(true);


          let reports = [];
          response.reports.forEach((report,index) => {
            reports[report.id] = report.title;
          });

          let locations = [];
          let rows = [];
          response.locations.forEach((location,index) => {
              locations[location.id] = location.name;
              rows[location.id] = [];
          });

          response.settings.forEach((setting,index) => {
            rows[setting.locId][setting.repId] = setting.selected;

          });

          let tableData = [];
          rows.map((row, locId) => {

            let rowData = {};
            row.map((setting, repId) => {
              rowData[repId] = setting ? <CInputCheckbox></CInputCheckbox> : <CInputCheckbox></CInputCheckbox>;
            });
            rowData['windFarm'] = locations[locId];

            tableData.push(rowData);

          });

          const tableFields = reports.map((title, id) => ({
              key: id,
              label: title
            }
          ));
          tableFields.unshift({
            key: 'windFarm',
            label: 'Wind Farm'
          });

          setTableData(tableData);
          setTableFields(tableFields);

        }

      }
    );


  }





  return (
    <CCard>
      <CCardHeader>
        <CRow>
          <CCol sm="6">
            <h3 id="traffic" className="card-title mb-0">
              Reports
            </h3>
            <div className="small text-medium-emphasis">Settings</div>
          </CCol>
        </CRow>
      </CCardHeader>
      <CCardBody>
        <CRow>
          <CCol sm="6">
        <CLabel>Please select reports you wish to receive by e-mail</CLabel>

        {/* {parksRows} */}
        <CDataTable
                  items={tableData}
                  fields={tableFields}
                  striped
                />

          </CCol>
        </CRow>

      </CCardBody>
    </CCard>
  )
}

export default Reports
