import React, { useEffect, useState } from 'react'
import DataAPI from '../../../helpers/DataAPI.js'
import {DateFilter, getDateLabel, formatDate, formatDate2, round} from '../../../helpers/utils.js'

import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CSpinner,
} from '@coreui/react'

import {
  CChartLine,
  CChartPolarArea
} from '@coreui/react-chartjs'

import { DocsLink } from 'src/reusable'

const ClimateTrends = () => {

  const [dateRange, setDateRange] = useState('cm');
  const [groupBy, setGroupBy] = useState('day');
  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [generatorsLoaded, setGeneratorsLoaded] = useState(false);
  const [selectedGenerators, setSelectedGenerators] = useState([]);
  const [filterSubmitted, setFilterSubmitted] = useState(false);
  const [generators, setGenerators] = useState([]);
  const [generatorColors, setGeneratorColors] = useState([]);
  const [generatorsSelected, setGeneratorsSelected] = useState(false);

  const [lineChartOneData, setLineChartOneData] = useState({
    labels: [],
    datasets: []
  });
  const [lineChartTwoData, setLineChartTwoData] = useState({
    labels: [],
    datasets: []
  });
  const [polarChartData, setPolarChartData] = useState({
    labels: [],
    datasets: []
  });

  const colors =  ['#003f5c', '#7a5195', '#bc5090', '#ef5675', '#ff764a', '#ffa600','#9ceb01']


  useEffect(() => {
    loadGenerators();
  }, []);
  
  
  var colorCounter=0;

  const windRoseLabels = [
            '0-3','3-6','6-9', '9-12', '12-15', '15*', 
          ];

  const windRoseColor = (value) => {



    let n=0;

    if (value < 3) {
      n=0;
    } else if (value < 6){
      n=1;
    } else if (value < 9){
      n=2;
    } else if (value < 12){
      n=3;
    } else if (value < 15){
      n=4;
    } else {
      n=5;

    }

    console.log('windRoseColor', value, n)

    const x = colors;

    return x[n];
  }



  const fetchData = (period) => {

    setLoading(true);

    const body = {}

    if (period && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }
    body.groupBy = groupBy;
    
    DataAPI(
      {
      'endpoint': 'chart/climate',
      'method': 'POST',
      'body': body
    })
      .then(function (responseData) {

        if (responseData.error && responseData.error.message) alert(responseData.error.message)

        setLoading(false);

        if (responseData && responseData.graphs) {

          var windSpeedData = {
            labels: [],
            datasets: []
          }
          var temperatureData = {
            labels: [],
            datasets: []
          }
          var windRoseData = {
            labels: [],
            datasets: []
          }


          var metMastWindSpeedDataSet = {
            label: 'Met Mast',
            borderColor: '#f32e2e',
            pointBackgroundColor: 'transparent',
            pointBorderColor: 'transparent',
            backgroundColor: 'transparent',
            data: []
          };
          var turbinesAverageWindSpeedDataSet = {
            label: 'Turbines Average',
            borderColor: '#930303',
            pointBackgroundColor: 'transparent',
            pointBorderColor: 'transparent',
            backgroundColor: 'transparent',
            data: []
          };
          var metMastTemperatureDataSet = {
            label: 'Met Mast',
            borderColor: '#f32e2e',
            pointBackgroundColor: 'transparent',
            pointBorderColor: 'transparent',
            backgroundColor: 'transparent',
            data: []
          };
          var turbinesAverageTemperatureDataSet = {
            label: 'Turbines Average',
            borderColor: '#930303',
            pointBackgroundColor: 'transparent',
            pointBorderColor: 'transparent',
            backgroundColor: 'transparent',
            data: []
          };




          // const datasetMetMast = {
          //   label: 'Met Mast',
          //   borderColor: '#f87979',
          //   pointBackgroundColor: 'transparent',
          //   pointBorderColor: 'transparent',
          //   backgroundColor: 'transparent',
          //   data: []
            
          // };
          // windSpeedData.datasets.push(datasetMetMast);




        responseData.graphs.find((item) => {
          // switch (item.type) {
          //   case 'WindSpeed':
              item.data.forEach((rDs) => {
                const mmWindSpeed = (Math.round(rDs.mmWindSpeed * 100) / 100).toFixed(2);
                metMastWindSpeedDataSet.data.push(mmWindSpeed);
                const taWindSpeed = (Math.round(rDs.taWindSpeed * 100) / 100).toFixed(2);
                turbinesAverageWindSpeedDataSet.data.push(taWindSpeed);
                

                let date = rDs.datetimeFrom.split(' ')[0];
                let time = rDs.datetimeFrom.split(' ')[1];
                let timeArr = time.split(':')

                const rDfrom = `${date}`;// - ${timeArr[0]}:${timeArr[1]}`;
                const rDto = rDs.datetimeTo;
                windSpeedData.labels.push(rDfrom);
              });

              windSpeedData.datasets.push(metMastWindSpeedDataSet);
              windSpeedData.datasets.push(turbinesAverageWindSpeedDataSet);
              
              setLineChartOneData(windSpeedData)
              // break;

          //   case 'Temperature':
          //     item.data.forEach((rDs) => {
          //       temperatureDataSets.data.push(round(rDs.temperature))
          //       const rDfrom = rDs.datetimeFrom;
          //       const rDto = rDs.datetimeTo;
          //       temperatureData.labels.push(formatDate2(rDfrom, 'time'));
          //     });
          //     temperatureData.datasets.push(temperatureDataSets);
          //     setLineChartTwoData(temperatureData)
          //     break;
          //   case 'WindRose':
          //     item.data.forEach((rDs) => {
          //       let windRoseDataSets = {}
          //       const diff = parseInt(item.data.length) - parseInt(rDs.radiuses.length);
                
          //       const color = windRoseColor(rDs);

          //       windRoseDataSets.data = rDs.radiuses;
          //       windRoseDataSets.backgroundColor=[];

          //       rDs.radiuses.forEach(radius => windRoseDataSets.backgroundColor.push(windRoseColor(radius)));

          //       if (diff > 0) {
          //         for (let i = 0; i <= diff; i++) {
          //           windRoseDataSets.data.push(0);
          //         }
          //       }
                
          //       windRoseDataSets.pointBackgroundColor = color;
          //       // windRoseDataSets.pointBorderColor = "white",
          //       windRoseDataSets.pointHoverBackgroundColor = color;
          //       windRoseDataSets.pointHoverBorderColor = color;
                
          //       windRoseData.datasets.push(windRoseDataSets);

          //       // console.log('windRoseRange',rDfrom,rDs.angle.from)
                

                
          //     });

          //     windRoseData.datasets.backgroundColor = ['red','blue'];

          //     setPolarChartData(windRoseData)
          //     break;
          // }
        });

      }
      });

  }

  const loadGenerators = () => {

    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        
        if (!dataLoaded && response  && !response.error) {

          setGenerators(response.generators);
          // setSelectedGenerators(response.generators.map((gen) => (gen.id)));

          let colorIndex = 0
          response.generators.forEach((gen) => {
            generatorColors[gen.code] = colors[colorIndex%colors.length];
            setGeneratorColors(generatorColors);
            colorIndex++
          });

          setGeneratorsLoaded(true);

        }
      }
    );

  }

  const selectGenerator = (id) => {

    let newSelected = selectedGenerators;
    newSelected = newSelected.includes(id)
                          ? newSelected.filter(i => i !== id) // remove item
                          : [ ...newSelected, id ]; // add item
    setSelectedGenerators(newSelected);
  }

  const filterGenerators = () => {

    setDataLoaded(false);
    setGeneratorsSelected(false);
    setFilterSubmitted(true);
    if (selectedGenerators.length > 0) {
      setLoading(true);
      setGeneratorsSelected(true);
      fetchData(dateRange);
    }

  }



  const windRoseLabelsMap = windRoseLabels.map(
    (v,m) => {
      const color = colors[m];
      return <span style={{marginRight:'20px'}}>{v}<span style={{backgroundColor:color, width: '30px', height: '10px', display: 'inline-block', 'marginLeft':'10px' }}></span></span>;
    }
  )


  return (

      <CCard>

      <CCardHeader>
          <CRow>
            <CCol sm="9">
              <h3 id="traffic" className="card-title mb-0">
                Climate
              </h3>
              <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div>
            </CCol>

            <CCol sm="3" class="text-right d-flex flex-center">
              <CRow className={"w-100 justify-content-end"}>
                <CCol className="d-flex flex-center px-md-1 py-2 px-4">
                  <h6 className="mr-2 ml-4 m-0" style={{lineHeight:1.2}}>Period</h6>
                  <DateFilter options={'cm,cy,12w,12m,x,xx'.split(',')} disabled={loading} onChange={(value) => { setDateRange(value); fetchData(value);}} />
                </CCol>
              </CRow>
            </CCol>
          </CRow>

        </CCardHeader>

        <CCardBody>

          <CRow className={"py-3 mb-4 mx-0 bg-light"} style={{borderRadius:"3px"}}>
