              <h6 className="mx-2 my-2 pt-1" style={{lineHeight:1.2,minWidth:'110px'}}>Select generators:</h6>   
              {generatorsLoaded ? 
                <div>
                  { generators.map((gen, index) => (  
                    <CButton 
                      style={{backgroundColor:generatorColors[gen.code],color: 'white'}} 
                      className={(selectedGenerators.includes(gen.id) ? "selected" : "") + " btn-generator mx-1 my-1"}
                      onClick={() => selectGenerator(gen.id)} 
                      id={gen.id}
                      >
                        {gen.code}
                    </CButton>
                  )) }
                </div>
                :
                <CSpinner 
                  size="sm"
                  className="loading-spinner"
                  color='#321fdb'
                />
              }
            </CCol>
            <CCol sm="2" className="text-right d-flex flex-end flex-justify-end ">
              <CButton color="primary" className="mx-2 mb-1" onClick={() => filterGenerators()} >Submit</CButton>
            </CCol>
        </CRow>

        { generatorsSelected ?

          <div>

            {!loading || dataLoadError ? 

              <div>
                <CRow>
                  <CCol>
                    <h4 className="pb-2 mb-4 border-bottom" >Wind Speed</h4>

                    <div>m/s</div>
                    <CChartLine
                      datasets={lineChartOneData.datasets}
                      labels={lineChartOneData.labels}
                      options={{
                        tooltips: {
                          enabled: true
                        }
                      }}
                    />


                    <br />
                    <br />
                  
                  </CCol>
                </CRow>
                <CRow>
                  <CCol>
                  
                    {false && <><h4 className="pb-2 mb-4 border-bottom">Wind Rose</h4>

                    <div style={{maxWidth:'600px', margin:'auto'}}>{windRoseLabelsMap}</div>

                    <CChartPolarArea
                      datasets={polarChartData.datasets}
                      labels={polarChartData.labels}
                      options={{
                        tooltips: {
                          enabled: true
                        }
                      }}
                    />


                    <br />
                    <br /></>}

                  </CCol>
                </CRow>
                <CRow>
                  <CCol>

                    <h4 className="pb-2 mb-4 border-bottom">Temperature</h4>

                    <div>C</div>
                    <CChartLine
                      datasets={lineChartTwoData.datasets}
                      labels={lineChartTwoData.labels}
                      options={{
                        tooltips: {
                          enabled: true
                        }
                      }}
                    />
                  </CCol>
                </CRow>
              </div>
            : 
              <div className='text-center'>
                <CSpinner 
                  className="loading-spinner"
                  color='#321fdb'
                />
              </div>
            }

          </div>

        :

        <div>
          { filterSubmitted &&
            <CRow>
              <CCol className="text-center">
                Select one or more generators
              </CCol>
            </CRow>
          }
        </div>

}
        </CCardBody>
      </CCard>

  )
}

export default ClimateTrends
