import React, { useState, useEffect } from 'react'

import {
  CButton,
  CCard,
  CCardBody,
  CCardGroup,
  CCol,
  CContainer,
  CForm,
  CInput,
  CInputGroup,
  CInputGroupPrepend,
  CInputGroupText,
  CLink,
  CRow
} from '@coreui/react'

import CIcon from '@coreui/icons-react'

import DataAPI from '../../../helpers/DataAPI.js'
import {getCookie} from '../../../helpers/sessionCookie.js'


const Settings = () => {
  
  const [loading, setLoading] = useState(false);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [newPasswordConfirm, setNewPasswordConfirm] = useState('');
  const [canSave, setCanSave] = useState(false);
  const [changePassword, setChangePassword] = useState(false);

  useEffect(() => {
    loadUser();
  }, []);


  const loadUser = () => {

    setLoading(true);
    
    DataAPI({
      'endpoint': 'security/authenticate/current',
      'method': 'GET',
      'body': {email: getCookie('name')}
    }).then(
      response => {

        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

        setName(response.name)
        setEmail(response.email)

      }
    );


  }


  const enableSave = () => {

  }

  const saveNewPassword = () => {

    DataAPI({
      'endpoint': 'security/authenticate/reset',
      'method': 'POST',
      'body': {
        "id": reset_token,
        "newPassword": newPassword,
        "newPasswordConfirm": newPasswordConfirm
    }
    }).then(
      response => {

        if (response.changed) {          
          
          setPasswordChanged(true);
          setMessage('Your password was updated.')

        } else {
          alert('An error occurred!');
        }

      }
    );

  }


  return (
      <CCard className="mb-4">

      <CCardHeader>
          <CRow>
            <CCol sm="6">
              <h3 id="settings" className="card-title mb-0">
                Settings
              </h3>
            </CCol>
          </CRow>

        </CCardHeader>

        <CCardBody>

          <CRow className="justify-content-center">
            <CCol md="6">
              <CCard className="p-4 border-light shadow">
                <CCardBody>
                  
                    <CForm>
                      <CInputGroup className="mb-3">
                        <CInputGroupPrepend>
                          <CInputGroupText>
                            <CIcon name="cil-user" />Name: 
                          </CInputGroupText>
                        </CInputGroupPrepend>
                        <CInput onChange={enableSave} value={name} type="text" placeholder="Name" />
                      </CInputGroup>
                      <CInputGroup className="mb-3">
                        <CInputGroupPrepend>
                          <CInputGroupText>
                            <CIcon name="cil-envelope-closed" />E-mail:
                          </CInputGroupText>
                        </CInputGroupPrepend>
                        <CInput onChange={enableSave} value={email} type="text" placeholder="E-mail" />
                      </CInputGroup>

                      <CRow>
                        <CCol xs="12">
                          <CButton onClick={() => changePassword(true)} color="primary" className="px-4 mr-3">Change Password</CButton>
                        </CCol>
                      </CRow>

                      {changePassword &&
                        <div>
                          <CInputGroup className="mb-3">
                            <CInputGroupPrepend>
                              <CInputGroupText>
                                <CIcon name="cil-lock-locked" />
                              </CInputGroupText>
                            </CInputGroupPrepend>
                            <CInput onChange={(ev) => {setNewPassword(ev.target.value)}} type="password" placeholder="New Password" />
                          </CInputGroup>
                          <CInputGroup className="mb-3">
                            <CInputGroupPrepend>
                              <CInputGroupText>
                                <CIcon name="cil-lock-locked" />
                              </CInputGroupText>
                            </CInputGroupPrepend>
                            <CInput onChange={(ev) => {setNewPasswordConfirm(ev.target.value)}} type="password" placeholder="Confirm New Password"  />
                          </CInputGroup>
                          <CRow>
                            <CCol xs="12">
                              <CButton onClick={saveNewPassword} color="primary" className="px-4 mr-3">Save new password</CButton>
                            </CCol>
                          </CRow>
                        </div>
                      }

                    </CForm>
                    :
                    <div>
                      {message}
                      <CButton onClick={resendRequest} color="primary" className="px-4 mr-3">Re-send email</CButton>
                    </div>
                  }

                </CCardBody>
              </CCard>
            </CCol>
          </CRow>

          
        </CCardBody>

      </CCard>



  )
}

export default Settings
