import React, { useState, useEffect } from 'react'

import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CButtonGroup,
  CProgress,
  CCardFooter,
  CFormSelect,
  CDataTable,
  CBadge,
  CSelect,
  CModal,
  CModalHeader,
  CModalFooter,
  CModalBody,
  CModalTitle,
  CSpinner,
  CContainer
} from '@coreui/react'

import {
  CChartLine
} from '@coreui/react-chartjs'

import Multiselect from 'multiselect-react-dropdown';

import { Line } from 'react-chartjs-2';

import DataAPI from '../../../helpers/DataAPI.js'

import {DateFilter, formatDate, round, getDateLabel} from '../../../helpers/utils.js'


const Charts = () => {

  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState();
  const [curves, setCurves] = useState([]);
  const [dateRange, setDateRange] = useState('cm');
  const [windSector, setWindSector] = useState('');
  const [generators, setGenerators] = useState([]);
  const [generatorsLoaded, setGeneratorsLoaded] = useState(false);
  const [selectedGenerators, setSelectedGenerators] = useState([]);
  const [filterSubmitted, setFilterSubmitted] = useState(false);
  const [generatorColors, setGeneratorColors] = useState([]);
  const [performance, setPerformance] = useState({});
  const [generatorsSelected, setGeneratorsSelected] = useState(false);
  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });

  const colors =  ['#003f5c', '#7a5195', '#bc5090', '#ef5675', '#ff764a', '#ffa600','#9ceb01']


  useEffect(() => {
    loadGenerators();
  }, []);


  const fetchData = () => {

    const {period, windSector} = options

    setLoading(true);

    const body = {}

    if (selectedGenerators) body.generators = selectedGenerators;
    if (windSector) {
      body.angleFrom = windSector.split('-')[0]
      body.angleTo = windSector.split('-')[1]
    }
    if (dateRange && dateRange.split('--').length==2) {
      body.from = dateRange.split('--')[0]
      body.to = dateRange.split('--')[1]
    } else {
      body.period = dateRange
    }


    DataAPI({
      'endpoint': 'chart/powerCurve',
      'method': 'POST',
      'body': body
    }).then(
      response => {

        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }
          setDataLoaded(true);
          setData(response);
          refreshChart(response);
          
      }
    );


  }


  const loadGenerators = () => {

    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        
        if (!dataLoaded && response  && !response.error) {

          setGenerators(response.generators);
          setSelectedGenerators(response.generators.map((gen) => (gen.id)));

          let colorIndex = 0
          response.generators.forEach((gen) => {
            generatorColors[gen.code] = colors[colorIndex%colors.length];
            setGeneratorColors(generatorColors);
            colorIndex++
          });

          setGeneratorsLoaded(true);

        }
      }
    );

  }


  const refreshChart = (generatorData) => {

    const generators = generatorData.generator;

    if (generators.length==0) return;

      const graphData = {
        labels: [],
        datasets: [],
        backgroundColor: colors
      };

      let productionTotal = generators[0].data.curves[1].x.map((x) => {return 0});
      generators.forEach(
        (generator,index) => {
          const dataset = {
            label: generator.name,
            borderColor: generatorColors[generator.code],//[index%7],
            pointBackgroundColor: 'transparent',
            pointBorderColor: 'transparent',
            backgroundColor: 'transparent',
            data: generator.data.curves[1].x.map((x, i) => { 
                  productionTotal[i] += generator.data.curves[1].y[i];
                  windSpeedTotal[i] += x;
                  return {x, y: generator.data.curves[1].y[i] };
                }
              )
          };
          graphData.datasets.push(dataset);
        }
      );
      const datasetAvarage = {
        label: "Avarage",
        borderColor: "#040404",
        pointBackgroundColor: 'transparent',
        pointBorderColor: 'transparent',
        backgroundColor: 'transparent',
        data: productionTotal.map((y, i) => { 
          return {x: x/generators.length, y: productionTotal[i]/generators.length }
        })
      };
      graphData.datasets.push(datasetAvarage);

      const labels = [];
      generators[0].data.curves[0].x.forEach(
        x => {
          labels.push(round(x))
        }
      );
      graphData.labels = labels;

      setGraphData(graphData);

  }

  const selectGenerator = (id) => {

    let newSelected = selectedGenerators;
    newSelected = newSelected.includes(id)
                          ? newSelected.filter(i => i !== id) // remove item
                          : [ ...newSelected, id ]; // add item
    setSelectedGenerators(newSelected);
  }

  const filterGenerators = () => {

    setDataLoaded(false);
    setGeneratorsSelected(false);
    setFilterSubmitted(true);
    if (selectedGenerators.length > 0) {
      setLoading(true);
      setGeneratorsSelected(true);
      fetchData({period: dateRange, windSector: windSector});
    }

  }

  const options = {
    animation: {
      duration: loading ? 0 : 1000
    },
    tooltips:
    {
        mode: 'index',
        intersect: false,
        callbacks:
        {
            labelColor: function(tooltipItem, chart)
            {
                var datasetIndex = tooltipItem.datasetIndex;
                var borderColor = chart.legend.legendItems[datasetIndex].strokeStyle;
                var backgroundColor = chart.legend.legendItems[datasetIndex].strokeStyle;
                return {
                    borderColor: borderColor,
                    backgroundColor: backgroundColor
                };
            },
            title: function(tooltipItem, data) { 
              return 'Wind Speed: ' + tooltipItem.label + " m/s" 
            },
            label: function(tooltipItem, data) {
              return 'Avarage Power: ' + round(tooltipItem.value,1) + ' kW';
            }
        }
    },
    interaction: {
      mode: 'index',
      intersect: false,
    },
  };


  return (
    <CCard>

    <CCardHeader>
          <CRow>
            <CCol sm="6">
              <h3 id="traffic" className="card-title mb-0">
                Power Curve - Benchmark
              </h3>
              <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div>
            </CCol>

            <CCol sm="6" className="text-right d-flex flex-center">
                
                <h6 className="mr-2 ml-4 m-0" style={{lineHeight:1.2}}>Period</h6>
                <DateFilter value={dateRange} options={['cm','30d','cy','12w','12m','x','xx']} disabled={loading} onChange={(value) => { setDateRange(value); }} />

                <h6 className="mr-2 ml-4 m-0" style={{lineHeight:1.2,minWidth:'72px'}}>Wind sector</h6>
                <CSelect value={windSector} style={{maxWidth:'150px'}} disabled={loading} onChange={(ev) => { setWindSector(ev.target.value);}} custom name="period" id="period">
                  <option value="" selected></option>
                  <option value="0-45">0-45</option>
                  <option value="45-90">45-90</option>
                  <option value="90-135">90-135</option>
                  <option value="135-180">135-180</option>
                  <option value="180-225">180-225</option>
                  <option value="225-270">225-270</option>
                  <option value="270-315">270-315</option>
                  <option value="315-360">315-360</option>
                </CSelect>

              </CCol>
           </CRow>





        </CCardHeader>
      
      <CCardBody>

      <CRow className={"py-3 mb-4 mx-0 bg-light"} style={{borderRadius:"3px"}}>
              <CCol sm="9">
              {generatorsLoaded && 
                generators.map((gen, index) => (  
                    <CButton 
                      style={{backgroundColor:generatorColors[gen.code],color: 'white'}} 
                      className={(selectedGenerators.includes(gen.id) ? "selected" : "") + "btn-generator mx-2"}
                      onClick={() => selectGenerator(gen.id)} 
                      id={gen.id}
                      >
                        {gen.name}
                    </CButton>
                ))
              }
              </CCol>
              <CCol sm="3" className="text-right">
                <CButton color="primary" className="mx-2" onClick={() => filterGenerators()} >Submit</CButton>
              </CCol>
            </CRow>


          {/* <CRow> */}
            {/* <CCol xl="6" md="12" className="d-flex px-md-1 py-2 px-4">
                <h6 className="mr-2 ml-md-4 m-0 col-xl-2 col-md-3 text-right col-3 py-2 px-0 px-sm-1" style={{minWidth:'64px'}}>Generators</h6>
                <div class="multiselect-container mr-2">
                  <Multiselect 
                    disabled={loading}
                    options={generators.map((oGenerator) => {
                        return {id:oGenerator.id,name:oGenerator.code}
                      })} // Options to display in the dropdown
                    selectedValues={generators.map((oGenerator) => {
                      return {id:oGenerator.id,name:oGenerator.code}
                    })} // Preselected value to persist in dropdown
                    onSelect={(selectedList) => {setSelectedGenerators(selectedList.map((gen) => { return gen.id } ))}} // Function will trigger on select event
                    onRemove={(selectedList) => {setSelectedGenerators(selectedList.map((gen) => { return gen.id } ))}} // Function will trigger on remove event
                    displayValue="name" // Property name to display in the dropdown options
                    />
                  </div>
            </CCol> */}
            {/* <CCol xl="3" md="6" className="d-flex flex-center px-md-1 py-2 px-4">
                  <h6 className="mr-2 ml-md-4 m-0 px-0 px-sm-1 col-xl-3 col-md-5 col-3 text-right">Period</h6>
                  <div class="col-xl-8 col-md-7 px-0 mr-2">
                    <DateFilter disabled={loading} value={dateRange} options={['cm','30d','cy','12w','12m','x','xx']} onChange={(value) => {setDateRange(value);}}/>
                  </div>
            </CCol>

            <CCol xl="3" md="6" className="d-flex flex-center px-md-1 py-2 px-4">
              <h6 className="mr-2 ml-md-4 m-0 px-0 px-sm-1 col-xl-3 col-md-5 col-3 text-right">Wind sector</h6>
              <CSelect disabled={loading} custom name="windSector" id="windSector" className="col-xl-8 col-md-7 mr-2" onChange={(ev) => {setWindSector(ev.target.value);}}>
                <option value="" selected></option>
                <option value="0-45">0-45</option>
                <option value="45-90">45-90</option>
                <option value="90-135">90-135</option>
                <option value="135-180">135-180</option>
                <option value="180-225">180-225</option>
                <option value="225-270">225-270</option>
                <option value="270-315">270-315</option>
                <option value="315-360">315-360</option>
              </CSelect>
            </CCol> */}

            {/* </CRow> */}

        {/* //   </CCol>
        //   <CCol md="3" className="text-right d-flex flex-center flex-justify-end px-4 py-2">
        //     <CButton color="primary" className="mx-2" onClick={() => filterSubmit()} >Submit</CButton>
        //   </CCol>
        // </CRow>
         */}


        { generatorsSelected ?

        <CRow>
          <CCol className="text-center">

          {!loading || dataLoadError ? 

            <div>
              <div style={{marginBottom:'50px'}}>
                <div className="text-left" style={{width: '100%'}}>Avarage Power (KW)</div>
                <CChartLine 
                  datasets={graphData.datasets}
                  labels={graphData.labels}
                  options={options} />
                <div className="text-center mt-1" style={{width: '100%'}}>Wind speed (m/s)</div>
              </div>
            </div>

          : 
            <CSpinner 
              className="loading-spinner"
              color='#321fdb'
            />
          }
            
          </CCol>
        </CRow>

        :

        <div>
          { filterSubmitted &&
            <CRow>
              <CCol className="text-center">
                Select one or more generators
              </CCol>
            </CRow>
          }
        </div>


        }

      </CCardBody>



    </CCard>
  )
}

export default Charts
