import React, { useState, useEffect } from 'react'
import DataAPI from '../../../../helpers/DataAPI.js'
import {formatDate, round, getDateLabel, DateFilter} from '../../../../helpers/utils.js'
import {setCookie,getCookie} from '../../../../helpers/sessionCookie.js'

import {
  CCard,
  CCardBody,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CSelect,
  CDataTable,
  CBadge,
  CSpinner,
  CImg,
} from '@coreui/react'

import { Line } from 'react-chartjs-2';

import ChartLoading from '../../../others/ChartLoading.js'

import res from './res_short.json';


const EnergyGeneration = () => {

  const [generator, setGenerator] = useState('');
  const [generators, setGenerators] = useState([]);
  const [generatorsLoaded, setGeneratorsLoaded] = useState(false);
  const [period, setPeriod] = useState('cm');
  // const [data, setData] = useState([]);
  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });

  useEffect(() => {
    loadGenerators();
    // loadStaticData();
  }, []);


  // const loadStaticData = () => {

  //   const data = res.data;
  //   const graphData = {
  //     labels: data.evolution.map((point, i) => { return point.x }),
  //     datasets: [
  //       {
  //         label: 'Data',
  //         data: data.evolution.map((point, i) => { return point.y * 100 }),
  //         borderColor: '#bc5090',
  //         backgroundColor: 'transparent',
  //         borderWidth: 1,
  //         pointRadius: 0
  //       },
  //       {
  //         label: 'Outlier Limit Top',
  //         data: data.evolution.map((point, i) => { return 80 }),
  //         borderColor: 'red',
  //         backgroundColor: 'transparent',
  //         borderWidth: 1.2,
  //         pointRadius: 0
  //       },
  //       {
  //         label: 'Outlier Limit Bottom',
  //         data: data.evolution.map((point, i) => { return 20 }),
  //         borderColor: 'red',
  //         backgroundColor: 'transparent',
  //         borderWidth: 1.2,
  //         pointRadius: 0
  //       },
  //       {
  //         label: 'Median Performance',
  //         data: data.evolution.map((point, i) => { return 50 }),
  //         borderColor: '#42e428',
  //         backgroundColor: 'transparent',
  //         borderWidth: 1.2,
  //         pointRadius: 0
  //       }
  //     ],
  //   }
  //   setGraphData(graphData);
  // }


  const fetchData = () => {

    setLoading(true);

    const body = {}
    body.location = getCookie('location');
    body.groupBy = 'day';
    if (generator) body.generators = [generator];
    if (period && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }

    DataAPI({
      'endpoint': 'wind/performanceIndex',
      'method': 'POST',
      'body': body
    }).then(
      response => {

        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

        setDataLoaded(true);
        // setData(response);
        

        const data = {
          labels: [],
          datasets: [
            {
              label: 'Outlier Limit Top',
              data: response.data[0].data.graph.map((x) => {return 80}),//data.evolution.map((point, i) => { return 80 }),
              borderColor: 'red',
              backgroundColor: 'transparent',
              borderWidth: 1.2,
              pointRadius: 0
            },
            {
              label: 'Outlier Limit Bottom',
              data: response.data[0].data.graph.map((x) => {return 20}),//data.evolution.map((point, i) => { return 20 }),
              borderColor: 'red',
              backgroundColor: 'transparent',
              borderWidth: 1.2,
              pointRadius: 0
            },
            {
              label: 'Median Performance',
              data: response.data[0].data.graph.map((x) => {return 50}),//data.evolution.map((point, i) => { return 50 }),
              borderColor: '#42e428',
              backgroundColor: 'transparent',
              borderWidth: 1.2,
              pointRadius: 0
            }
          ]
        }

        const lineColors = [];

        response.data.forEach((rDs) => {

          const normal = (ctx,value) => 
            ctx.p0.parsed.y < ctx.p1.parsed.y ? 
            value : undefined;
          const withAlarm = (ctx,value) => ctx.p0.parsed.y > ctx.p1.parsed.y ? value : undefined;

          const dataset = {
            label: 'Data',
            data: [],//rDs.evolution.map((point, i) => { return point.y * 100 }),
            borderColor: '#bc5090',
            backgroundColor: 'transparent',
            borderWidth: 1,
            pointRadius: 0,
            segment: {
              borderColor: ctx => normal(ctx,'green') || withAlarm(ctx,'red')
            }
          };
          rDs.data.graph.forEach((rD) => {
            // const label = (groupBy == "day") ? `${formatDate(rD.from)}` : `${formatDate(rD.from)} - ${formatDate(rD.to)}`
            // dataset.labels.push(label)
            dataset.data.push(rD.performance);

            let color = 'green';
            if (rD.performance > 90)
            color = 'red'
            lineColors

            // let color = 'green';
            // if (rD.performance > 100)
            //   color = 'red';
            // dataset.borderColor.push(color);
          });

          // dataset.labels.reverse();
          dataset.data.reverse();
          data.datasets.push(dataset);

        });

        response.data[0].data.graph.forEach((rD) => {
          const label = `${formatDate(rD.from, 'noyear')} - ${formatDate(rD.to, 'noyear')}`
          data.labels.push(label);
        });

        data.labels.reverse();

        setGraphData(data);

      }
    );


  }


  const loadGenerators = () => {

    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        
        if (response && response.error) {
          setCookie('lastTimeStamp', '');
          setCookie('name', '');
          window.location.reload();
        }
        else if (!dataLoaded && response  && !response.error) {

          if (response.generators != null) {
            setGenerators(response.generators);
            setGenerator(response.generators[0].id);
          }
          setGeneratorsLoaded(true)

        }
      }
    );


  }



  const options = {
    responsive: true,
    options: {
      animation: false,
      spanGaps: true
    },
    scales: {
      y:
        {
          suggestedMin: 10,
          suggestedMax: 90,
        }
    },
    tooltips: {
      enabled: false
    },
    elements: {
      line: {
          tension: .4
      }
    },
    plugins: {
      legend: {
        display: false
      },
      tooltip: {
        callbacks: {
          label: function(tooltipItem, data) {
              return 'Percentile of performance: ' + round(tooltipItem.raw,1) + " %";
          }
        }
      },
      // beforeRender: (x, options) => {
      //   const c = x.chart;
      //   const dataset = x.data.datasets[0];
      //   const yScale = x.scales['y-axis-0'];
      //   const yPos = yScale.getPixelForValue(0);
    
      //   const gradientFill = c.ctx.createLinearGradient(0, 0, 0, c.height);
      //   gradientFill.addColorStop(0, 'rgb(86,188,77)');
      //   gradientFill.addColorStop(yPos / c.height, 'rgb(86,188,77)');
      //   gradientFill.addColorStop(yPos / c.height, 'rgb(229,66,66)');
      //   gradientFill.addColorStop(1, 'rgb(229,66,66)');
    
      //   const model = x.data.datasets[0]._meta[Object.keys(dataset._meta)[0]].dataset._model;
      //   model.borderColor = gradientFill;
      // },
    }
    
  };


  return (
      <CCard className="mb-4">

      <CCardHeader>
          <CRow>
            <CCol sm="5">
              <h3 id="traffic" className="card-title mb-0">
              AI Analytics - Energy Generation
              </h3>
              <div className="small text-medium-emphasis">{getDateLabel(period)}</div>
            </CCol>
            <CCol sm="7" className="text-right d-flex flex-center flex-justify-end">
              <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4,minWidth:'64px'}}>Turbine</h6>
              <CSelect className={'input-xs'} disabled={loading} onChange={(ev) => {setGenerator(ev.target.value); }} custom name="generator" id="generator">
              {generators.map((oGenerator) => (
                                  <option value={oGenerator.id}>
                                    {oGenerator.code}
                                  </option>
                                ))}</CSelect>
              <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4}}>Period</h6>
              <DateFilter className={'input-md'} value={period} options={['y','cm','cy','x','xx']} disabled={loading} onChange={(value) => { setPeriod(value); }} />
              <CButton color="primary" disabled={loading || !generatorsLoaded} className="mr-2 ml-3" onClick={() => { fetchData();}} >Submit</CButton>
            </CCol>
           </CRow>

        </CCardHeader>

        <CCardBody>

          { (dataLoaded || loading) &&

          <div>

          {(!loading || dataLoadError) ?
            <CRow className="mb-4">
              <CCol>
                <h4 className="pb-2 mb-4 text-center" >Wind Turbines for investigation/resolution: 3</h4>
                <div>Performance</div>
                <Line
                  redraw 
                  data={graphData}
                  options={options}
                />
              </CCol>
            </CRow>
            :
            <CRow style={{justifyContent:'center'}}>
              <CSpinner 
                className="loading-spinner"
                color='#321fdb'
              />
            </CRow>
          }
         

          </div>

          }
          
        </CCardBody>

      </CCard>



  )
}

export default EnergyGeneration
