  }

  const options = {animation: {duration: loading ? 0 : 1000}};
  const bodyOpacity = 1;//loading?.5:1

  return (
    <>
      
      <CCard>
        <CCardHeader>
          <CRow>
            <CCol sm="7">
              <h4 id="traffic" className="card-title mb-0">
                Overview
              </h4>
              <div className="small text-medium-emphasis">{dateRangeName}</div>
              </CCol>
            <CCol sm="5" class="text-right">

            <DateFilter warning={"Seleccionar un rango máximo de 31 días"} options={['y','cm','x']} disabled={loading} onChange={(value) => { filterData(value); }} />

                            </CCol>
                            </CRow>


        </CCardHeader>
        <CCardBody>

        <div style={{opacity:bodyOpacity}}>

        {false && <div style={{zIndex: 999, position: 'absolute', width: '100%', height: '100%', backgroundColor:'rgba(255,255,255,.7)'}}><CSpinner style={{position: 'absolute'}} /></div>}
        


        <CRow>
      

      <CCol sm="6" lg="4">
        <CWidgetDropdown
          color="gradient-warning"
          header="LOAD FACTOR (%)"
          text={loadFactor}
          footerSlot={
            <div class="px-lg-5 pb-lg-5">
            <Doughnut
               data={loadFactorChartData}
               options={options}
            />
            </div>
          }
        >
          
        </CWidgetDropdown>
      </CCol>

      <CCol sm="6" lg="4">
        <CWidgetDropdown
          color="gradient-info"
          header="AVAILABILITY (%)"
          text={availability}
          footerSlot={
            <div class="px-lg-5 pb-lg-5">
