import React, { useEffect, useState } from 'react'
import DataAPI from '../../../helpers/DataAPI.js'
import {DateFilter, getDateLabel, formatDate, formatDate2, round} from '../../../helpers/utils.js'
import {setCookie,getCookie} from '../../../helpers/sessionCookie.js'

import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CSpinner,
  CLabel,
} from '@coreui/react'

import {
  Chart,
  Plugin
} from 'chart.js';

import { CChartLine } from '@coreui/react-chartjs'

import { Line } from 'react-chartjs-2'

import { OPEN_WEATHER_API } from '../../../constants'
import arrow from '../../../assets/arrow.png'


const ClimateForecast = () => {

  const [dataLoaded, setDataLoaded] = useState(false)
  const [dataLoadError, setDataLoadError] = useState(false)
  const [loading, setLoading] = useState(true)
  const [arrowImage, setArrowImage] = useState(null)

  const [lineChartOneData, setLineChartOneData] = useState({
    labels: [],
    datasets: []
  })

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {

    setLoading(true);
    // var image = new Image();  // see note on creating an image
    // image.src = arrow;
    // image.onload = function(){
    //   setArrowImage(this)
    // }

    let img = React.createElement(
      "img",
      {
        src: arrow,
      },
    )
    setArrowImage(img);

    const APIResponse = await fetch(OPEN_WEATHER_API);
    const responseData = await APIResponse.json();

    if (responseData.error && responseData.error.message) {
      setDataLoadError(true)
      alert(responseData.error.message)
    } 

    

    if (responseData) {

      const graphData1 = {
        labels: responseData.list.map( (x,i) => {
          return x.wind.deg;
        }),
        datasets: [{
          label:  '',
          borderColor: '#003f5c',
          pointBackgroundColor: '#003f5c',
          pointBorderColor: 'transparent',
          backgroundColor: 'transparent',
          data: responseData.list.map( (x,i) => {
            return x.wind.speed;
          })
        }]
      }
      
      // responseData.graphs[0].gen_code.forEach((gen,index) => {
      //   const dataset1 = {
      //     label:  gen,
      //     borderColor: '#003f5c',
      //     pointBackgroundColor: '#003f5c',
      //     pointBorderColor: 'transparent',
      //     backgroundColor: 'transparent',
      //     data: responseData.graphs[0].data.map( (x,i) => {
      //       return x.genWindSpeed[index];
      //     })
      //   }
      
      //   graphData1.datasets.push(dataset1)
      // });

      setLineChartOneData(graphData1)

      setLoading(false);

    }


  }

  const plugins = [{
    afterDraw: (chart) => {
      let ctx = chart.ctx;
      var xAxis = chart.scales['x'];
      var yAxis = chart.scales['y'];
      xAxis.ticks.forEach((value, index) => {
        var x = xAxis.getPixelForTick(index);
        // var image = new Image();
        // image.src = images[index],
        // let imgElement = (<Image
        //                   source={arrow}
        //                 />);
        var arrowImage = new Image(30,30);
        arrowImage.src = arrow
        arrowImage.onload = function() {
          var newImageCanvas = document.createElement('canvas');
          newImageCanvas.width  = 30;
          newImageCanvas.height = 30;
          var newCtx = newImageCanvas.getContext('2d') ;
          newCtx.save();
          newCtx.translate(newImageCanvas.width/2,newImageCanvas.height/2);
          newCtx.rotate(1*0.01745);
          newCtx.translate(-newImageCanvas.width * 0.5, -newImageCanvas.height * 0.5);  
          newCtx.drawImage(arrowImage, 0,0) ;
          newCtx.restore();
          ctx.drawImage(newImageCanvas, x - 12, yAxis.bottom - 10, arrowImage.width, arrowImage.height)
        }
        
      });
    }
  }];

  const options =  {
    type: 'line',
    responsive: true,
    animation: {duration: loading ? 0 : 1000},
    tooltips: {
      enabled: true
    },
    legend: {
      display: false
    },
    elements: {
      line: {
          tension: .4
      }
    },
    plugins: {
      tooltip: {
        callbacks: {
          label: function(tooltipItem, data) {
            return tooltipItem.dataset.label + ": " + round(tooltipItem.raw,1) + ' m/s';
          }
        }
      },
      legend: {
        display: false
      },
      afterDraw: chart => {
        var ctx = chart.chart.ctx;
        var xAxis = chart.scales['x'];
        var yAxis = chart.scales['y-axis-0'];
        xAxis.ticks.forEach((value, index) => {
          var x = xAxis.getPixelForTick(index);
          // var image = new Image();
          // image.src = images[index],
            ctx.drawImage(arrow, x - 12, yAxis.bottom + 10);
        });
      }
    }
  };


  return (

      <CCard>

      <CCardHeader>
          <CRow>
            <CCol sm="4">
              <h3 id="traffic" className="card-title mb-0">
                Climate - Forecast
              </h3>
              {/* <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div> */}
            </CCol>

            <CCol sm="8" className="text-right d-flex flex-center flex-justify-end">
              {/* <h6 className="mr-2 ml-4 m-0" style={{lineHeight:1.2}}>Period</h6>
              <DateFilter options={['y','cm','cy','x','xx']} disabled={loading} onChange={(value) => { setDateRange(value); }} /> */}
            </CCol>
          </CRow>

        </CCardHeader>

        <CCardBody>

            {!loading || dataLoadError ? 

              <div>

                <CRow className={'mb-5'}>
                  <CCol>
                    <h4 className="pb-2 mb-4 border-bottom" >Wind Speed</h4>
                    <div>m/s</div>
                    <Line
                      data={lineChartOneData}
                      options={options}
                      plugins={plugins}
                      // datasets={[{
                      //   label:  '',
                      //   borderColor: '#003f5c',
                      //   pointBackgroundColor: '#003f5c',
                      //   pointBorderColor: 'transparent',
                      //   backgroundColor: 'transparent',
                      //   data: [1,2,3]
                      // }]}
                      // options={options}
                      // labels={['a','b','c']}
                    />
                  </CCol>
                </CRow>


              </div>
            : 
              <div className='text-center'>
                <CSpinner 
                  className="loading-spinner"
                  color='#321fdb'
                />
              </div>
            }

        </CCardBody>
      </CCard>

  )
}

export default ClimateForecast
