import React, { useState, useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CDataTable,
  CRow,
  CCol,
  CSpinner,
  CSelect
} from '@coreui/react'
import { DocsLink } from 'src/reusable'

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Bar } from 'react-chartjs-2';

import DataAPI from '../../../helpers/DataAPI.js'

import {DateFilter, round, formatDate, getDateLabel} from '../../../helpers/utils.js'

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const Charts = () => {

  const [dateRange, setDateRange] = useState('12w');
  const [groupBy, setGroupBy] = useState('week');
  const [generators, setGenerators] = useState([]);
  const [generator, setGenerator] = useState(null);

  const [dataLoaded, setDataLoaded] = useState(false);

  const [loading, setLoading] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);

  const [tablehData, setTablehData] = useState({
    items: [],
    fields: []
  });
  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });

  const fetchData = (options = {}) => {

    const {period, groupBy, generator} = options

    const body = {}

    if (generator && generator !== '0') body.generators = [generator];
    
    if (period && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }
    body.groupBy = groupBy

    setLoading(true);

    DataAPI(
      {
      'endpoint': 'chart/alarms',
      'body': body
    }).then(function (responseData) {

      setLoading(false);
      if (responseData.error) {
        setDataLoadError(true);
          if (responseData.error.message) {
          return(alert(responseData.error.message))
          } else {
          return(alert(responseData.error)) 
          }
      }

      // const dataKeys = ['from', 'to', ...(responseData.dataKeys.map(x => {return x.replace('.0','')}))];

      // const alarms = responseData.data.map((d) => {

      //   d.from = formatDate(d.from);
      //   d.to = formatDate(d.to);
        
      //   d.alarms = d.alarms.map((alarm, index) => {

      //     const rAlarm = {};

      //     d[responseData.dataKeys[index].replace('.0','')] = round(alarm)+'%'

      //   });
      //   delete d.alarms;
      //   return d;
      // });

      // setTablehData({
      //   items: alarms,
      //   fields: dataKeys
      // })

      const data = {
        labels: [],
        datasets: []
      }
      let labels = [];

      const colors = [
        '#003f5c',
        '#374c80',
        '#7a5195',
        '#bc5090',
        '#ef5675',
        '#ff764a',
        '#ffa600',
        '#003f5c',
        '#374c80',
        '#7a5195',
        '#bc5090',
      ];


      // const data2 = responseData.data.map((d) => {

      //   const rDfrom = formatDate(d.from);
      //   const rDto = formatDate(d.to);
      //   const label = (groupBy == "day") ? `${formatDate(rDfrom)}` : `${formatDate(rDfrom)} - ${formatDate(rDto)}`
      //   data.labels.push(label);
        
      //   const datasets2 = d.alarms.map((alarm, index) => {

      //     const datasets = {
      //       label: responseData.dataKeys[index],
      //       backgroundColor: colors[index],
      //       data: []
      //     };
      //     datasets.data.push(alarm);

      //   });
      //   data.datasets.push(datasets2);
      //   // delete d.alarms;
      //   return d;
      // });



      responseData.data.forEach((rD,index) => {
       
        const rDfrom = formatDate(rD.from);
        const rDto = formatDate(rD.to);
        const label = (groupBy == "day") ? `${rDfrom}` : `${rDfrom} - ${rDto}`
        data.labels.push(label);

        // if (responseData.dataKeys[index] != 'total' && responseData.dataKeys[index] != 'turbineOK') {

          rD.alarms.forEach((alarm) => {
            // const dsData = rD.dataSet.find((dS) => dS.id === rDs.id);
            if (responseData.dataKeys[index] != 'total' && responseData.dataKeys[index] != 'turbineOK') {
            dataset.data.push(alarm);
            }



          });

          const dataset = {
            label: responseData.dataKeys[index],
            backgroundColor: colors[index],
            data: []
          };
          rD.alarms.forEach((alarm) => {
            // const dsData = rD.dataSet.find((dS) => dS.id === rDs.id);
            dataset.data.push(alarm);
          });
          data.datasets.push(dataset);
        // }
      });
    

      // responseData.data.forEach((rD) => {
      //   const rDfrom = rD.from;
      //   const rDto = rD.to;
      //   const label = (groupBy == "day") ? `${formatDate(rDfrom)}` : `${formatDate(rDfrom)} - ${formatDate(rDto)}`
      //   data.labels.push(label);

      // });
      setGraphData(data)
    });



  }

  useEffect(() => {
    loadGenerators();
  }, []);


  const loadGenerators = () => {
    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        if (!dataLoaded && response  && !response.error) {
          setGenerators(response.generators);
          fetchData({period: dateRange, groupBy: groupBy});
        }
      }
    );
  }


  const optionsStackedBar = {
    responsive: true,
    scales: {
      x: {
        stacked: true,
      },
      y: {
        stacked: true,
      },
    },
  };

  const labels = ['January', 'February', 'March', 'April', 'May', 'June', 'July'];

  const data = {
    labels,
    datasets: [
      {
        label: 'Dataset 1',
        data: [23,444,-34],
        backgroundColor: 'rgb(255, 99, 132)',
      },
      {
        label: 'Dataset 2',
        data: [233,44,-334],
        backgroundColor: 'rgb(75, 192, 192)',
      },
      {
        label: 'Dataset 3',
        data: [239,4,34],
        backgroundColor: 'rgb(53, 162, 235)',
      },
    ],
  };

  return (
    <CCard>
      <CCardHeader>
          <CRow>
            <CCol sm="5">
              <h4 id="traffic" className="card-title mb-0">
                Alarms
              </h4>
              <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div>
              </CCol>
            
              <CCol sm="7" className="text-right d-flex">
                <h6 className="mr-2 ml-4" style={{lineHeight:2.4,minWidth:'64px'}}>Generator</h6>
                <CSelect disabled={loading} onChange={(ev) => {setGenerator(ev.target.value); fetchData({period: dateRange, groupBy: groupBy, generator: ev.target.value});}} custom name="ccmonth" id="ccmonth">
                  <option value="0">All generators</option>
                {generators.map((oGenerator) => (
                                    <option value={oGenerator.id}>
                                      {oGenerator.code}
                                    </option>
                                  ))}</CSelect>
                <h6 className="mr-2 ml-4" style={{lineHeight:2.4,minWidth:'60px'}}>Group by</h6>
                <CSelect value={groupBy} disabled={loading} onChange={(ev) => { setGroupBy(ev.target.value); fetchData({period: dateRange, groupBy: ev.target.value});}} custom name="ccmonth" id="ccmonth">
                    <option value="day">Day</option>
                    <option value="week" selected>Week</option>
                    <option value="month">Month</option>
                </CSelect>
                <h6 className="mr-2 ml-4" style={{lineHeight:2.4}}>Period</h6>
              <DateFilter value={dateRange} options={['y','cm','cy','12w','12m','x','xx']} disabled={loading} onChange={(value) => { setDateRange(value); fetchData({period: value, groupBy: groupBy});}} />
            </CCol>
          </CRow>


        </CCardHeader>
        {!loading || dataLoadError
                    ? 
          <CCardBody>
            <CDataTable
              striped
              items={tablehData.items}
              fields={tablehData.fields}
            />


          <Bar options={optionsStackedBar} data={graphData} />;



          </CCardBody>
          :
          <CCardBody style={{textAlign:'center'}}>
            <CSpinner 
              className="loading-spinner"
              color='#321fdb'
            />
          </CCardBody>
        }
    </CCard>
  )
}

export default Charts
