import React, { useState, useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CButtonGroup,
  CProgress,
  CCardFooter,
  CFormSelect,
  CSelect
} from '@coreui/react'



import CIcon from '@coreui/icons-react'

import DataAPI from '../../../helpers/DataAPI.js'

import {
  CChartBar,
  CChartLine,
  CChartDoughnut,
  CChartRadar,
  CChartPie,
  CChartPolarArea
} from '@coreui/react-chartjs'
import { DocsLink } from 'src/reusable'

import {formatDate, round, getDateLabel, DateFilter} from '../../../helpers/utils.js'

import { Bar, Chart, Line, Scatter } from 'react-chartjs-2';

const rand = () => {
  return 2;
}


const ctx = document.getElementById('x');

// var mixedChart = new Chart(ctx, {
//     data: {
//         datasets: [
//         {
//             type: 'line',
//             label: 'Line Dataset',
//             data: [50, 50, 50, 50],
//         }],
//         labels: ['January', 'February', 'March', 'April']
//     },
//     // options: options
// });


const Charts = () => {


  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });

  const [generators, setGenerators] = useState([]);
  const [generatorsLoaded, setGeneratorsLoaded] = useState(false);
  const [park, setPark] = useState();
  const [date, setDate] = useState();
  const [dateRange, setDateRange] = useState('12w');
  const [groupBy, setGroupBy] = useState('week');
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);


  const fetchData = (dateRange) => {

    // DataAPI({
    //   'endpoint': 'admin/generators',
    //   'body': {
    //     'period' : dateRange
    //   }
    // }).then(
    //   response => {
        
    //     if (!generatorsLoaded) {
    //       setGenerators(response);
    //       setGeneratorsLoaded(true);
    //     }
    //   }
    // );

    setLoading(true);


    const body = {}

    if (dateRange && dateRange.split('--').length==2) {
      body.from = dateRange.split('--')[0]
      body.to = dateRange.split('--')[1]
    } else {
      body.period = dateRange
    }

    DataAPI({
      endpoint: 'chart/performanceIndex',
      method: 'POST',
      body: body
    })
      .then(function (responseData) {

        setLoading(false);

        if (responseData.error) {
           if (responseData.error.message) {
            return(alert(responseData.error.message))
           } else {
            return(alert(responseData.error)) 
           }
        }

        const data = {
          labels: [],
          datasets: [],
          backgroundColor: ["red", "blue", "green", "blue", "red", "blue"]

        }



        const colors = [
            '#003f5c',
            '#374c80',
            '#7a5195',
            '#bc5090',
            '#ef5675',
            '#ff764a',
            '#ffa600'
          ];


          const dataset100 = {
            labels: [''],
            label: '',
            backgroundColor: colors.pop(),
            data: []
          };

        responseData.data.forEach((rDs) => {

          const dataset = {
            labels: [],
            label: rDs.name,
            backgroundColor: colors.pop(),
            data: [],
            hidden: rDs.name!='Total'
          };

          rDs.data.graph.forEach((rD) => {
            // const dsData = rD.dataSet.find((dS) => dS.id === rDs.id);
            dataset.labels.push(`${formatDate(rD.from)} - ${formatDate(rD.to)}`);
            dataset.data.push(rD.value)

            dataset100.data.push(100)

          });

          dataset.labels.reverse();
          dataset.data.reverse();

          data.datasets.push(dataset);
          // data.datasets.push(dataset100);

        });

        responseData.data[0].data.graph.forEach((rD) => {
          const rDfrom = rD.from;
          const rDto = rD.to;
          data.labels.push(`${formatDate(rD.from, 'noyear')} - ${formatDate(rD.to, 'noyear')}`);

        });

        data.labels.reverse();

        setGraphData(data)

      })
      .catch(
        err => {
          console.warn(`ERROR! ${err}`)
          setLoading(false);
        }

        // err => alert(`ERROR! ${err}`)
      );

  }


  useEffect(() => {


    fetchData(dateRange);


  }, []);



  const dataSets = [];


    const parks = [
    {
        "id": 2,
        "code": "location #2",
        "name": "location sample",
        "address": "Location for sample",
        "latitude": -34.288322967923214,
        "longitude": -54.050271841125806,
        "outputCapacity": 3.0,
        "dataDefinitionId": 2,
        "dataDefinition": null,
        "stations": null,
        "generators": null
    }
]



  const dates = [
    'Hoy',
    'Ayer',
    'Esta semana',
    'Este mes',
    'Este año'
  ]


  const change = (ev) => {
  }

  const refreshChart = (dateRange) => {
    fetchData({period: dateRange,  groupby: groupBy});
  }

  const options = {animation: {duration: loading ? 0 : 1000}};

  return (
      <CCard className="mb-4">

      <CCardHeader>
          <CRow>
            <CCol sm="7">
              <h4 id="traffic" className="card-title mb-0">
                Performance Index
              </h4>
              <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div>
              </CCol>

              <CCol sm="2" class="text-right">
                {/* <DateFilter value={groupBy} options={['day','week','month']} disabled={loading} onChange={(value) => { setGroupBy(value); fetchData(value);}} /> */}

                <CSelect disabled={loading} options={['day','week','month']} onChange={(value) => { setGraphData(value); fetchData({period: dateRange, groupby: value});}} custom name="ccmonth" id="ccmonth"> </CSelect>
              </CCol>

              <CCol sm="3" class="text-right">
                <DateFilter value={dateRange} options={['cm','cy','12w','12m','x']} disabled={loading} onChange={(value) => { setDateRange(value); fetchData({period: value, groupby: groupby});}} />
              </CCol>

          </CRow>


        </CCardHeader>

        <CCardBody>
         
          <Line data={graphData} options={options} />
        </CCardBody>

      </CCard>
  )
}

export default Charts
